import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import { PlusIcon, Bars3Icon } from "@heroicons/react/24/outline";
import { promptCardAPI, handleAPIError } from "../services/api";
import ChatInterface from "./ChatInterface";
import PromptCard from "./prompts/PromptCard";
import PromptEditForm from "./prompts/PromptEditForm";
import { SidebarSkeleton } from "./SkeletonLoader";

// 프롬프트 편집 모달
const PromptEditModal = ({ isOpen, onSubmit, onCancel, initialData }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <PromptEditForm
          onSubmit={onSubmit}
          onCancel={onCancel}
          initialData={initialData}
          isModal={true}
        />
      </div>
    </div>
  );
};

const PromptCardManager = ({ projectId, onCardsChanged, projectName }) => {
  // 상태 관리
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingCard, setEditingCard] = useState(null);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // 프롬프트 카드 로드
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const cards = await promptCardAPI.getPromptCards(projectId);
      setPromptCards(cards);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 카드 업데이트
  const handleUpdateCard = async (cardData) => {
    try {
      if (editingCard) {
        // 편집 모드
        await promptCardAPI.updatePromptCard(
          editingCard.promptId || editingCard.id,
          cardData
        );
        toast.success("프롬프트 카드가 수정되었습니다!");
      } else {
        // 생성 모드
        await promptCardAPI.createPromptCard(projectId, cardData);
        toast.success("프롬프트 카드가 생성되었습니다!");
      }

      await loadPromptCards();
      setShowForm(false);
      setEditingCard(null);

      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(
        `카드 ${editingCard ? "수정" : "생성"} 실패: ${errorInfo.message}`
      );
    }
  };

  // 카드 토글 (최적화 UI)
  const handleToggleCard = async (promptId, enabled) => {
    // 낙관적 업데이트: 즉시 UI 반영
    const originalCards = [...promptCards];
    const updatedCards = promptCards.map((card) =>
      (card.promptId || card.id) === promptId ? { ...card, enabled } : card
    );
    setPromptCards(updatedCards);

    try {
      await promptCardAPI.updatePromptCard(promptId, { enabled });
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      // 실패 시 복원
      setPromptCards(originalCards);
      const errorInfo = handleAPIError(error);
      toast.error(`카드 상태 변경 실패: ${errorInfo.message}`);
    }
  };

  // 카드 삭제 (최적화 UI)
  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트 카드를 삭제하시겠습니까?")) {
      return;
    }

    // 낙관적 업데이트: 즉시 UI에서 제거
    const originalCards = [...promptCards];
    const updatedCards = promptCards.filter(
      (card) => (card.promptId || card.id) !== promptId
    );
    setPromptCards(updatedCards);
    toast.success("프롬프트 카드가 삭제되었습니다!");

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      // 실패 시 복원
      setPromptCards(originalCards);
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 사이드바 */}
      <div
        className={`${
          sidebarCollapsed ? "w-16" : "w-80"
        } bg-white border-r border-gray-200 transition-all duration-300 flex flex-col`}
      >
        {/* 사이드바 헤더 */}
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            {!sidebarCollapsed && (
              <h2 className="text-lg font-semibold text-gray-900">
                프롬프트 카드
              </h2>
            )}
            <button
              onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
              className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-md"
            >
              <Bars3Icon className="h-5 w-5" />
            </button>
          </div>
        </div>

        {/* 프롬프트 카드 목록 */}
        <div className="flex-1 overflow-y-auto p-4">
          {loading ? (
            <SidebarSkeleton />
          ) : sidebarCollapsed ? (
            // 축소된 사이드바 - 아이콘만 표시
            <div className="space-y-2">
              {promptCards.map((card, index) => (
                <div
                  key={card.promptId || card.id}
                  className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-medium ${
                    card.enabled
                      ? "bg-blue-100 text-blue-800"
                      : "bg-gray-100 text-gray-500"
                  }`}
                  title={card.title}
                >
                  {index + 1}
                </div>
              ))}
            </div>
          ) : (
            // 전체 사이드바
            <div className="space-y-3">
              {promptCards.map((card, index) => (
                <PromptCard
                  key={card.promptId || card.id}
                  card={card}
                  onEdit={handleEditCard}
                  onToggle={handleToggleCard}
                  onDelete={handleDeleteCard}
                  stepNumber={index + 1}
                />
              ))}
            </div>
          )}
        </div>

        {/* 새 카드 추가 버튼 */}
        {!sidebarCollapsed && (
          <div className="p-4 border-t border-gray-200">
            <button
              onClick={() => setShowForm(true)}
              className="w-full flex items-center justify-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <PlusIcon className="h-4 w-4 mr-2" />새 프롬프트 추가
            </button>
          </div>
        )}
      </div>

      {/* 메인 콘텐츠 - 채팅 인터페이스 */}
      <div className="flex-1">
        <ChatInterface
          projectId={projectId}
          projectName={projectName}
          promptCards={promptCards}
        />
      </div>

      {/* 편집 모달 */}
      <PromptEditModal
        isOpen={showForm}
        onSubmit={handleUpdateCard}
        onCancel={handleCancelForm}
        initialData={editingCard}
      />
    </div>
  );
};

export default PromptCardManager;
