// 프롬프트 통계 정보 계산 유틸리티
export const calculatePromptStats = promptCards => {
  const stats = {
    totalCards: promptCards.length,
    activeCards: promptCards.filter(card => card.enabled !== false).length,
    totalTokens: 0,
    totalSize: 0,
    avgTokensPerCard: 0,
    categories: new Set(),
    models: new Set(),
    temperatureRange: {
      min: 1,
      max: 0
    }
  };
  promptCards.forEach(card => {
    // 카테고리 수집
    stats.categories.add(card.category);

    // 모델 수집
    stats.models.add(card.model);

    // 프롬프트 텍스트 통계
    if (card.prompt_text) {
      const textLength = card.prompt_text.length;
      stats.totalSize += textLength;

      // 대략적인 토큰 수 계산 (영어: 4자/토큰, 한국어: 2자/토큰)
      const estimatedTokens = Math.ceil(textLength / 2.5);
      stats.totalTokens += estimatedTokens;
    }

    // 온도 범위 계산
    const temp = parseFloat(card.temperature);
    if (temp < stats.temperatureRange.min) stats.temperatureRange.min = temp;
    if (temp > stats.temperatureRange.max) stats.temperatureRange.max = temp;
  });

  // 평균 토큰 계산
  stats.avgTokensPerCard = stats.totalCards > 0 ? Math.round(stats.totalTokens / stats.totalCards) : 0;

  // Set을 배열로 변환
  stats.categories = Array.from(stats.categories);
  stats.models = Array.from(stats.models);
  return stats;
};

// 스마트 제목 생성 함수 (실제 뉴스 분석)
const generateSmartTitles = (articleContent, userRequest = "") => {
  const content = articleContent.toLowerCase();

  // 더 정교한 분석
  const analysis = analyzeArticleContent(content);
  const keywords = extractRealKeywords(content);

  // 사용자 요청 분석
  const isShortRequest = userRequest.includes("짧게") || userRequest.includes("간결") || userRequest.includes("단순");
  const isImpactfulRequest = userRequest.includes("임팩트") || userRequest.includes("강렬") || userRequest.includes("눈에 띄");
  const isConservativeRequest = userRequest.includes("보수적") || userRequest.includes("안전") || userRequest.includes("전문적");
  console.log("📰 기사 분석 결과:", analysis);
  console.log("🔑 추출된 키워드:", keywords);

  // 실제 기사 내용 기반 제목 생성
  const titles = [];

  // 기본적인 사실 전달형 제목들
  if (analysis.mainSubject && analysis.mainAction) {
    titles.push(`${analysis.mainSubject} ${analysis.mainAction}`);
    titles.push(`${analysis.mainSubject}, ${analysis.mainAction} 나서`);
  }

  // 구체적 정보가 있는 경우
  if (analysis.specificInfo) {
    titles.push(`${analysis.mainSubject} ${analysis.specificInfo} ${analysis.mainAction}`);
  }

  // 시점/전망 정보가 있는 경우
  if (analysis.timeframe || analysis.target) {
    const timeInfo = analysis.timeframe || analysis.target;
    titles.push(`${analysis.mainSubject}, ${timeInfo} ${analysis.mainAction} 계획`);
  }

  // 결과/효과 중심 제목
  if (analysis.expectedResult) {
    titles.push(`${analysis.mainSubject} ${analysis.mainAction}로 ${analysis.expectedResult} 노린다`);
  }

  // 폴백: 기본 제목들
  if (titles.length < 3) {
    titles.push(`${analysis.mainSubject || "기업"} 새로운 도전`);
    titles.push(`주목받는 ${analysis.mainAction || "사업 전략"}`);
    titles.push(`${analysis.industry || "업계"} 변화 바람`);
  }

  // 사용자 요청에 따른 제목 조정
  let finalTitles = titles.slice(0, 5);
  if (isShortRequest) {
    // 짧은 제목 요청 시 글자 수 축약
    finalTitles = finalTitles.map(title => {
      if (title.length > 20) {
        return title.substring(0, 18) + "..";
      }
      return title;
    });
  }
  if (isImpactfulRequest) {
    // 임팩트 강화 요청 시 강렬한 표현 추가
    finalTitles = finalTitles.map(title => {
      if (!title.includes("!") && !title.includes("주목")) {
        return title.replace(/계획|예정/, "확정").replace(/추진|진행/, "단행");
      }
      return title;
    });
  }
  if (isConservativeRequest) {
    // 보수적 요청 시 안정적 표현으로 변경
    finalTitles = finalTitles.map(title => {
      return title.replace(/게임체인저|승부수/, "전략").replace(/!/, "");
    });
  }

  // userRequest가 있으면 첫 번째 제목에 반영 상태 표시
  const requestNote = userRequest ? `\n\n💡 "${userRequest}" 요청을 반영하여 생성했습니다.` : "";
  return finalTitles.map((title, i) => `${i + 1}. ${title}`).join("\n") + requestNote;
};

// 실제 기사 내용 분석 함수
const analyzeArticleContent = content => {
  // 회사명 추출
  const companies = ["lg전자", "lg", "엘지전자", "삼성전자", "삼성", "sk하이닉스", "sk", "현대자동차", "현대", "포스코", "네이버", "카카오", "쿠팡", "kt", "skt", "롯데", "신세계", "하나금융", "우리은행", "kb금융", "기아", "두산", "gs", "한화", "ls", "오리온", "cj"];
  const foundCompany = companies.find(company => content.includes(company));

  // 액션 동사 추출
  const actions = [{
    keywords: ["출시", "런칭", "발표"],
    action: "출시"
  }, {
    keywords: ["개발", "연구", "제작"],
    action: "개발"
  }, {
    keywords: ["인수", "합병", "투자"],
    action: "투자"
  }, {
    keywords: ["진출", "확장", "진입"],
    action: "진출"
  }, {
    keywords: ["계획", "예정", "추진"],
    action: "추진"
  }, {
    keywords: ["협력", "제휴", "파트너십"],
    action: "협력"
  }, {
    keywords: ["구축", "설립", "건설"],
    action: "구축"
  }];
  const foundAction = actions.find(actionGroup => actionGroup.keywords.some(keyword => content.includes(keyword)));

  // 구체적 정보 추출
  const specificTerms = ["ai", "인공지능", "반도체", "hbm", "전기차", "ev", "배터리", "소프트웨어", "플랫폼", "서비스", "솔루션", "시스템", "기술", "장비", "제품"];
  const foundSpecific = specificTerms.find(term => content.includes(term));

  // 시간 관련 정보
  const timeframes = ["2024년", "2025년", "2026년", "내년", "올해", "상반기", "하반기", "1분기", "2분기", "3분기", "4분기", "연말", "연초"];
  const foundTimeframe = timeframes.find(time => content.includes(time));

  // 목표/결과 관련
  const results = ["매출 증대", "시장 점유율", "경쟁력 강화", "수익성 개선", "글로벌 진출", "디지털 전환", "탄소중립", "esg", "지속가능"];
  const foundResult = results.find(result => content.includes(result));

  // 산업 분야
  const industries = ["반도체", "자동차", "금융", "유통", "통신", "건설", "에너지", "바이오", "게임", "it", "소프트웨어", "하드웨어"];
  const foundIndustry = industries.find(industry => content.includes(industry));

  // 회사명 정리
  let cleanCompany = foundCompany;
  if (foundCompany) {
    if (foundCompany === "lg" || foundCompany === "lg전자") cleanCompany = "LG전자";else if (foundCompany === "삼성" || foundCompany === "삼성전자") cleanCompany = "삼성전자";else if (foundCompany === "sk" || foundCompany === "sk하이닉스") cleanCompany = "SK하이닉스";else if (foundCompany === "현대" || foundCompany === "현대자동차") cleanCompany = "현대자동차";else cleanCompany = foundCompany;
  }
  return {
    mainSubject: cleanCompany || extractSubjectFromSentence(content),
    mainAction: foundAction ? foundAction.action : "발표",
    specificInfo: foundSpecific,
    timeframe: foundTimeframe,
    expectedResult: foundResult,
    industry: foundIndustry
  };
};

// 문장에서 주어 추출 (간단한 방식)
const extractSubjectFromSentence = content => {
  const sentences = content.split(/[.!?]/).filter(s => s.length > 10);
  if (sentences.length === 0) return "기업";
  const firstSentence = sentences[0].trim();
  const words = firstSentence.split(" ");

  // 첫 번째 문장의 첫 번째 단어가 주어인 경우가 많음
  if (words.length > 0) {
    const subject = words[0].replace(/[^가-힣a-zA-Z0-9]/g, "");
    if (subject.length > 1) {
      return subject;
    }
  }
  return "기업";
};

// 실제 키워드 추출 (빈도 기반)
const extractRealKeywords = content => {
  const commonWords = ["그는", "그녀는", "이는", "이번", "최근", "당시", "현재", "앞서", "또한", "하지만", "그러나", "따라서"];
  const words = content.split(/\s+/).map(word => word.replace(/[^가-힣a-zA-Z0-9]/g, "")).filter(word => word.length > 1 && !commonWords.includes(word));

  // 빈도 계산
  const frequency = {};
  words.forEach(word => {
    frequency[word] = (frequency[word] || 0) + 1;
  });

  // 상위 키워드 반환
  return Object.entries(frequency).sort(([, a], [, b]) => b - a).slice(0, 5).map(([word]) => word);
};

// 사용자 프롬프트를 반영한 제목 생성 (Bedrock Claude 시뮬레이션)
const generateTitlesWithUserPrompts = (articleContent, userRequest = "", savedPrompts = {}) => {
  console.log("🧠 Claude 시뮬레이션: 단계별 프롬프트 처리");

  // 1단계: 역할 및 목표 (instruction)
  const rolePrompt = savedPrompts.instruction || "당신은 전문적인 기사 제목 생성 AI입니다.";
  console.log("1️⃣ 역할 정의:", rolePrompt.substring(0, 50) + "...");

  // 2단계: 지식 베이스 (knowledge)
  const knowledgePrompt = savedPrompts.knowledge || "제목 작성 원칙: 간결하고 명확한 표현";
  console.log("2️⃣ 지식 베이스:", knowledgePrompt.substring(0, 50) + "...");

  // 3단계: 사고 과정 (secondary - CoT)
  const reasoningPrompt = savedPrompts.secondary || "단계별 사고 과정을 거쳐 제목을 생성합니다.";
  console.log("3️⃣ 사고 과정:", reasoningPrompt.substring(0, 50) + "...");

  // 기사 분석
  const analysis = analyzeArticleContent(articleContent.toLowerCase());

  // 사용자 요청 분석
  const isShortRequest = userRequest.includes("짧게") || userRequest.includes("간결");
  const isImpactfulRequest = userRequest.includes("임팩트") || userRequest.includes("강렬") || userRequest.includes("눈에 띄");
  const isConservativeRequest = userRequest.includes("보수적") || userRequest.includes("안전");

  // Claude 스타일의 제목 생성 (단계별 프롬프트 반영)
  const titles = [];

  // 기본 정보 전달형 (knowledge 프롬프트 반영)
  if (analysis.mainSubject && analysis.mainAction) {
    let title1 = `${analysis.mainSubject} ${analysis.mainAction}`;
    let title2 = `${analysis.mainSubject}, ${analysis.mainAction} 계획`;

    // 스타일 가이드 적용
    if (savedPrompts.style_guide) {
      if (savedPrompts.style_guide.includes("간결") || isShortRequest) {
        title1 = title1.length > 15 ? title1.substring(0, 13) + ".." : title1;
        title2 = title2.length > 15 ? title2.substring(0, 13) + ".." : title2;
      }
    }
    titles.push(title1);
    titles.push(title2);
  }

  // 상세 정보 포함형 (secondary 프롬프트의 단계별 사고 반영)
  if (analysis.specificInfo) {
    let title3 = `${analysis.mainSubject} ${analysis.specificInfo} ${analysis.mainAction}`;
    if (analysis.timeframe) {
      title3 = `${analysis.mainSubject}, ${analysis.timeframe} ${analysis.specificInfo} ${analysis.mainAction}`;
    }
    titles.push(title3);
  }

  // 결과/효과 중심 (validation 프롬프트의 ReAct 패턴 반영)
  if (analysis.expectedResult || analysis.industry) {
    const effectTopic = analysis.expectedResult || `${analysis.industry} 혁신`;
    titles.push(`${analysis.mainSubject} ${analysis.mainAction}로 ${effectTopic} 노린다`);
  }

  // enhancement 프롬프트 반영 - 품질 검증 및 개선
  let finalTitles = titles.slice(0, 4);

  // 사용자 요청에 따른 조정
  if (isImpactfulRequest) {
    finalTitles = finalTitles.map(title => {
      if (!title.includes("!")) {
        return title.replace(/계획|예정/, "확정").replace(/노린다/, "목표") + "!";
      }
      return title;
    });
  }
  if (isConservativeRequest) {
    finalTitles = finalTitles.map(title => {
      return title.replace(/!/, "").replace(/노린다/, "전망");
    });
  }

  // 프롬프트 기반 추가 제목
  if (savedPrompts.enhancement && savedPrompts.enhancement.includes("매력도")) {
    finalTitles.push(`주목! ${analysis.mainSubject}의 ${analysis.specificInfo || "새로운"} 도전`);
  }

  // 최종 정리 및 반환
  const resultTitles = finalTitles.slice(0, 5);
  const promptInfo = Object.keys(savedPrompts).length > 0 ? `\n\n🎯 적용된 사용자 프롬프트: ${Object.keys(savedPrompts).join(", ")}` : "\n\n📝 기본 프롬프트 사용";
  const userRequestInfo = userRequest ? `\n💡 "${userRequest}" 요청이 반영되었습니다.` : "";
  return resultTitles.map((title, i) => `${i + 1}. ${title}`).join("\n") + promptInfo + userRequestInfo;
};

