// 프롬프트 통계 정보 계산 유틸리티
export const calculatePromptStats = (promptCards) => {
  const stats = {
    totalCards: promptCards.length,
    activeCards: promptCards.filter((card) => card.enabled !== false).length,
    totalTokens: 0,
    totalSize: 0,
    avgTokensPerCard: 0,
    categories: new Set(),
    models: new Set(),
    temperatureRange: { min: 1, max: 0 },
  };

  promptCards.forEach((card) => {
    // 카테고리 수집
    stats.categories.add(card.category);

    // 모델 수집
    stats.models.add(card.model);

    // 프롬프트 텍스트 통계
    if (card.prompt_text) {
      const textLength = card.prompt_text.length;
      stats.totalSize += textLength;

      // 대략적인 토큰 수 계산 (영어: 4자/토큰, 한국어: 2자/토큰)
      const estimatedTokens = Math.ceil(textLength / 2.5);
      stats.totalTokens += estimatedTokens;
    }

    // 온도 범위 계산
    const temp = parseFloat(card.temperature);
    if (temp < stats.temperatureRange.min) stats.temperatureRange.min = temp;
    if (temp > stats.temperatureRange.max) stats.temperatureRange.max = temp;
  });

  // 평균 토큰 계산
  stats.avgTokensPerCard =
    stats.totalCards > 0 ? Math.round(stats.totalTokens / stats.totalCards) : 0;

  // Set을 배열로 변환
  stats.categories = Array.from(stats.categories);
  stats.models = Array.from(stats.models);

  return stats;
};

// 스마트 제목 생성 함수 (실제 뉴스 분석)
const generateSmartTitles = (articleContent, userRequest = "") => {
  const content = articleContent.toLowerCase();

  // 더 정교한 분석
  const analysis = analyzeArticleContent(content);
  const keywords = extractRealKeywords(content);

  // 사용자 요청 분석
  const isShortRequest =
    userRequest.includes("짧게") ||
    userRequest.includes("간결") ||
    userRequest.includes("단순");
  const isImpactfulRequest =
    userRequest.includes("임팩트") ||
    userRequest.includes("강렬") ||
    userRequest.includes("눈에 띄");
  const isConservativeRequest =
    userRequest.includes("보수적") ||
    userRequest.includes("안전") ||
    userRequest.includes("전문적");

  console.log("📰 기사 분석 결과:", analysis);
  console.log("🔑 추출된 키워드:", keywords);

  // 실제 기사 내용 기반 제목 생성
  const titles = [];

  // 기본적인 사실 전달형 제목들
  if (analysis.mainSubject && analysis.mainAction) {
    titles.push(`${analysis.mainSubject} ${analysis.mainAction}`);
    titles.push(`${analysis.mainSubject}, ${analysis.mainAction} 나서`);
  }

  // 구체적 정보가 있는 경우
  if (analysis.specificInfo) {
    titles.push(
      `${analysis.mainSubject} ${analysis.specificInfo} ${analysis.mainAction}`
    );
  }

  // 시점/전망 정보가 있는 경우
  if (analysis.timeframe || analysis.target) {
    const timeInfo = analysis.timeframe || analysis.target;
    titles.push(
      `${analysis.mainSubject}, ${timeInfo} ${analysis.mainAction} 계획`
    );
  }

  // 결과/효과 중심 제목
  if (analysis.expectedResult) {
    titles.push(
      `${analysis.mainSubject} ${analysis.mainAction}로 ${analysis.expectedResult} 노린다`
    );
  }

  // 폴백: 기본 제목들
  if (titles.length < 3) {
    titles.push(`${analysis.mainSubject || "기업"} 새로운 도전`);
    titles.push(`주목받는 ${analysis.mainAction || "사업 전략"}`);
    titles.push(`${analysis.industry || "업계"} 변화 바람`);
  }

  // 사용자 요청에 따른 제목 조정
  let finalTitles = titles.slice(0, 5);

  if (isShortRequest) {
    // 짧은 제목 요청 시 글자 수 축약
    finalTitles = finalTitles.map((title) => {
      if (title.length > 20) {
        return title.substring(0, 18) + "..";
      }
      return title;
    });
  }

  if (isImpactfulRequest) {
    // 임팩트 강화 요청 시 강렬한 표현 추가
    finalTitles = finalTitles.map((title) => {
      if (!title.includes("!") && !title.includes("주목")) {
        return title.replace(/계획|예정/, "확정").replace(/추진|진행/, "단행");
      }
      return title;
    });
  }

  if (isConservativeRequest) {
    // 보수적 요청 시 안정적 표현으로 변경
    finalTitles = finalTitles.map((title) => {
      return title.replace(/게임체인저|승부수/, "전략").replace(/!/, "");
    });
  }

  // userRequest가 있으면 첫 번째 제목에 반영 상태 표시
  const requestNote = userRequest
    ? `\n\n💡 "${userRequest}" 요청을 반영하여 생성했습니다.`
    : "";

  return (
    finalTitles.map((title, i) => `${i + 1}. ${title}`).join("\n") + requestNote
  );
};

