const API_BASE_URL =
  process.env.REACT_APP_API_URL || "https://your-api-gateway-url";

// API 에러 처리
export const handleAPIError = (error) => {
  console.error("API Error:", error);

  if (error.response) {
    return {
      message: error.response.data?.message || "서버 오류가 발생했습니다",
      status: error.response.status,
    };
  } else if (error.request) {
    return {
      message: "네트워크 연결을 확인해주세요",
      status: null,
    };
  } else {
    return {
      message: error.message || "알 수 없는 오류가 발생했습니다",
      status: null,
    };
  }
};

// 공통 fetch 함수
const apiFetch = async (endpoint, options = {}) => {
  const url = `${API_BASE_URL}${endpoint}`;
  const config = {
    headers: {
      "Content-Type": "application/json",
      ...options.headers,
    },
    ...options,
  };

  try {
    const response = await fetch(url, config);
    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.message || `HTTP error! status: ${response.status}`);
    }

    return data;
  } catch (error) {
    throw error;
  }
};

// 프로젝트 API
export const projectAPI = {
  // 프로젝트 목록 조회
  getProjects: async () => {
    return await apiFetch("/projects");
  },

  // 프로젝트 생성
  createProject: async (projectData) => {
    return await apiFetch("/projects", {
      method: "POST",
      body: JSON.stringify(projectData),
    });
  },

  // 프로젝트 조회
  getProject: async (projectId) => {
    return await apiFetch(`/projects/${projectId}`);
  },

  // 프로젝트 삭제
  deleteProject: async (projectId) => {
    return await apiFetch(`/projects/${projectId}`, {
      method: "DELETE",
    });
  },
};

// 프롬프트 API
export const promptAPI = {
  // 프롬프트 목록 조회
  getPrompts: async (projectId) => {
    return await apiFetch(`/projects/${projectId}/prompts`);
  },

  // 프롬프트 생성
  createPrompt: async (projectId, promptData) => {
    return await apiFetch(`/projects/${projectId}/prompts`, {
      method: "POST",
      body: JSON.stringify(promptData),
    });
  },

  // 프롬프트 수정
  updatePrompt: async (projectId, promptId, promptData) => {
    return await apiFetch(`/projects/${projectId}/prompts/${promptId}`, {
      method: "PUT",
      body: JSON.stringify(promptData),
    });
  },

  // 프롬프트 삭제
  deletePrompt: async (projectId, promptId) => {
    return await apiFetch(`/projects/${projectId}/prompts/${promptId}`, {
      method: "DELETE",
    });
  },
};

// AI 제목 생성 API
export const aiAPI = {
  // 제목 생성
  generateTitle: async (projectId, content) => {
    return await apiFetch(`/projects/${projectId}/generate-title`, {
      method: "POST",
      body: JSON.stringify({ content }),
    });
  },
};
