import { useState, useCallback } from "react";
import { toast } from "react-hot-toast";
import { orchestrationAPI } from "../services/api";

/**
 * Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Ïã§Ìñâ Î∞è Í≤∞Í≥º Ìè¥ÎßÅÏùÑ ÏúÑÌïú Ïª§Ïä§ÌÖÄ ÌõÖ
 * @param {string} projectId - ÌîÑÎ°úÏ†ùÌä∏ ID
 * @returns {Object} - Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Í¥ÄÎ†® ÏÉÅÌÉúÏôÄ Ìï®ÏàòÎì§
 */
export const useOrchestration = (projectId) => {
  const [isExecuting, setIsExecuting] = useState(false);
  const [currentExecution, setCurrentExecution] = useState(null);
  const [executionStatus, setExecutionStatus] = useState(null);

  /**
   * Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Ïã§Ìñâ
   * @param {string} content - ÏûÖÎ†• ÎÇ¥Ïö©
   * @param {Object} config - Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò ÏÑ§Ï†ï
   * @returns {Promise<string>} - Ïã§Ìñâ ID
   */
  const executeOrchestration = useCallback(
    async (content, config = {}) => {
      if (!content.trim()) {
        toast.error("ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
        return null;
      }

      try {
        setIsExecuting(true);
        setExecutionStatus("STARTING");

        const defaultConfig = {
          useAllSteps: true,
          enabledSteps: [],
          maxRetries: 3,
          temperature: 0.7,
          ...config,
        };

        const response = await orchestrationAPI.executeOrchestration(
          projectId,
          content,
          defaultConfig
        );

        console.log("üîç Orchestration API ÏùëÎãµ:", response);

        setCurrentExecution(response.executionId || response.conversationId);
        setExecutionStatus("RUNNING");

        // Ï†ÑÏ≤¥ ÏùëÎãµ Í∞ùÏ≤¥ Î∞òÌôò (executionIdÎßåÏù¥ ÏïÑÎãå)
        return response;
      } catch (error) {
        console.error("Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Ïã§Ìñâ Ïã§Ìå®:", error);
        setIsExecuting(false);
        setExecutionStatus("FAILED");
        throw error;
      }
    },
    [projectId]
  );

  /**
   * Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Í≤∞Í≥º Ìè¥ÎßÅ
   * @param {string} executionId - Ïã§Ìñâ ID
   * @param {Function} onComplete - ÏôÑÎ£å Ïãú ÏΩúÎ∞±
   * @param {Function} onError - ÏóêÎü¨ Ïãú ÏΩúÎ∞±
   */
  const pollOrchestrationResult = useCallback(
    async (executionId, onComplete, onError) => {
      const poll = async () => {
        try {
          const status = await orchestrationAPI.getOrchestrationStatus(
            projectId,
            executionId
          );

          setExecutionStatus(status.status);

          if (status.status === "COMPLETED") {
            const result = await orchestrationAPI.getOrchestrationResult(
              projectId,
              executionId
            );

            setIsExecuting(false);
            setExecutionStatus("COMPLETED");

            if (onComplete) {
              onComplete(result);
            }
          } else if (status.status === "FAILED") {
            setIsExecuting(false);
            setExecutionStatus("FAILED");

            if (onError) {
              onError(new Error("Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò Ïã§Ìå®"));
            }
          } else if (status.status === "RUNNING") {
            // 3Ï¥à ÌõÑ Îã§Ïãú Ìè¥ÎßÅ
            setTimeout(poll, 3000);
          }
        } catch (error) {
          console.error("Í≤∞Í≥º Ï°∞Ìöå Ïã§Ìå®:", error);
          setIsExecuting(false);
          setExecutionStatus("FAILED");

          if (onError) {
            onError(error);
          }
        }
      };

      poll();
    },
    [projectId]
  );

  /**
   * Ïò§ÏºÄÏä§Ìä∏Î†àÏù¥ÏÖò ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
   */
  const resetOrchestration = useCallback(() => {
    setIsExecuting(false);
    setCurrentExecution(null);
    setExecutionStatus(null);
  }, []);

  return {
    isExecuting,
    currentExecution,
    executionStatus,
    executeOrchestration,
    pollOrchestrationResult,
    resetOrchestration,
  };
};
