import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  CloudArrowUpIcon,
  XMarkIcon,
  Bars3Icon,
  EllipsisHorizontalIcon,
  PaperAirplaneIcon,
  UserIcon,
  ChatBubbleLeftIcon,
} from "@heroicons/react/24/outline";
import {
  promptCardAPI,
  PROMPT_CARD_CATEGORIES,
  AVAILABLE_MODELS,
  handleAPIError,
} from "../services/api";

// 프롬프트 카드 카테고리별 색상 정의
const CATEGORY_COLORS = {
  blue: `bg-blue-100 text-blue-800 border-blue-200`,
  purple: `bg-purple-100 text-purple-800 border-purple-200`,
  green: `bg-green-100 text-green-800 border-green-200`,
  orange: `bg-orange-100 text-orange-800 border-orange-200`,
  red: `bg-red-100 text-red-800 border-red-200`,
  yellow: `bg-yellow-100 text-yellow-800 border-yellow-200`,
  gray: `bg-gray-100 text-gray-800 border-gray-200`,
};

// 작은 모달창으로 변경된 입력 방식 선택 모달
const InputMethodModal = ({ isOpen, onClose, onSelectMethod }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl shadow-xl p-6 max-w-sm w-full mx-4">
        <div className="text-center mb-6">
          <h3 className="text-lg font-bold text-gray-900 mb-2">
            프롬프트 추가
          </h3>
          <p className="text-sm text-gray-600">입력 방법을 선택하세요</p>
        </div>

        <div className="space-y-3">
          {/* 파일 업로드 옵션 */}
          <button
            onClick={() => onSelectMethod("upload")}
            className="w-full p-4 border-2 border-gray-200 rounded-lg hover:border-blue-400 hover:bg-blue-50 transition-all duration-200 text-left group"
          >
            <div className="flex items-center">
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center mr-3">
                <CloudArrowUpIcon className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <h4 className="font-medium text-gray-900 group-hover:text-blue-600 text-sm">
                  파일 업로드
                </h4>
                <p className="text-xs text-gray-600">파일에서 프롬프트 생성</p>
              </div>
            </div>
          </button>

          {/* 직접 입력 옵션 */}
          <button
            onClick={() => onSelectMethod("direct")}
            className="w-full p-4 border-2 border-gray-200 rounded-lg hover:border-green-400 hover:bg-green-50 transition-all duration-200 text-left group"
          >
            <div className="flex items-center">
              <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center mr-3">
                <PencilIcon className="w-5 h-5 text-green-600" />
              </div>
              <div>
                <h4 className="font-medium text-gray-900 group-hover:text-green-600 text-sm">
                  직접 입력
                </h4>
                <p className="text-xs text-gray-600">텍스트로 프롬프트 작성</p>
              </div>
            </div>
          </button>
        </div>

        <div className="mt-6 flex justify-end">
          <button
            onClick={onClose}
            className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
          >
            취소
          </button>
        </div>
      </div>
    </div>
  );
};

// 채팅 인터페이스 컴포넌트
const ChatInterface = ({ projectId }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      role: "user",
      content: inputMessage,
      timestamp: new Date(),
    };
    setMessages((prev) => [...prev, userMessage]);
    setInputMessage("");
    setIsLoading(true);

    try {
      // TODO: 실제 AI API 호출 구현
      // 현재는 mock 응답
      setTimeout(() => {
        const aiMessage = {
          role: "assistant",
          content: "안녕하세요! 프롬프트 카드를 활용한 AI 응답입니다.",
          timestamp: new Date(),
        };
        setMessages((prev) => [...prev, aiMessage]);
        setIsLoading(false);
      }, 1000);
    } catch (error) {
      console.error("메시지 전송 실패:", error);
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="flex flex-col h-full">
      {/* 채팅 헤더 */}
      <div className="p-4 border-b border-gray-200 bg-white">
        <div className="flex items-center space-x-2">
          <ChatBubbleLeftIcon className="h-5 w-5 text-blue-600" />
          <h3 className="font-semibold text-gray-900">AI 대화</h3>
        </div>
      </div>

      {/* 메시지 영역 */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.length === 0 ? (
          <div className="text-center py-8">
            <SparklesIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-500">AI와 대화를 시작해보세요</p>
          </div>
        ) : (
          messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${
                message.role === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                  message.role === "user"
                    ? "bg-blue-600 text-white"
                    : "bg-gray-100 text-gray-900"
                }`}
              >
                <p className="text-sm">{message.content}</p>
                <p
                  className={`text-xs mt-1 ${
                    message.role === "user" ? "text-blue-200" : "text-gray-500"
                  }`}
                >
                  {message.timestamp.toLocaleTimeString()}
                </p>
              </div>
            </div>
          ))
        )}
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-gray-100 rounded-lg px-4 py-2">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div
                  className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"
                  style={{ animationDelay: "0.1s" }}
                ></div>
                <div
                  className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"
                  style={{ animationDelay: "0.2s" }}
                ></div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* 입력 영역 */}
      <div className="p-4 border-t border-gray-200 bg-white">
        <div className="flex space-x-2">
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="메시지를 입력하세요..."
            className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
            rows="1"
            style={{ minHeight: "40px", maxHeight: "120px" }}
          />
          <button
            onClick={sendMessage}
            disabled={!inputMessage.trim() || isLoading}
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-300 text-white rounded-lg transition-colors"
          >
            <PaperAirplaneIcon className="h-4 w-4" />
          </button>
        </div>
      </div>
    </div>
  );
};

// 프롬프트 폼 (기존과 동일하지만 작은 변경사항 적용)
const PromptForm = ({
  inputMethod,
  onSubmit,
  onCancel,
  initialData = null,
}) => {
  const [formData, setFormData] = useState({
    title: "",
    category: "역할",
    model: "claude-3-5-sonnet-20241022",
    temperature: 0.7,
    content: "",
    enabled: true,
    ...initialData,
  });

  const [file, setFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.title.trim()) {
      toast.error("제목을 입력해주세요.");
      return;
    }

    if (inputMethod === "upload" && !file && !initialData) {
      toast.error("파일을 선택해주세요.");
      return;
    }

    if (inputMethod === "direct" && !formData.content.trim()) {
      toast.error("프롬프트 내용을 입력해주세요.");
      return;
    }

    try {
      let content = formData.content;

      if (inputMethod === "upload" && file) {
        content = await readFileContent(file);
      }

      const cardData = {
        ...formData,
        content,
      };

      await onSubmit(cardData);
    } catch (error) {
      console.error("폼 제출 실패:", error);
      toast.error("폼 제출에 실패했습니다.");
    }
  };

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = reject;
      reader.readAsText(file);
    });
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-8">
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            {initialData ? "프롬프트 편집" : "새 프롬프트 카드"}
          </h2>
          <p className="text-gray-600">
            {initialData
              ? "프롬프트 정보를 수정하세요"
              : "새로운 프롬프트 카드를 생성하세요"}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* 제목 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              제목
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프롬프트 카드 제목을 입력하세요"
            />
          </div>

          {/* 카테고리와 모델 */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                카테고리
              </label>
              <select
                value={formData.category}
                onChange={(e) => handleChange("category", e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="역할">역할</option>
                <option value="가이드라인">가이드라인</option>
                <option value="워크플로우">워크플로우</option>
                <option value="출력결과">출력결과</option>
                <option value="예시">예시</option>
                <option value="검수">검수</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                모델
              </label>
              <select
                value={formData.model}
                onChange={(e) => handleChange("model", e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="claude-3-5-sonnet-20241022">
                  Claude 3.5 Sonnet
                </option>
                <option value="claude-3-sonnet-20240229">
                  Claude 3 Sonnet
                </option>
                <option value="claude-3-haiku-20240307">Claude 3 Haiku</option>
              </select>
            </div>
          </div>

          {/* 온도 설정 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              온도: {formData.temperature}
            </label>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={formData.temperature}
              onChange={(e) =>
                handleChange("temperature", parseFloat(e.target.value))
              }
              className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
            />
            <div className="flex justify-between text-xs text-gray-500 mt-1">
              <span>정확함 (0.0)</span>
              <span>창의적 (1.0)</span>
            </div>
          </div>

          {/* 파일 업로드 또는 직접 입력 */}
          {inputMethod === "upload" && !initialData && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                파일 선택
              </label>
              <input
                type="file"
                accept=".txt,.md,.doc,.docx"
                onChange={(e) => setFile(e.target.files[0])}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          )}

          {(inputMethod === "direct" || initialData) && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                프롬프트 내용
              </label>
              <textarea
                value={formData.content}
                onChange={(e) => handleChange("content", e.target.value)}
                rows={8}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="프롬프트 내용을 입력하세요..."
              />
              <div className="mt-2 text-right">
                <button
                  type="button"
                  onClick={() =>
                    handleChange(
                      "content",
                      formData.content + "\n\n이 입력란을 작성하세요."
                    )
                  }
                  className="text-xs text-blue-600 hover:text-blue-800"
                >
                  이 입력란을 작성하세요.
                </button>
              </div>
            </div>
          )}

          {/* 버튼 */}
          <div className="flex justify-end space-x-3 pt-6">
            <button
              type="button"
              onClick={onCancel}
              className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              취소
            </button>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
            >
              {initialData ? "수정" : "저장"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// 개선된 사이드바 프롬프트 카드 (메뉴 버튼 추가)
const SidebarPromptCard = ({ card, onEdit, onDelete, onToggle }) => {
  const [showMenu, setShowMenu] = useState(false);
  const [isToggling, setIsToggling] = useState(false);

  const getCategoryColor = (category) => {
    const colorMap = {
      역할: "bg-blue-100 text-blue-800",
      가이드라인: "bg-purple-100 text-purple-800",
      워크플로우: "bg-green-100 text-green-800",
      출력결과: "bg-orange-100 text-orange-800",
      예시: "bg-yellow-100 text-yellow-800",
      검수: "bg-red-100 text-red-800",
    };
    return colorMap[category] || "bg-gray-100 text-gray-800";
  };

  const getModelDisplayName = (modelId) => {
    if (modelId?.includes("claude-3-5-sonnet")) return "Claude 3.5 Sonnet";
    if (modelId?.includes("claude-3-sonnet")) return "Claude 3 Sonnet";
    if (modelId?.includes("claude")) return "Claude";
    return "Unknown Model";
  };

  // 즉시 반응하는 토글 (낙관적 업데이트)
  const handleToggleClick = async () => {
    if (isToggling) return;

    setIsToggling(true);
    const newEnabled = !card.enabled;

    // 즉시 UI 업데이트 (낙관적 업데이트)
    card.enabled = newEnabled;

    try {
      await onToggle(card.promptId || card.id, newEnabled);
    } catch (error) {
      // 실패시 원래 상태로 복원
      card.enabled = !newEnabled;
      toast.error("상태 변경에 실패했습니다.");
    } finally {
      setIsToggling(false);
    }
  };

  const handleDelete = () => {
    setShowMenu(false);
    onDelete(card.promptId || card.id);
  };

  const handleEdit = () => {
    setShowMenu(false);
    onEdit(card);
  };

  return (
    <div className="group p-4 rounded-xl border border-gray-200 bg-white hover:bg-gray-50 transition-all duration-200">
      <div className="flex items-start justify-between">
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-2">
            <span
              className={`inline-flex items-center px-2 py-1 rounded-md text-xs font-medium ${getCategoryColor(
                card.category
              )}`}
            >
              {card.category}
            </span>
            <h6 className="text-sm font-medium text-gray-900 truncate">
              {card.title}
            </h6>
          </div>
          <div className="text-xs text-gray-500 space-y-1">
            <div>Temperature: {card.temperature || 0.7}</div>
            <div>Model: {getModelDisplayName(card.model)}</div>
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          {/* 메뉴 버튼 */}
          <div className="relative">
            <button
              onClick={() => setShowMenu(!showMenu)}
              className="opacity-0 group-hover:opacity-100 p-1 text-gray-400 hover:text-gray-600 rounded transition-all"
            >
              <EllipsisHorizontalIcon className="h-4 w-4" />
            </button>

            {/* 드롭다운 메뉴 */}
            {showMenu && (
              <div className="absolute right-0 top-8 w-32 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10">
                <button
                  onClick={handleEdit}
                  className="w-full px-3 py-2 text-left text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                >
                  <PencilIcon className="h-4 w-4 mr-2" />
                  편집
                </button>
                <button
                  onClick={handleDelete}
                  className="w-full px-3 py-2 text-left text-sm text-red-600 hover:bg-red-50 flex items-center"
                >
                  <TrashIcon className="h-4 w-4 mr-2" />
                  삭제
                </button>
              </div>
            )}
          </div>

          {/* 토글 스위치 */}
          <button
            onClick={handleToggleClick}
            disabled={isToggling}
            className={`w-8 h-4 rounded-full relative transition-colors ${
              card.enabled ? "bg-blue-600" : "bg-gray-300"
            } ${isToggling ? "opacity-50" : ""}`}
          >
            <div
              className={`w-3 h-3 bg-white rounded-full shadow transform transition-transform absolute top-0.5 ${
                card.enabled ? "translate-x-4" : "translate-x-0.5"
              }`}
            ></div>
          </button>
        </div>
      </div>

      {/* 메뉴가 열려있을 때 클릭시 닫기 */}
      {showMenu && (
        <div
          className="fixed inset-0 z-5"
          onClick={() => setShowMenu(false)}
        ></div>
      )}
    </div>
  );
};

// 메인 PromptCardManager 컴포넌트
const PromptCardManager = ({ projectId, onCardsChanged }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showInputModal, setShowInputModal] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [inputMethod, setInputMethod] = useState(null);
  const [editingCard, setEditingCard] = useState(null);

  // 프롬프트 카드 목록 로딩
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(
        projectId,
        true,
        true
      );
      setPromptCards(response.promptCards || []);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 프롬프트 추가 버튼 클릭
  const handleAddPrompt = () => {
    setShowInputModal(true);
  };

  // 입력 방식 선택
  const handleSelectMethod = (method) => {
    setInputMethod(method);
    setShowInputModal(false);
    setShowForm(true);
  };

  // 프롬프트 카드 생성
  const handleCreateCard = async (cardData) => {
    try {
      await promptCardAPI.createPromptCard(projectId, cardData);
      toast.success("프롬프트 카드가 생성되었습니다!");
      setShowForm(false);
      setInputMethod(null);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 생성 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 수정
  const handleUpdateCard = async (cardData) => {
    try {
      await promptCardAPI.updatePromptCard(
        projectId,
        editingCard.promptId || editingCard.id,
        cardData
      );
      toast.success("프롬프트 카드가 수정되었습니다!");
      setEditingCard(null);
      setShowForm(false);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 수정 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 삭제
  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트 카드를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트 카드가 삭제되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 활성/비활성 토글 (백그라운드 처리)
  const handleToggleCard = async (promptId, enabled) => {
    try {
      await promptCardAPI.togglePromptCard(projectId, promptId, enabled);
      // 성공시에만 토스트 메시지 (선택적)
      // toast.success(enabled ? "카드가 활성화되었습니다!" : "카드가 비활성화되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      throw error; // SidebarPromptCard에서 처리
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setInputMethod("direct");
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setInputMethod(null);
    setEditingCard(null);
  };

  // 카테고리별 그룹화
  const groupedCards = promptCards.reduce((groups, card) => {
    const category = card.category || "기타";
    if (!groups[category]) {
      groups[category] = [];
    }
    groups[category].push(card);
    return groups;
  }, {});

  const activeCards = promptCards.filter(
    (card) => card.enabled !== false
  ).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 왼쪽 채팅 영역 */}
      <div className="flex-1 flex flex-col">
        {/* 상단 헤더 (새 프롬프트 추가 버튼) */}
        <div className="bg-white border-b border-gray-200 px-6 py-4">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">
              AI 워크스페이스
            </h2>
            <button
              onClick={handleAddPrompt}
              className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
            >
              <PlusIcon className="h-4 w-4 mr-2" />새 프롬프트 추가
            </button>
          </div>
        </div>

        {/* 메인 콘텐츠 영역 */}
        <div className="flex-1 overflow-hidden">
          {showForm ? (
            <div className="h-full overflow-y-auto p-6">
              <PromptForm
                inputMethod={inputMethod}
                onSubmit={editingCard ? handleUpdateCard : handleCreateCard}
                onCancel={handleCancelForm}
                initialData={editingCard}
              />
            </div>
          ) : (
            <ChatInterface projectId={projectId} />
          )}
        </div>
      </div>

      {/* 우측 사이드바 */}
      <aside className="w-96 bg-white border-l border-gray-200 shadow-sm flex flex-col">
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
          <div className="flex items-center space-x-2">
            <Cog6ToothIcon className="h-5 w-5 text-gray-600" />
            <h3 className="text-sm font-semibold text-gray-900">
              프로젝트 설정
            </h3>
          </div>
          <div className="text-xs text-gray-500">
            {activeCards}/{promptCards.length} 활성
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4">
          <div className="space-y-6">
            {Object.keys(groupedCards).length === 0 ? (
              <div className="text-center py-8">
                <DocumentTextIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">프롬프트 카드가 없습니다</p>
                <button
                  onClick={handleAddPrompt}
                  className="mt-4 text-blue-600 hover:text-blue-800 text-sm"
                >
                  첫 번째 카드 만들기
                </button>
              </div>
            ) : (
              Object.entries(groupedCards).map(([category, cards]) => (
                <div key={category}>
                  <h5 className="text-xs font-medium text-gray-700 uppercase tracking-wider mb-2">
                    {category}
                  </h5>
                  <div className="space-y-2">
                    {cards.map((card) => (
                      <SidebarPromptCard
                        key={card.promptId || card.id}
                        card={card}
                        onEdit={handleEditCard}
                        onDelete={handleDeleteCard}
                        onToggle={handleToggleCard}
                      />
                    ))}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </aside>

      {/* 입력 방식 선택 모달 */}
      <InputMethodModal
        isOpen={showInputModal}
        onClose={() => setShowInputModal(false)}
        onSelectMethod={handleSelectMethod}
      />
    </div>
  );
};

export default PromptCardManager;
