import axios from "axios";

// 🔧 API 기본 설정 개선
const API_BASE_URL =
  process.env.REACT_APP_API_URL ||
  "https://gcm3qzoy04.execute-api.us-east-1.amazonaws.com/prod";

// Axios 인스턴스 개선
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
  timeout: 300000, // 🔧 수정: 5분으로 단축 (기존 15분은 너무 길어서 504 타임아웃 발생)
});

// 요청 인터셉터
api.interceptors.request.use((config) => {
  console.log("API 요청:", config.method?.toUpperCase(), config.url);
  return config;
});

// 응답 인터셉터
api.interceptors.response.use(
  (response) => {
    console.log("API 응답:", response.status, response.config.url);
    return response;
  },
  (error) => {
    console.error("API 오류 상세:", {
      status: error.response?.status,
      message: error.message,
      code: error.code,
      url: error.config?.url,
      data: error.response?.data,
    });
    return Promise.reject(error);
  }
);

// =============================================================================
// 1. 프로젝트 API
// =============================================================================

export const projectAPI = {
  getProjects: async () => {
    const response = await api.get("/projects");
    return response.data;
  },

  getProject: async (projectId) => {
    const response = await api.get(`/projects/${projectId}`);
    return response.data;
  },

  createProject: async (projectData) => {
    const response = await api.post("/projects", projectData);
    return response.data;
  },

  updateProject: async (projectId, projectData) => {
    const response = await api.put(`/projects/${projectId}`, projectData);
    return response.data;
  },

  deleteProject: async (projectId) => {
    const response = await api.delete(`/projects/${projectId}`);
    return response.data;
  },

  getUploadUrl: async (projectId, fileName) => {
    const response = await api.get(`/projects/${projectId}/upload-url`, {
      params: { fileName },
    });
    return response.data;
  },
};

// =============================================================================
// 2. 프롬프트 카드 API
// =============================================================================

export const promptCardAPI = {
  getPromptCards: async (
    projectId,
    includeContent = false,
    includeStats = false
  ) => {
    const response = await api.get(`/prompts/${projectId}`, {
      params: { includeContent, includeStats },
    });
    return response.data;
  },

  createPromptCard: async (projectId, promptData) => {
    const response = await api.post(`/prompts/${projectId}`, promptData);
    return response.data;
  },

  updatePromptCard: async (projectId, promptId, promptData) => {
    const response = await api.put(
      `/prompts/${projectId}/${promptId}`,
      promptData
    );
    return response.data;
  },

  getPromptContent: async (projectId, promptId) => {
    const response = await api.get(`/prompts/${projectId}/${promptId}/content`);
    return response.data;
  },

  deletePromptCard: async (projectId, promptId) => {
    const response = await api.delete(`/prompts/${projectId}/${promptId}`);
    return response.data;
  },

  reorderPromptCards: async (projectId, reorderData) => {
    // 백엔드에서 별도의 reorder API가 없으므로 개별 업데이트로 처리
    // reorderData = [{ promptId, stepOrder }, ...]
    const updatePromises = reorderData.map(({ promptId, stepOrder }) =>
      api.put(`/prompts/${projectId}/${promptId}`, { stepOrder })
    );

    const responses = await Promise.all(updatePromises);
    return {
      message: "프롬프트 카드 순서가 업데이트되었습니다.",
      updatedCards: responses.map((r) => r.data),
    };
  },
};

// =============================================================================
// 3. 제목 생성 API
// =============================================================================

// 🔧 개선된 generateAPI
export const generateAPI = {
  generateTitle: async (projectId, data) => {
    console.log("대화 생성 요청 시작:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    try {
      const response = await api.post(`/projects/${projectId}/generate`, data);

      console.log("대화 생성 성공:", {
        status: response.status,
        mode: response.data.mode,
        message: response.data.message,
        timestamp: new Date().toISOString(),
      });

      return response.data;
    } catch (error) {
      console.error("대화 생성 실패:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        responseData: error.response?.data,
        timestamp: new Date().toISOString(),
      });
      throw error;
    }
  },

  // 🔧 스트리밍 API 완전 개선 - 실패 시 즉시 폴백
  generateTitleStream: async (
    projectId,
    data,
    onChunk,
    onError,
    onComplete
  ) => {
    console.log("스트리밍 대화 생성 요청 시작:", {
      projectId,
      inputLength: data.userInput.length,
      historyLength: data.chat_history?.length || 0,
      timestamp: new Date().toISOString(),
    });

    // 🔧 스트리밍 시도 전에 간단한 연결 테스트
    try {
      // 스트리밍 엔드포인트 사전 테스트 (매우 짧은 타임아웃)
      await api.post(
        `/projects/${projectId}/generate/stream`,
        { userInput: "test", chat_history: [] },
        {
          timeout: 5000, // 5초 테스트
          validateStatus: () => false, // 모든 상태 코드 허용 (테스트용)
        }
      );
    } catch (testError) {
      console.log(
        "🔧 스트리밍 엔드포인트 사전 테스트 실패, 즉시 폴백:",
        testError.code
      );

      // 즉시 일반 API로 폴백
      try {
        const fallbackResponse = await api.post(
          `/projects/${projectId}/generate`,
          data
        );

        console.log("✅ 즉시 폴백 성공:", {
          mode: fallbackResponse.data.mode,
          timestamp: new Date().toISOString(),
        });

        // 🔧 폴백 응답을 스트리밍 형태로 시뮬레이션
        if (fallbackResponse.data.result && onChunk) {
          const fullText = fallbackResponse.data.result;
          const words = fullText.split(" ");

          // 빠른 속도로 워드 단위 스트리밍 시뮬레이션
          for (let i = 0; i < words.length; i++) {
            const word = words[i] + (i < words.length - 1 ? " " : "");
            onChunk(word, { content: word });

            // 매우 짧은 지연 (실시간 느낌)
            await new Promise((resolve) => setTimeout(resolve, 50));
          }
        }

        // 완료 콜백 호출
        if (onComplete) {
          onComplete({
            result: fallbackResponse.data.result,
            model_info: fallbackResponse.data.model_info,
            performance_metrics: fallbackResponse.data.performance_metrics,
            timestamp: new Date().toISOString(),
          });
        }

        return fallbackResponse.data;
      } catch (fallbackError) {
        console.error("❌ 폴백도 실패:", fallbackError);
        if (onError) {
          onError(
            new Error(
              "모든 API 엔드포인트가 실패했습니다. 서버 상태를 확인해주세요."
            )
          );
        }
        throw new Error(
          "서비스를 사용할 수 없습니다. 잠시 후 다시 시도해주세요."
        );
      }
    }

    // 🔧 실제 스트리밍 시도 (사전 테스트 통과한 경우에만)
    try {
      console.log("🔧 실제 스트리밍 API 호출 시도...");

      const response = await api.post(
        `/projects/${projectId}/generate/stream`,
        data,
        {
          timeout: 120000, // 🔧 2분으로 단축
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      );

      if (!response || !response.data) {
        throw new Error("스트리밍 응답이 없습니다");
      }

      const responseData = response.data;

      console.log("✅ 스트리밍 응답 수신:", {
        mode: responseData.mode,
        resultLength: responseData.result?.length || 0,
        chunksCount: responseData.chunks?.length || 0,
        timestamp: new Date().toISOString(),
      });

      // 청크 데이터 처리
      if (responseData.chunks && Array.isArray(responseData.chunks)) {
        for (const chunk of responseData.chunks) {
          if (chunk.content && onChunk) {
            onChunk(chunk.content, chunk);
            await new Promise((resolve) => setTimeout(resolve, 30)); // 스트리밍 효과
          }
        }
      } else if (responseData.result && onChunk) {
        // 청크 없는 경우 텍스트를 단어 단위로 분할
        const words = responseData.result.split(" ");
        const chunkSize = Math.max(1, Math.floor(words.length / 20)); // 20개 청크

        for (let i = 0; i < words.length; i += chunkSize) {
          const chunkWords = words.slice(i, i + chunkSize);
          const chunkContent =
            chunkWords.join(" ") + (i + chunkSize < words.length ? " " : "");

          if (chunkContent.trim()) {
            onChunk(chunkContent, { content: chunkContent });
            await new Promise((resolve) => setTimeout(resolve, 80));
          }
        }
      }

      // 완료 콜백 호출
      if (onComplete) {
        onComplete({
          result: responseData.result,
          model_info: responseData.model_info,
          performance_metrics: responseData.performance_metrics,
          timestamp: responseData.timestamp || new Date().toISOString(),
        });
      }

      return responseData;
    } catch (error) {
      console.error("스트리밍 대화 생성 실패:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        timestamp: new Date().toISOString(),
      });

      // 🔧 스트리밍 실패 시 즉시 일반 API로 폴백
      if (
        error.code === "ECONNABORTED" ||
        error.code === "ERR_NETWORK" ||
        error.response?.status === 504
      ) {
        console.log("🔧 스트리밍 실패로 인한 일반 API 폴백...");

        try {
          const fallbackResponse = await api.post(
            `/projects/${projectId}/generate`,
            data
          );

          console.log("✅ 폴백 API 성공:", {
            mode: fallbackResponse.data.mode,
            timestamp: new Date().toISOString(),
          });

          if (onComplete) {
            onComplete({
              result: fallbackResponse.data.result,
              model_info: fallbackResponse.data.model_info,
              performance_metrics: fallbackResponse.data.performance_metrics,
              timestamp: new Date().toISOString(),
            });
          }

          return fallbackResponse.data;
        } catch (fallbackError) {
          console.error("❌ 폴백 API도 실패:", fallbackError);

          if (onError) {
            onError(
              new Error(
                "네트워크 연결에 문제가 있습니다. 잠시 후 다시 시도해주세요."
              )
            );
          }

          throw new Error("서비스 연결에 실패했습니다.");
        }
      }

      // 기타 오류
      if (onError) {
        onError(error);
      }

      throw error;
    }
  },

  getExecutionStatus: async (executionArn) => {
    return {
      status: "SUCCEEDED",
      output: "{}",
    };
  },
};

