// 🔧 개선된 오류 처리 함수
export const handleAPIError = (error) => {
  console.error("API 오류 상세 분석:", {
    message: error.message,
    code: error.code,
    status: error.response?.status,
    statusText: error.response?.statusText,
    data: error.response?.data,
    timestamp: new Date().toISOString(),
  });

  // 🔧 Gateway Timeout 특별 처리
  if (error.response?.status === 504) {
    return {
      message:
        "서버 응답 시간이 초과되었습니다. 요청을 간소화하거나 잠시 후 다시 시도해주세요.",
      statusCode: 504,
      errorType: "GATEWAY_TIMEOUT",
    };
  }

  // 🔧 CORS 오류 특별 처리
  if (
    error.message?.includes("CORS") ||
    error.code === "ERR_NETWORK" ||
    error.message?.includes("Access-Control-Allow-Origin")
  ) {
    return {
      message: "서버 연결 설정에 문제가 있습니다. 관리자에게 문의하세요.",
      statusCode: 0,
      errorType: "CORS_ERROR",
    };
  }

  // 🔧 타임아웃 오류 특별 처리
  if (error.code === "ECONNABORTED") {
    return {
      message:
        "요청 처리 시간이 초과되었습니다. 입력을 줄이거나 잠시 후 다시 시도해주세요.",
      statusCode: 0,
      errorType: "TIMEOUT_ERROR",
    };
  }

  if (error.response) {
    const status = error.response.status;
    const message =
      error.response.data?.message ||
      error.response.data?.error ||
      "서버 오류가 발생했습니다";

    switch (status) {
      case 400:
        return { message: `잘못된 요청: ${message}`, statusCode: 400 };
      case 401:
        return { message: "인증이 필요합니다", statusCode: 401 };
      case 403:
        return { message: "권한이 없습니다", statusCode: 403 };
      case 404:
        return { message: "요청한 리소스를 찾을 수 없습니다", statusCode: 404 };
      case 429:
        return {
          message: "요청이 너무 많습니다. 잠시 후 다시 시도해주세요",
          statusCode: 429,
        };
      case 500:
        return { message: "서버 내부 오류가 발생했습니다", statusCode: 500 };
      default:
        return {
          message: `서버 오류 (${status}): ${message}`,
          statusCode: status,
        };
    }
  } else if (error.request) {
    return {
      message: "서버에 연결할 수 없습니다. 네트워크 연결을 확인해주세요",
      statusCode: 0,
      errorType: "NETWORK_ERROR",
    };
  } else {
    return {
      message: `요청 오류: ${error.message}`,
      statusCode: -1,
      errorType: "REQUEST_ERROR",
    };
  }
};

