import React, { useState, useEffect, useMemo, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  FolderOpenIcon,
  PlusIcon,
  EllipsisHorizontalIcon,
  TrashIcon,
  PencilIcon,
  CalendarIcon,
  DocumentTextIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  Squares2X2Icon,
  ListBulletIcon,
  TagIcon,
  BookmarkIcon,
  SparklesIcon,
  ArrowPathIcon,
  FolderIcon,
  XMarkIcon,
  ChevronDownIcon,
  CheckIcon,
  Cog6ToothIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  DEFAULT_PROJECT_CATEGORIES,
  categoryAPI,
  projectCategoryAPI,
  getCategoryInfo,
  getCategoryColorClasses,
  filterProjects,
  formatTokenCount,
  formatFileSize,
  calculatePromptStats,
  promptCardAPI,
  COLOR_OPTIONS,
} from "../services/api";
import { ProjectListSkeleton, ProjectCardSkeleton } from "./SkeletonLoader";
import CreateProject from "./CreateProject";

const ProjectList = () => {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 필터링 상태
  const [activeCategory, setActiveCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("created");
  const [viewMode, setViewMode] = useState("grid");
  const [sortDropdownOpen, setSortDropdownOpen] = useState(false);
  const sortDropdownRef = useRef(null);

  // 카테고리 상태
  const [userCategories, setUserCategories] = useState([]);
  const [showCategoryModal, setShowCategoryModal] = useState(false);
  const [showCategoryManager, setShowCategoryManager] = useState(false); // �� 복원: 카테고리 관리 state

  // 프로젝트 편집 상태
  const [editingProject, setEditingProject] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  // 드래그앤드롭 상태
  const [draggedProject, setDraggedProject] = useState(null);
  const [dropTargetCategory, setDropTargetCategory] = useState(null);

  // 통계 상태 - 제거됨
  // const [projectStats, setProjectStats] = useState({});
  // const [overallStats, setOverallStats] = useState({
  //   totalProjects: 0,
  //   totalPrompts: 0,
  //   totalTokens: 0,
  //   categoriesUsed: 0,
  // });

  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    loadProjects();
    loadUserCategories();
  }, []);

  const loadUserCategories = async () => {
    try {
      const data = await categoryAPI.getUserCategories();
      setUserCategories(data.categories || []);
    } catch (error) {
      console.error("카테고리 로드 실패:", error);
      setUserCategories(DEFAULT_PROJECT_CATEGORIES);
    }
  };

  const loadProjects = async () => {
    try {
      console.log("🔄 loadProjects 시작");
      setLoading(true);
      setError(null);
      const data = await projectAPI.getProjects();
      const projectsWithStats = data.projects || [];
      console.log("📊 로드된 프로젝트 수:", projectsWithStats.length);
      console.log("📋 프로젝트 목록:", projectsWithStats);

      // 통계 로딩 제거 - 직접 프로젝트 설정
      setProjects(projectsWithStats);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteProject = async (projectId, projectName) => {
    if (!window.confirm(`"${projectName}" 프로젝트를 삭제하시겠습니까?`)) {
      return;
    }

    // 낙관적 업데이트: 즉시 UI에서 프로젝트 제거
    const originalProjects = [...projects];
    const updatedProjects = projects.filter((p) => p.projectId !== projectId);
    setProjects(updatedProjects);

    // 통계도 업데이트
    const newStats = { ...overallStats };
    newStats.totalProjects = Math.max(0, newStats.totalProjects - 1);
    setOverallStats(newStats);

    // 즉시 성공 토스트 표시
    toast.success("프로젝트가 삭제되었습니다");

    try {
      // 백그라운드에서 실제 삭제 진행
      await projectAPI.deleteProject(projectId);
      console.log("프로젝트 삭제 성공:", projectId);
    } catch (err) {
      console.error("프로젝트 삭제 오류:", err);

      // 실패 시 원래 상태로 복원
      setProjects(originalProjects);
      setOverallStats(overallStats); // 원래 통계로 복원

      let errorMessage = "프로젝트 삭제에 실패했습니다";

      if (err.response?.status === 403) {
        errorMessage =
          "삭제 권한이 없습니다. S3 파일 삭제 권한을 확인해주세요.";
      } else if (err.response?.status === 404) {
        errorMessage = "이미 삭제된 프로젝트입니다";
        // 404의 경우 실제로는 삭제된 것이므로 복원하지 않음
        return;
      } else if (err.response) {
        errorMessage =
          err.response.data?.message || `서버 오류 (${err.response.status})`;
      } else if (err.request) {
        errorMessage = "네트워크 오류: CORS 또는 연결 문제";
      }

      toast.error(errorMessage);
    }
  };

  // 프로젝트 카테고리 변경
  const changeProjectCategory = async (projectId, categoryId) => {
    try {
      await projectCategoryAPI.updateProjectCategory(projectId, categoryId);
      toast.success("카테고리가 변경되었습니다");
      loadProjects();
    } catch (err) {
      toast.error("카테고리 변경에 실패했습니다");
    }
  };

  // 프로젝트 편집 함수들
  const handleEditProject = (project) => {
    setEditingProject(project);
    setShowEditModal(true);
  };

  const handleUpdateProject = async (projectData) => {
    try {
      await projectAPI.updateProject(editingProject.projectId, projectData);
      toast.success("프로젝트가 수정되었습니다");
      setShowEditModal(false);
      setEditingProject(null);
      loadProjects();
    } catch (err) {
      const errorInfo = handleAPIError(err);
      toast.error(`프로젝트 수정 실패: ${errorInfo.message}`);
    }
  };

  const handleCancelEdit = () => {
    setShowEditModal(false);
    setEditingProject(null);
  };

  // 드래그앤드롭 핸들러들
  const handleDragStart = (e, project) => {
    setDraggedProject(project);
    if (e.dataTransfer) {
      e.dataTransfer.effectAllowed = "move";
      e.dataTransfer.setData("text/html", e.target.outerHTML);
    }
  };

  const handleDragOver = (e, categoryId) => {
    e.preventDefault();
    if (e.dataTransfer) {
      e.dataTransfer.dropEffect = "move";
    }
    setDropTargetCategory(categoryId);
  };

  const handleDragLeave = (e) => {
    // 드래그가 카테고리 영역을 벗어날 때
    if (!e.currentTarget.contains(e.relatedTarget)) {
      setDropTargetCategory(null);
    }
  };

  const handleDrop = async (e, categoryId) => {
    e.preventDefault();
    setDropTargetCategory(null);

    if (draggedProject) {
      // "전체" 카테고리로 드래그하면 카테고리를 기본값으로 설정
      const targetCategory = categoryId === "all" ? "news" : categoryId;

      if (targetCategory !== draggedProject.category) {
        await changeProjectCategory(draggedProject.projectId, targetCategory);
      }
    }
    setDraggedProject(null);
  };

  const handleDragEnd = () => {
    setDraggedProject(null);
    setDropTargetCategory(null);
  };

  // 필터링된 프로젝트 목록
  const filteredProjects = useMemo(() => {
    return filterProjects(projects, {
      category: activeCategory,
      searchQuery,
      sortBy,
    });
  }, [projects, activeCategory, searchQuery, sortBy]);

  // 카테고리별 프로젝트 수
  const categoryStats = useMemo(() => {
    const stats = { all: projects.length };
    userCategories.forEach((category) => {
      stats[category.id] = projects.filter(
        (p) => p.category === category.id
      ).length;
    });
    return stats;
  }, [projects, userCategories]);

  // 드롭다운 외부 클릭 감지
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        sortDropdownRef.current &&
        !sortDropdownRef.current.contains(event.target)
      ) {
        setSortDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // 정렬 옵션 데이터
  const sortOptions = [
    { value: "created", label: "생성일순" },
    { value: "updated", label: "수정일순" },
    { value: "name", label: "이름순" },
  ];

  const currentSortOption = sortOptions.find(
    (option) => option.value === sortBy
  );

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">프로젝트 목록을 불러오는 중...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4">⚠️ 오류가 발생했습니다</div>
        <p className="text-gray-600 mb-6">{error}</p>
        <button
          onClick={loadProjects}
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
        >
          다시 시도
        </button>
      </div>
    );
  }

  const handleCreateSuccess = () => {
    console.log("📝 handleCreateSuccess 호출됨 - 프로젝트 목록 새로고침 시작");
    loadProjects(); // 프로젝트 목록 새로고침
  };

  return (
    <div className="space-y-6">
      {/* 헤더 & 전체 통계 */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">프로젝트 목록</h2>
          <p className="text-gray-600 mt-1">
            AI 제목 생성 프로젝트를 관리하고 새로운 프로젝트를 생성하세요
          </p>
        </div>
      </div>

      {/* 드래그 안내 메시지 */}
      {draggedProject && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
            <p className="text-blue-700 font-medium">
              "{draggedProject.name}" 프로젝트를 원하는 카테고리에 드래그해서
              놓으세요
            </p>
          </div>
        </div>
      )}

      {/* 필터링 바 */}
      <div className="bg-white rounded-xl border border-gray-200 p-6 shadow-sm">
        {/* 카테고리 탭 */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => setActiveCategory("all")}
              onDragOver={(e) => handleDragOver(e, "all")}
              onDragLeave={handleDragLeave}
              onDrop={(e) => handleDrop(e, "all")}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                activeCategory === "all"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              } ${
                dropTargetCategory === "all" && draggedProject
                  ? "ring-2 ring-blue-400 bg-blue-100 scale-105"
                  : ""
              }`}
            >
              전체 ({categoryStats.all})
              {dropTargetCategory === "all" && draggedProject && (
                <span className="ml-2 text-xs">← 여기에 놓기</span>
              )}
            </button>
            {userCategories.map((category) => (
              <button
                key={category.id}
                onClick={() => setActiveCategory(category.id)}
                onDragOver={(e) => handleDragOver(e, category.id)}
                onDragLeave={handleDragLeave}
                onDrop={(e) => handleDrop(e, category.id)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all flex items-center space-x-2 ${
                  activeCategory === category.id
                    ? `bg-${category.color}-600 text-white`
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                } ${
                  dropTargetCategory === category.id && draggedProject
                    ? `ring-2 ring-${category.color}-400 bg-${category.color}-100 scale-105`
                    : ""
                }`}
              >
                <div
                  className={`w-2 h-2 rounded-full bg-${category.color}-500`}
                ></div>
                <span>{category.name}</span>
                <span className="bg-white bg-opacity-20 px-2 py-0.5 rounded-full text-xs">
                  {categoryStats[category.id] || 0}
                </span>
                {dropTargetCategory === category.id && draggedProject && (
                  <span className="text-xs">← 놓기</span>
                )}
              </button>
            ))}
          </div>

          {/* 🔧 카테고리 관리 버튼 복원 - 카테고리 자유 관리 가능 */}
          <div className="flex items-center space-x-3">
            <button
              onClick={() => setShowCategoryManager(true)}
              className="inline-flex items-center px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <Cog6ToothIcon className="h-4 w-4 mr-2" />
              카테고리 관리
            </button>
            <span className="text-xs text-gray-500 bg-gray-50 px-3 py-1 rounded-full">
              💡 프로젝트 내 프롬프트 카드로 세부 커스터마이징하세요
            </span>
          </div>
        </div>

        {/* 검색 및 정렬 */}
        <div className="flex flex-col sm:flex-row gap-4">
          {/* 검색바 */}
          <div className="flex-1 relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="프로젝트 이름, 설명, 태그로 검색..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* 정렬 옵션 - 커스텀 드롭다운 */}
          <div className="flex items-center space-x-3">
            <FunnelIcon className="h-5 w-5 text-gray-400 flex-shrink-0" />
            <div className="relative" ref={sortDropdownRef}>
              <button
                onClick={() => setSortDropdownOpen(!sortDropdownOpen)}
                className="flex items-center justify-between pl-4 pr-3 py-3 bg-white border border-gray-300 rounded-lg hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 min-w-[140px]"
              >
                <div className="flex items-center space-x-2">
                  <span className="text-gray-700 font-medium">
                    {currentSortOption?.label}
                  </span>
                </div>
                <ChevronDownIcon
                  className={`h-4 w-4 text-gray-400 transition-transform duration-200 ${
                    sortDropdownOpen ? "rotate-180" : ""
                  }`}
                />
              </button>

              {/* 드롭다운 메뉴 */}
              {sortDropdownOpen && (
                <div className="absolute top-full left-0 mt-1 w-full bg-white border border-gray-200 rounded-lg shadow-lg z-50 overflow-hidden">
                  {sortOptions.map((option) => (
                    <button
                      key={option.value}
                      onClick={() => {
                        setSortBy(option.value);
                        setSortDropdownOpen(false);
                      }}
                      className={`w-full flex items-center justify-between px-4 py-3 text-left hover:bg-gray-50 transition-colors duration-150 ${
                        sortBy === option.value
                          ? "bg-blue-50 text-blue-600"
                          : "text-gray-700"
                      }`}
                    >
                      <div className="flex items-center space-x-2">
                        <span className="font-medium">{option.label}</span>
                      </div>
                      {sortBy === option.value && (
                        <CheckIcon className="h-4 w-4 text-blue-600" />
                      )}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* 뷰 모드 */}
          <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setViewMode("grid")}
              className={`p-2 rounded-md ${
                viewMode === "grid" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <Squares2X2Icon className="h-4 w-4" />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-md ${
                viewMode === "list" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <ListBulletIcon className="h-4 w-4" />
            </button>
          </div>

          {/* 새 프로젝트 버튼 */}
          <button
            onClick={() => setShowCreateModal(true)}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
          >
            <PlusIcon className="h-4 w-4 mr-2" />새 프로젝트
          </button>
        </div>
      </div>

      {/* 프로젝트 목록 */}
      {filteredProjects.length === 0 ? (
        <div className="text-center py-16 bg-white rounded-xl border border-gray-200">
          <FolderOpenIcon className="mx-auto h-16 w-16 text-gray-400 mb-4" />
          <h3 className="text-xl font-medium text-gray-900 mb-2">
            {searchQuery || activeCategory !== "all"
              ? "조건에 맞는 프로젝트가 없습니다"
              : "프로젝트가 없습니다"}
          </h3>
          <p className="text-gray-500 mb-8">
            {searchQuery || activeCategory !== "all"
              ? "다른 조건으로 검색해보세요"
              : "첫 번째 프로젝트를 생성해보세요"}
          </p>
          <button
            onClick={() => setShowCreateModal(true)}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-medium"
          >
            <PlusIcon className="h-4 w-4 mr-2" />새 프로젝트 생성
          </button>
        </div>
      ) : (
        <div
          className={
            viewMode === "grid"
              ? "grid gap-6 md:grid-cols-2 lg:grid-cols-3"
              : "space-y-4"
          }
        >
          {filteredProjects.map((project) => (
            <ProjectCard
              key={project.projectId}
              project={project}
              stats={projectStats[project.projectId]}
              onDelete={deleteProject}
              onEdit={handleEditProject}
              onCategoryChange={changeProjectCategory}
              onDragStart={handleDragStart}
              onDragEnd={handleDragEnd}
              viewMode={viewMode}
              userCategories={userCategories}
              isDragging={draggedProject?.projectId === project.projectId}
              navigate={navigate}
            />
          ))}
        </div>
      )}

      {/* 새 프로젝트 생성 모달 */}
      <CreateProject
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSuccess={handleCreateSuccess}
      />

      {/* 🔧 카테고리 관리 모달 복원 - 카테고리 자유 관리 가능 */}
      {showCategoryManager && (
        <CategoryManager
          categories={userCategories}
          onClose={() => setShowCategoryManager(false)}
          onSave={loadUserCategories}
        />
      )}

      {/* 프로젝트 편집 모달 */}
      <ProjectEditModal
        project={editingProject}
        userCategories={userCategories}
        isOpen={showEditModal}
        onSave={handleUpdateProject}
        onCancel={handleCancelEdit}
      />
    </div>
  );
};

const ProjectCard = ({
  project,
  stats,
  onDelete,
  onEdit,
  onCategoryChange,
  onDragStart,
  onDragEnd,
  viewMode,
  userCategories,
  isDragging,
  navigate,
}) => {
  const [showMenu, setShowMenu] = useState(false);
  const [showCategoryMenu, setShowCategoryMenu] = useState(false);
  const [showMoveMenu, setShowMoveMenu] = useState(false);
  const menuRef = useRef(null);
  const categoryMenuRef = useRef(null);
  const moveMenuRef = useRef(null);
  const categoryInfo = getCategoryInfo(project.category, userCategories);

  // 카드 클릭 핸들러
  const handleCardClick = (e) => {
    // 메뉴 버튼이나 카테고리 버튼을 클릭한 경우 무시
    if (e.target.closest("button") || e.target.closest("a")) {
      return;
    }
    // 프로젝트 상세 페이지로 이동
    navigate(`/projects/${project.projectId}`);
  };

  // 외부 클릭 시 메뉴 닫기
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(false);
      }
      if (
        categoryMenuRef.current &&
        !categoryMenuRef.current.contains(event.target)
      ) {
        setShowCategoryMenu(false);
      }
      if (moveMenuRef.current && !moveMenuRef.current.contains(event.target)) {
        setShowMoveMenu(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  if (viewMode === "list") {
    return (
      <div
        draggable
        onDragStart={(e) => onDragStart(e, project)}
        onDragEnd={onDragEnd}
        onClick={handleCardClick}
        className={`bg-white rounded-xl border border-gray-200 hover:shadow-md transition-all cursor-pointer ${
          isDragging ? "opacity-50 scale-95 shadow-lg" : ""
        }`}
      >
        <div className="p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 flex-1">
              <div className="flex-shrink-0">
                <DocumentTextIcon className="h-12 w-12 text-blue-600" />
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-3 mb-2">
                  <h3 className="text-xl font-semibold text-gray-900 truncate">
                    {project.name}
                  </h3>
                  <div className="relative" ref={categoryMenuRef}>
                    <button
                      onClick={() => setShowCategoryMenu(!showCategoryMenu)}
                      className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border cursor-pointer hover:shadow-sm transition-all ${getCategoryColorClasses(
                        categoryInfo.color
                      )}`}
                    >
                      <div
                        className={`w-2 h-2 rounded-full bg-${categoryInfo.color}-500 mr-2`}
                      ></div>
                      {categoryInfo.name}
                    </button>

                    {showCategoryMenu && (
                      <div className="absolute left-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                        <div className="py-1">
                          {userCategories.map((category) => (
                            <button
                              key={category.id}
                              onClick={() => {
                                onCategoryChange(
                                  project.projectId,
                                  category.id
                                );
                                setShowCategoryMenu(false);
                              }}
                              className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                            >
                              <div
                                className={`w-2 h-2 rounded-full bg-${category.color}-500 mr-3`}
                              ></div>
                              {category.name}
                            </button>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
                <p className="text-gray-500 truncate mb-3">
                  {project.description || "설명 없음"}
                </p>
                {/* 4. 날짜 (생성일, 수정일) - 자연스럽게 배치 */}
                <div className="flex items-center space-x-4 mb-3">
                  <div className="flex items-center text-sm text-gray-500">
                    <CalendarIcon className="h-4 w-4 mr-1.5" />
                    <span>
                      생성{" "}
                      {new Date(project.createdAt).toLocaleDateString("ko-KR")}
                    </span>
                  </div>
                  {project.updatedAt &&
                    project.updatedAt !== project.createdAt && (
                      <div className="flex items-center text-sm text-gray-500">
                        <span className="text-gray-300">•</span>
                        <span className="ml-1.5">
                          수정{" "}
                          {new Date(project.updatedAt).toLocaleDateString(
                            "ko-KR"
                          )}
                        </span>
                      </div>
                    )}
                </div>

                {/* 5. 프롬프트, 토큰 - 일관된 스타일 */}
                <div className="flex items-center space-x-4 mb-3">
                  <div className="flex items-center text-sm text-gray-500">
                    <DocumentTextIcon className="h-4 w-4 mr-1.5" />
                    <span>{stats?.totalCards || 0}개 프롬프트</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-500">
                    <SparklesIcon className="h-4 w-4 mr-1.5" />
                    <span>
                      {formatTokenCount(stats?.totalTokens || 0)} 토큰
                    </span>
                  </div>
                </div>

                {/* 6. 태그 - 책갈피 아이콘 사용 */}
                {project.tags && project.tags.length > 0 && (
                  <div className="mb-4">
                    <div className="flex items-center">
                      <BookmarkIcon className="h-4 w-4 mr-1.5 text-gray-400" />
                      <div className="flex flex-wrap gap-1.5">
                        {project.tags.slice(0, 3).map((tag, index) => (
                          <span
                            key={index}
                            className="inline-flex items-center bg-gray-100 text-gray-600 px-2.5 py-1 rounded-full text-xs font-medium"
                          >
                            {tag}
                          </span>
                        ))}
                        {project.tags.length > 3 && (
                          <span className="inline-flex items-center text-xs text-gray-400 px-2">
                            +{project.tags.length - 3}개
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                )}

                {/* 7. 프롬프트 용량 바 - 마지막 요소 */}
                {stats && stats.totalTokens > 0 && (
                  <div className="pt-2 border-t border-gray-100">
                    <div className="flex items-center justify-between text-xs text-gray-500 mb-2">
                      <span className="font-medium">프롬프트 용량</span>
                      <span className="font-mono text-gray-600">
                        {formatFileSize(stats.totalSize)}
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div
                        className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300"
                        style={{
                          width: `${Math.min(
                            (stats.totalTokens / 10000) * 100,
                            100
                          )}%`,
                        }}
                      ></div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {/* 폴더 이동 메뉴 */}
              <div className="relative" ref={moveMenuRef}>
                <button
                  onClick={() => setShowMoveMenu(!showMoveMenu)}
                  className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100"
                  title="폴더 이동"
                >
                  <FolderIcon className="h-5 w-5" />
                </button>

                {showMoveMenu && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                    <div className="py-1">
                      <div className="px-4 py-2 text-xs font-medium text-gray-500 uppercase tracking-wide">
                        카테고리 선택
                      </div>
                      {userCategories.map((category) => (
                        <button
                          key={category.id}
                          onClick={() => {
                            onCategoryChange(project.projectId, category.id);
                            setShowMoveMenu(false);
                          }}
                          className={`flex items-center w-full px-4 py-2 text-sm hover:bg-gray-50 ${
                            project.category === category.id
                              ? "bg-blue-50 text-blue-700 font-medium"
                              : "text-gray-700"
                          }`}
                        >
                          <div
                            className={`w-2 h-2 rounded-full bg-${category.color}-500 mr-3`}
                          ></div>
                          {category.name}
                          {project.category === category.id && (
                            <span className="ml-auto text-blue-600">✓</span>
                          )}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="relative" ref={menuRef}>
                <button
                  onClick={() => setShowMenu(!showMenu)}
                  className="text-gray-400 hover:text-gray-600 p-2 rounded-lg hover:bg-gray-100"
                >
                  <EllipsisHorizontalIcon className="h-5 w-5" />
                </button>

                {showMenu && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                    <div className="py-1">
                      <button
                        onClick={() => {
                          onEdit(project);
                          setShowMenu(false);
                        }}
                        className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                      >
                        <PencilIcon className="h-4 w-4 mr-3" />
                        편집
                      </button>
                      <button
                        onClick={() => {
                          setShowMoveMenu(true);
                          setShowMenu(false);
                        }}
                        className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                      >
                        <FolderIcon className="h-4 w-4 mr-3" />
                        폴더 이동
                      </button>
                      <button
                        onClick={() => {
                          onDelete(project.projectId, project.name);
                          setShowMenu(false);
                        }}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-700 hover:bg-red-50"
                      >
                        <TrashIcon className="h-4 w-4 mr-3" />
                        삭제
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div
      draggable
      onDragStart={(e) => onDragStart(e, project)}
      onDragEnd={onDragEnd}
      onClick={handleCardClick}
      className={`bg-white rounded-xl shadow-sm border border-gray-200 hover:shadow-md transition-all cursor-pointer ${
        isDragging ? "opacity-50 scale-95 shadow-lg" : ""
      }`}
    >
      <div className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">
              <DocumentTextIcon className="h-8 w-8 text-blue-600" />
            </div>
            <div>
              <h3 className="text-lg font-semibold text-gray-900 truncate">
                {project.name}
              </h3>
              <p className="text-sm text-gray-500 mt-1">
                {project.description || "설명 없음"}
              </p>
            </div>
          </div>

          <div className="relative" ref={menuRef}>
            <button
              onClick={() => setShowMenu(!showMenu)}
              className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
              title="프로젝트 옵션"
            >
              <EllipsisHorizontalIcon className="h-5 w-5" />
            </button>

            {showMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                <div className="py-1">
                  <button
                    onClick={() => {
                      onEdit(project);
                      setShowMenu(false);
                    }}
                    className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                  >
                    <PencilIcon className="h-4 w-4 mr-3" />
                    편집
                  </button>
                  <button
                    onClick={() => {
                      setShowMoveMenu(true);
                      setShowMenu(false);
                    }}
                    className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                  >
                    <FolderIcon className="h-4 w-4 mr-3" />
                    폴더 이동
                  </button>
                  <button
                    onClick={() => {
                      onDelete(project.projectId, project.name);
                      setShowMenu(false);
                    }}
                    className="flex items-center w-full px-4 py-2 text-sm text-red-700 hover:bg-red-50"
                  >
                    <TrashIcon className="h-4 w-4 mr-3" />
                    삭제
                  </button>
                </div>
              </div>
            )}

            {showMoveMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                <div className="py-1">
                  <div className="px-4 py-2 text-xs font-medium text-gray-500 uppercase tracking-wide">
                    카테고리 선택
                  </div>
                  {userCategories.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => {
                        onCategoryChange(project.projectId, category.id);
                        setShowMoveMenu(false);
                      }}
                      className={`flex items-center w-full px-4 py-2 text-sm hover:bg-gray-50 ${
                        project.category === category.id
                          ? "bg-blue-50 text-blue-700 font-medium"
                          : "text-gray-700"
                      }`}
                    >
                      <div
                        className={`w-2 h-2 rounded-full bg-${category.color}-500 mr-3`}
                      ></div>
                      {category.name}
                      {project.category === category.id && (
                        <span className="ml-auto text-blue-600">✓</span>
                      )}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* 카테고리 배지와 폴더 이동 */}
        <div className="flex items-center justify-between mb-4">
          <div className="relative" ref={categoryMenuRef}>
            <button
              onClick={() => setShowCategoryMenu(!showCategoryMenu)}
              className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border cursor-pointer hover:shadow-sm transition-all ${getCategoryColorClasses(
                categoryInfo.color
              )}`}
            >
              <div
                className={`w-2 h-2 rounded-full bg-${categoryInfo.color}-500 mr-2`}
              ></div>
              {categoryInfo.name}
            </button>

            {showCategoryMenu && (
              <div className="absolute left-0 mt-2 w-48 bg-white rounded-lg shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                <div className="py-1">
                  {userCategories.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => {
                        onCategoryChange(project.projectId, category.id);
                        setShowCategoryMenu(false);
                      }}
                      className="flex items-center w-full px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                    >
                      <div
                        className={`w-2 h-2 rounded-full bg-${category.color}-500 mr-3`}
                      ></div>
                      {category.name}
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* 4. 날짜 (생성일, 수정일) - 자연스럽게 배치 */}
        <div className="flex items-center space-x-4 mb-3">
          <div className="flex items-center text-sm text-gray-500">
            <CalendarIcon className="h-4 w-4 mr-1.5" />
            <span>
              생성 {new Date(project.createdAt).toLocaleDateString("ko-KR")}
            </span>
          </div>
          {project.updatedAt && project.updatedAt !== project.createdAt && (
            <div className="flex items-center text-sm text-gray-500">
              <span className="text-gray-300">•</span>
              <span className="ml-1.5">
                수정 {new Date(project.updatedAt).toLocaleDateString("ko-KR")}
              </span>
            </div>
          )}
        </div>

        {/* 5. 프롬프트, 토큰 - 일관된 스타일 */}
        <div className="flex items-center space-x-4 mb-3">
          <div className="flex items-center text-sm text-gray-500">
            <DocumentTextIcon className="h-4 w-4 mr-1.5" />
            <span>{stats?.totalCards || 0}개 프롬프트</span>
          </div>
          <div className="flex items-center text-sm text-gray-500">
            <SparklesIcon className="h-4 w-4 mr-1.5" />
            <span>{formatTokenCount(stats?.totalTokens || 0)} 토큰</span>
          </div>
        </div>

        {/* 6. 태그 - 책갈피 아이콘 사용 */}
        {project.tags && project.tags.length > 0 && (
          <div className="mb-4">
            <div className="flex items-center">
              <BookmarkIcon className="h-4 w-4 mr-1.5 text-gray-400" />
              <div className="flex flex-wrap gap-1.5">
                {project.tags.slice(0, 3).map((tag, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center bg-gray-100 text-gray-600 px-2.5 py-1 rounded-full text-xs font-medium"
                  >
                    {tag}
                  </span>
                ))}
                {project.tags.length > 3 && (
                  <span className="inline-flex items-center text-xs text-gray-400 px-2">
                    +{project.tags.length - 3}개
                  </span>
                )}
              </div>
            </div>
          </div>
        )}

        {/* 7. 프롬프트 용량 바 - 마지막 요소 */}
        {stats && stats.totalTokens > 0 && (
          <div className="pt-2 border-t border-gray-100">
            <div className="flex items-center justify-between text-xs text-gray-500 mb-2">
              <span className="font-medium">프롬프트 용량</span>
              <span className="font-mono text-gray-600">
                {formatFileSize(stats.totalSize)}
              </span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300"
                style={{
                  width: `${Math.min((stats.totalTokens / 10000) * 100, 100)}%`,
                }}
              ></div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// 카테고리 관리 모달
const CategoryManager = ({ onClose, onSave, categories }) => {
  const [userCategories, setUserCategories] = useState(categories);
  const [editingCategory, setEditingCategory] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);

  const handleDeleteCategory = async (categoryId) => {
    if (!window.confirm("정말로 이 카테고리를 삭제하시겠습니까?")) {
      return;
    }

    try {
      // 백엔드에서 삭제 먼저 실행
      await categoryAPI.deleteCategory(categoryId);
      toast.success("카테고리가 삭제되었습니다");

      // 백엔드 삭제 성공 후 전체 카테고리 목록 다시 로드
      const data = await categoryAPI.getUserCategories();
      setUserCategories(data.categories || []);
      onSave();
    } catch (error) {
      toast.error(error.message || "카테고리 삭제에 실패했습니다");
    }
  };

  const handleUpdateCategory = async (categoryId, data) => {
    try {
      await categoryAPI.updateCategory(categoryId, data);
      toast.success("카테고리가 수정되었습니다");

      // 백엔드 수정 성공 후 전체 카테고리 목록 다시 로드
      const categoryData = await categoryAPI.getUserCategories();
      setUserCategories(categoryData.categories || []);
      setEditingCategory(null);
      onSave();
    } catch (error) {
      toast.error("카테고리 수정에 실패했습니다");
    }
  };

  const handleCreateCategory = async (data) => {
    try {
      await categoryAPI.createCategory(data);
      toast.success("카테고리가 생성되었습니다");

      // 백엔드 생성 성공 후 전체 카테고리 목록 다시 로드
      const categoryData = await categoryAPI.getUserCategories();
      setUserCategories(categoryData.categories || []);
      setShowCreateForm(false);
      onSave();
    } catch (error) {
      toast.error("카테고리 생성에 실패했습니다");
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl w-full max-w-4xl max-h-[85vh] overflow-hidden border border-gray-100">
        <div className="px-8 py-6 bg-slate-50 border-b border-slate-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-slate-100 rounded-xl flex items-center justify-center">
                <FolderIcon className="h-5 w-5 text-slate-600" />
              </div>
              <div>
                <h3 className="text-xl font-semibold text-slate-900">
                  카테고리 관리
                </h3>
                <p className="text-sm text-slate-500">
                  프로젝트 분류를 커스터마이즈하세요
                </p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="p-2 text-slate-400 hover:text-slate-600 rounded-xl hover:bg-slate-100 transition-colors"
            >
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>
        </div>

        <div className="p-8 pb-12 overflow-y-auto max-h-[calc(80vh-120px)]">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h4 className="font-semibold text-slate-900">
                사용자 정의 카테고리
              </h4>
              <p className="text-sm text-slate-500 mt-1">
                프로젝트를 효율적으로 분류하고 관리하세요
              </p>
            </div>
            <button
              onClick={() => setShowCreateForm(true)}
              className="flex items-center px-4 py-2.5 bg-slate-900 text-white rounded-xl hover:bg-slate-800 transition-all duration-200 shadow-sm font-medium"
            >
              <PlusIcon className="h-4 w-4 mr-2" />새 카테고리
            </button>
          </div>

          {/* 새 카테고리 생성 폼 */}
          {showCreateForm && (
            <div className="mb-8 p-6 border border-slate-200 rounded-2xl bg-slate-50">
              <div className="flex items-center mb-4">
                <div className="w-8 h-8 bg-emerald-100 rounded-lg flex items-center justify-center mr-3">
                  <PlusIcon className="h-4 w-4 text-emerald-600" />
                </div>
                <h5 className="font-semibold text-slate-900">
                  새 카테고리 생성
                </h5>
              </div>
              <CategoryForm
                onSubmit={handleCreateCategory}
                onCancel={() => setShowCreateForm(false)}
              />
            </div>
          )}

          {/* 카테고리 목록 - 그리드 레이아웃 */}
          <div className="grid gap-3 sm:grid-cols-2 lg:grid-cols-3">
            {userCategories.map((category) => (
              <div key={category.id} className="group">
                {editingCategory?.id === category.id ? (
                  // 편집 모드 - 전체 너비
                  <div className="col-span-full p-6 border border-slate-300 rounded-2xl bg-white">
                    <div className="flex items-center mb-4">
                      <div className="w-8 h-8 bg-slate-100 rounded-lg flex items-center justify-center mr-3">
                        <PencilIcon className="h-4 w-4 text-slate-600" />
                      </div>
                      <h5 className="font-semibold text-slate-900">
                        카테고리 편집
                      </h5>
                    </div>
                    <CategoryForm
                      category={editingCategory}
                      onSubmit={(data) =>
                        handleUpdateCategory(editingCategory.id, data)
                      }
                      onCancel={() => setEditingCategory(null)}
                    />
                  </div>
                ) : (
                  // 일반 표시 모드 - 카드 스타일
                  <div className="p-4 border border-slate-200 rounded-xl hover:border-slate-300 transition-all duration-200 bg-white hover:shadow-md group">
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-4 flex-1">
                        <div
                          className={`w-4 h-4 rounded-full flex-shrink-0 mt-1 ${
                            COLOR_OPTIONS.find((c) => c.id === category.color)
                              ?.class || "bg-slate-500"
                          }`}
                        ></div>
                        <div className="flex-1 min-w-0">
                          <h5 className="font-semibold text-slate-900 truncate">
                            {category.name}
                          </h5>
                          <p className="text-sm text-slate-500 mt-1 line-clamp-2">
                            {category.description || "설명이 없습니다"}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-1 ml-3 opacity-60 group-hover:opacity-100 transition-opacity">
                        <button
                          onClick={() => setEditingCategory(category)}
                          className="p-1.5 text-slate-400 hover:text-slate-600 hover:bg-slate-100 rounded-lg transition-colors"
                          title="편집"
                        >
                          <PencilIcon className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => handleDeleteCategory(category.id)}
                          className="p-1.5 text-slate-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                          title="삭제"
                        >
                          <TrashIcon className="h-4 w-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>

          {userCategories.length === 0 && (
            <div className="text-center py-12">
              <div className="w-16 h-16 bg-slate-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                <FolderIcon className="h-8 w-8 text-slate-400" />
              </div>
              <h4 className="text-lg font-medium text-slate-900 mb-2">
                카테고리가 없습니다
              </h4>
              <p className="text-slate-500 mb-6">
                새로운 카테고리를 만들어 프로젝트를 체계적으로 관리해보세요
              </p>
              <button
                onClick={() => setShowCreateForm(true)}
                className="inline-flex items-center px-4 py-2 bg-slate-900 text-white rounded-xl hover:bg-slate-800 transition-colors font-medium"
              >
                <PlusIcon className="h-4 w-4 mr-2" />첫 번째 카테고리 만들기
              </button>
            </div>
          )}
        </div>

        {/* 모던한 푸터 */}
        <div className="px-8 py-6 mt-8 bg-slate-50 border-t border-slate-200 flex justify-end">
          <button
            onClick={onClose}
            className="px-6 py-2.5 text-slate-700 bg-white border border-slate-300 rounded-xl hover:bg-slate-50 transition-colors font-medium"
          >
            완료
          </button>
        </div>
      </div>
    </div>
  );
};

// 카테고리 폼 컴포넌트
const CategoryForm = ({ category, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    name: category?.name || "",
    description: category?.description || "",
    color: category?.color || "slate",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      toast.error("카테고리 이름을 입력해주세요");
      return;
    }
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-2">
        <label className="block text-sm font-semibold text-slate-800">
          카테고리 이름 <span className="text-red-500">*</span>
        </label>
        <input
          type="text"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          className="w-full px-4 py-3 border-2 border-slate-200 rounded-xl focus:outline-none focus:border-slate-500 focus:ring-4 focus:ring-slate-100 transition-all duration-200 text-slate-800 placeholder-slate-400"
          placeholder="예: 경제 뉴스, 기술 리뷰 등"
          required
        />
      </div>

      <div className="space-y-2">
        <label className="block text-sm font-semibold text-slate-800">
          설명
        </label>
        <textarea
          value={formData.description}
          onChange={(e) =>
            setFormData({ ...formData, description: e.target.value })
          }
          rows={3}
          className="w-full px-4 py-3 border-2 border-slate-200 rounded-xl focus:outline-none focus:border-slate-500 focus:ring-4 focus:ring-slate-100 transition-all duration-200 text-slate-800 placeholder-slate-400 resize-none"
          placeholder="이 카테고리에 대한 간단한 설명을 입력하세요"
        />
      </div>

      <div className="space-y-3">
        <label className="block text-sm font-semibold text-slate-800">
          대표 색상
        </label>
        <div className="flex flex-wrap gap-2">
          {COLOR_OPTIONS.map((color) => (
            <button
              key={color.id}
              type="button"
              onClick={() => setFormData({ ...formData, color: color.id })}
              className={`relative w-10 h-10 rounded-lg ${
                color.class
              } transition-all duration-200 shadow-sm hover:shadow-md ${
                formData.color === color.id
                  ? "ring-2 ring-offset-2 ring-slate-400 shadow-lg scale-110"
                  : "hover:scale-105"
              }`}
              title={color.name}
            >
              {formData.color === color.id && (
                <div className="absolute inset-0 flex items-center justify-center">
                  <CheckIcon className="h-4 w-4 text-white drop-shadow-sm" />
                </div>
              )}
            </button>
          ))}
        </div>
        <div className="flex items-center gap-2 text-xs text-slate-500">
          <span>선택한 색상:</span>
          <span className="font-medium text-slate-700">
            {COLOR_OPTIONS.find((c) => c.id === formData.color)?.name}
          </span>
        </div>
      </div>

      <div className="flex justify-end gap-3 pt-4 border-t border-slate-200">
        <button
          type="button"
          onClick={onCancel}
          className="px-4 py-2 text-slate-700 bg-white border border-slate-300 rounded-lg hover:bg-slate-50 transition-colors font-medium"
        >
          취소
        </button>
        <button
          type="submit"
          className="px-4 py-2 bg-slate-900 text-white rounded-lg hover:bg-slate-800 transition-colors font-medium shadow-sm"
        >
          {category ? "수정" : "생성"}
        </button>
      </div>
    </form>
  );
};

// 프로젝트 편집 모달 컴포넌트
const ProjectEditModal = ({
  project,
  userCategories,
  isOpen,
  onSave,
  onCancel,
}) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    category: "",
    tags: [],
  });
  const [tagInput, setTagInput] = useState("");
  const [categoryDropdownOpen, setCategoryDropdownOpen] = useState(false);
  const categoryDropdownRef = useRef(null);

  useEffect(() => {
    if (project) {
      setFormData({
        name: project.name || "",
        description: project.description || "",
        category: project.category || userCategories[0]?.id || "",
        tags: Array.isArray(project.tags) ? [...project.tags] : [],
      });
    }
  }, [project, userCategories]);

  // 드롭다운 외부 클릭 감지
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        categoryDropdownRef.current &&
        !categoryDropdownRef.current.contains(event.target)
      ) {
        setCategoryDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      toast.error("프로젝트 이름을 입력해주세요");
      return;
    }
    if (!formData.category) {
      toast.error("카테고리를 선택해주세요");
      return;
    }
    onSave(formData);
  };

  const handleCategorySelect = (categoryId) => {
    setFormData({ ...formData, category: categoryId });
    setCategoryDropdownOpen(false);
  };

  const handleModalClose = () => {
    setCategoryDropdownOpen(false);
    setTagInput("");
    onCancel();
  };

  // 태그 추가 함수
  const addTag = () => {
    const tag = tagInput.trim();
    if (tag && !formData.tags.includes(tag) && formData.tags.length < 10) {
      setFormData({
        ...formData,
        tags: [...formData.tags, tag],
      });
      setTagInput("");
    }
  };

  // 태그 제거 함수
  const removeTag = (tagToRemove) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter((tag) => tag !== tagToRemove),
    });
  };

  // Enter 키로 태그 추가
  const handleTagKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addTag();
    }
  };

  // 현재 선택된 카테고리 정보
  const currentCategory = userCategories.find(
    (category) => category.id === formData.category
  );

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-md w-full">
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">프로젝트 편집</h3>
          <button
            onClick={handleModalClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <XMarkIcon className="h-5 w-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-4 space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              프로젝트 이름 *
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프로젝트 이름을 입력하세요"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              설명
            </label>
            <textarea
              value={formData.description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프로젝트 설명을 입력하세요"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              카테고리 *
            </label>
            {userCategories.length === 0 ? (
              <div className="text-center py-4 border-2 border-dashed border-gray-300 rounded-lg">
                <p className="text-gray-500 text-sm">
                  사용할 수 있는 카테고리가 없습니다.
                </p>
                <p className="text-xs text-gray-400 mt-1">
                  카테고리를 먼저 생성해주세요.
                </p>
              </div>
            ) : (
              <div className="relative" ref={categoryDropdownRef}>
                <button
                  type="button"
                  onClick={() => setCategoryDropdownOpen(!categoryDropdownOpen)}
                  className="w-full flex items-center justify-between px-3 py-2 bg-white border border-gray-300 rounded-lg hover:border-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200"
                  disabled={userCategories.length === 0}
                >
                  <div className="flex items-center space-x-2">
                    {currentCategory && (
                      <div
                        className={`w-3 h-3 rounded-full bg-${currentCategory.color}-500`}
                      ></div>
                    )}
                    <span className="text-gray-700 font-medium">
                      {currentCategory?.name || "카테고리 선택"}
                    </span>
                  </div>
                  <ChevronDownIcon
                    className={`h-4 w-4 text-gray-400 transition-transform duration-200 ${
                      categoryDropdownOpen ? "rotate-180" : ""
                    }`}
                  />
                </button>

                {/* 드롭다운 메뉴 */}
                {categoryDropdownOpen && (
                  <div className="absolute top-full left-0 mt-1 w-full bg-white border border-gray-200 rounded-lg shadow-lg z-50 overflow-hidden">
                    {userCategories.map((category) => (
                      <button
                        key={category.id}
                        type="button"
                        onClick={() => handleCategorySelect(category.id)}
                        className={`w-full flex items-center justify-between px-3 py-2 text-left hover:bg-gray-50 transition-colors duration-150 ${
                          formData.category === category.id
                            ? "bg-blue-50 text-blue-600"
                            : "text-gray-700"
                        }`}
                      >
                        <div className="flex items-center space-x-2">
                          <div
                            className={`w-3 h-3 rounded-full bg-${category.color}-500`}
                          ></div>
                          <span className="font-medium">{category.name}</span>
                        </div>
                        {formData.category === category.id && (
                          <CheckIcon className="h-4 w-4 text-blue-600" />
                        )}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>

          {/* 태그 관리 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              태그 ({formData.tags.length}/10)
            </label>

            {/* 현재 태그들 */}
            {formData.tags.length > 0 && (
              <div className="flex flex-wrap gap-2 mb-3">
                {formData.tags.map((tag, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-800"
                  >
                    {tag}
                    <button
                      type="button"
                      onClick={() => removeTag(tag)}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >
                      <XMarkIcon className="h-3 w-3" />
                    </button>
                  </span>
                ))}
              </div>
            )}

            {/* 태그 입력 */}
            <div className="flex gap-2">
              <input
                type="text"
                value={tagInput}
                onChange={(e) => setTagInput(e.target.value)}
                onKeyPress={handleTagKeyPress}
                className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="태그를 입력하고 Enter를 누르세요"
                maxLength={20}
                disabled={formData.tags.length >= 10}
              />
              <button
                type="button"
                onClick={addTag}
                disabled={!tagInput.trim() || formData.tags.length >= 10}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                추가
              </button>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              태그는 최대 10개까지 추가할 수 있습니다. 각 태그는 20자 이내로
              입력하세요.
            </p>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={handleModalClose}
              className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              취소
            </button>
            <button
              type="submit"
              disabled={
                !formData.name.trim() ||
                !formData.category ||
                userCategories.length === 0
              }
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              저장
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectList;
