import React, { useState, useEffect } from "react";
import PromptCardManager from "./PromptCardManager";
import ChatWindow from "./chat/ChatWindow";
import { promptCardAPI } from "../services/api";

const AdminView = ({ projectId, projectName }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showChat, setShowChat] = useState(false);

  useEffect(() => {
    const loadPromptCards = async () => {
      try {
        setLoading(true);
        const response = await promptCardAPI.getPromptCards(projectId, true);
        setPromptCards(response.promptCards || []);
      } catch (error) {
        console.warn("프롬프트 카드 로드 실패:", error);
        setPromptCards([]);
      } finally {
        setLoading(false);
      }
    };

    loadPromptCards();
  }, [projectId]);

  if (loading) {
    return (
      <div className="h-full w-full bg-white dark:bg-dark-primary transition-colors duration-300 flex items-center justify-center">
        <div className="text-gray-500 dark:text-gray-400">로딩 중...</div>
      </div>
    );
  }

  return (
    <div className="h-full w-full bg-white dark:bg-dark-primary transition-colors duration-300">
      {/* 상단 탭 버튼 */}
      <div className="flex border-b border-gray-200 dark:border-gray-700 bg-white dark:bg-dark-secondary">
        <button
          onClick={() => setShowChat(false)}
          className={`px-6 py-3 text-sm font-medium border-b-2 transition-colors ${
            !showChat
              ? "border-blue-500 text-blue-600 dark:text-blue-400"
              : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
          }`}
        >
          프롬프트 관리
        </button>
        <button
          onClick={() => setShowChat(true)}
          className={`px-6 py-3 text-sm font-medium border-b-2 transition-colors ${
            showChat
              ? "border-blue-500 text-blue-600 dark:text-blue-400"
              : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
          }`}
        >
          채팅 모니터링
        </button>
      </div>

      {/* 콘텐츠 영역 */}
      <div className="h-[calc(100%-53px)]">
        {showChat ? (
          <ChatWindow
            projectId={projectId}
            projectName={projectName}
            promptCards={promptCards}
            isAdminMode={false}
          />
        ) : (
          <PromptCardManager projectId={projectId} projectName={projectName} />
        )}
      </div>
    </div>
  );
};

export default AdminView;
