import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  Bars3Icon,
  // ChatBubbleLeftRightIcon 제거
} from "@heroicons/react/24/outline";
import { promptCardAPI, handleAPIError } from "../services/api";
import PromptEditModal from "./prompts/PromptEditModal";
import ChatInterface from "./ChatInterface";
import PromptCard from "./prompts/PromptCard";
import PromptEditForm from "./prompts/PromptEditForm";
import { SidebarSkeleton } from "./SkeletonLoader";
import { useChat } from "../hooks/useChat";

// AI 워크플로우 기반 프롬프트 템플릿
const FIXED_PROMPT_CATEGORIES = [
  {
    id: "instruction",
    name: "역할 및 목표",
    order: 1,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "AI의 역할, 정체성, 핵심 목표를 명확히 정의합니다",
    defaultContent:
      "당신은 전문적인 기사 제목 생성 AI입니다.\n목표: 독자의 관심을 끌고 정확한 정보를 전달하는 제목 생성",
    placeholder: "AI의 역할과 주요 목표를 구체적으로 작성하세요...",
  },
  {
    id: "knowledge",
    name: "지식 베이스",
    order: 2,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "작업 수행에 필요한 핵심 지식과 원칙을 제공합니다",
    defaultContent:
      "제목 작성 원칙:\n- 간결하고 명확한 표현\n- 핵심 키워드 포함\n- 독자의 관심 유발",
    placeholder: "작업에 필요한 핵심 지식과 원칙을 작성하세요...",
  },
  {
    id: "secondary",
    name: "CoT (사고 과정)",
    order: 3,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "단계별 추론 과정을 통해 사고의 투명성을 확보합니다",
    defaultContent:
      "다음 단계로 사고하세요:\n1. 기사의 핵심 내용 파악\n2. 주요 키워드 추출\n3. 독자 관점에서 흥미도 평가\n4. 제목 후보 생성\n5. 최적 제목 선택",
    placeholder: "단계별 사고 과정을 구체적으로 작성하세요...",
    isCoT: true,
  },
  {
    id: "style_guide",
    name: "스타일 가이드",
    order: 4,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "일관된 스타일과 형식을 유지하기 위한 가이드라인",
    defaultContent:
      "스타일 요구사항:\n- 길이: 15-25자 권장\n- 톤: 전문적이면서 친근\n- 형식: 명사형 종결",
    placeholder: "스타일과 형식 요구사항을 작성하세요...",
  },
  {
    id: "validation",
    name: "ReAct (추론+행동)",
    order: 5,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "추론과 행동을 결합하여 더 정확한 결과를 도출합니다",
    defaultContent:
      "Thought: 이 기사의 핵심은 무엇인가?\nAction: 키워드를 추출하고 중요도를 평가한다\nObservation: 추출된 정보를 바탕으로 제목을 구성한다\nThought: 생성된 제목이 요구사항을 충족하는가?\nAction: 필요시 수정하고 최종 검증한다",
    placeholder: "Thought/Action/Observation 패턴으로 작성하세요...",
    isReAct: true,
  },
  {
    id: "enhancement",
    name: "품질 검증",
    order: 6,
    color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
    icon: "",
    description: "최종 결과물의 품질을 검증하고 개선합니다",
    defaultContent:
      "품질 검증 기준:\n- 정확성: 기사 내용과 일치\n- 매력도: 독자 관심 유발\n- 적절성: 매체 성격에 부합\n- 완성도: 문법과 표현의 정확성",
    placeholder: "품질 검증 기준과 개선 방안을 작성하세요...",
  },
];

// 프롬프트 편집 모달 (이름 변경)
const PromptFormModal = ({ isOpen, onSubmit, onCancel, initialData }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg w-full max-w-lg max-h-[80vh] overflow-y-auto">
        <PromptEditForm
          onSubmit={onSubmit}
          onCancel={onCancel}
          initialData={initialData}
          isModal={true}
        />
      </div>
    </div>
  );
};

const PromptCardManager = ({ projectId, onCardsChanged, projectName }) => {
  // 상태 관리
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingCard, setEditingCard] = useState(null);
  // savedChats, showSavedChats 상태 제거

  // 채팅 기능 (재생성용)
  const { regenerateLastResult, hasLastProcessedData } = useChat(projectId, projectName);

  // 프롬프트 카드 로드 및 기본 템플릿 생성
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      // include_content=true로 S3에서 실제 프롬프트 내용 로드
      const response = await promptCardAPI.getPromptCards(projectId, true, false);

      // API 응답 구조 확인 및 안전한 처리
      let cards = [];
      if (Array.isArray(response)) {
        cards = response;
      } else if (response && Array.isArray(response.promptCards)) {
        cards = response.promptCards;
      } else if (response && Array.isArray(response.data)) {
        cards = response.data;
      } else {
        console.warn("Unexpected API response structure:", response);
        cards = [];
      }

      // 고정된 템플릿 카드들과 기존 카드들을 매칭
      const templateCards = FIXED_PROMPT_CATEGORIES.map((template) => {
        const existingCard = cards.find(
          (card) => card.category === template.id
        );

        if (existingCard) {
          console.log(`🔍 기존 카드 발견 (${template.name}):`, {
            promptId: existingCard.promptId,
            category: existingCard.category,
            prompt_text_length: existingCard.prompt_text?.length || 0,
            prompt_text_preview: existingCard.prompt_text?.substring(0, 50) + "...",
            s3Key: existingCard.s3Key
          });

          // 기존 카드가 있으면 해당 카드 사용 (isTemplate 플래그 제거)
          return {
            ...existingCard,
            // 템플릿의 UI 정보만 사용 (텍스트 내용은 기존 카드 우선)
            name: template.name,
            order: template.order,
            color: template.color,
            icon: template.icon,
            description: template.description,
            placeholder: template.placeholder,
            title: existingCard.title || `${template.name} 프롬프트`,
            prompt_text: existingCard.prompt_text || template.defaultContent, // S3에서 로드된 텍스트 우선
            isTemplate: false, // 실제 카드는 템플릿이 아님
          };
        } else {
          // 기존 카드가 없으면 템플릿으로 기본 카드 생성
          return {
            id: `template-${template.id}`,
            promptId: `template-${template.id}`,
            title: `${template.name} 프롬프트`,
            category: template.id,
            prompt_text: template.defaultContent,
            model_name: "claude-3-5-sonnet-20241022",
            temperature: 0.7,
            enabled: true,
            step_order: template.order,
            isTemplate: true, // 템플릿 카드임을 표시
            ...template,
          };
        }
      });

      // 순서대로 정렬
      templateCards.sort((a, b) => (a.order || 999) - (b.order || 999));

      setPromptCards(templateCards);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);

      // 오류 시에도 기본 템플릿 카드들 표시
      const defaultCards = FIXED_PROMPT_CATEGORIES.map((template) => ({
        id: `template-${template.id}`,
        promptId: `template-${template.id}`,
        title: `${template.name} 프롬프트`,
        category: template.id,
        prompt_text: template.defaultContent,
        model_name: "claude-3-5-sonnet-20241022",
        temperature: 0.7,
        enabled: true,
        step_order: template.order,
        isTemplate: true,
        ...template,
      }));

      setPromptCards(defaultCards);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
    // 저장된 대화 로드 코드 제거
  }, [loadPromptCards, projectId]);

  // 카드 업데이트
  const handleUpdateCard = async (cardData) => {
    try {
      console.log("📝 프롬프트 카드 업데이트 시작:", { editingCard, cardData });

      if (editingCard && editingCard.promptId && !editingCard.promptId.startsWith('template-')) {
        // 실제 기존 카드 편집
        const updateData = {
          ...cardData,
          category: editingCard.category,
          title: editingCard.title,
          step_order: editingCard.step_order || editingCard.order,
        };

        console.log("🔄 기존 카드 수정:", {
          projectId,
          promptId: editingCard.promptId,
          updateData
        });

        await promptCardAPI.updatePromptCard(
          projectId,
          editingCard.promptId,
          updateData
        );
        toast.success("프롬프트 카드가 수정되었습니다!");
      } else if (editingCard && (editingCard.isTemplate || editingCard.promptId?.startsWith('template-'))) {
        // 템플릿 카드를 실제 카드로 생성
        const newCardData = {
          ...cardData,
          category: editingCard.category,
          title: editingCard.title,
          step_order: editingCard.order,
        };

        console.log("✨ 템플릿에서 새 카드 생성:", { projectId, newCardData });

        await promptCardAPI.createPromptCard(projectId, newCardData);
        toast.success("프롬프트 카드가 생성되었습니다!");
      } else {
        // 새 카드 생성
        const newCardData = {
          ...cardData,
          category: "instruction", // 기본 카테고리
          title: "새 프롬프트 카드",
          step_order: 1,
        };

        console.log("🆕 완전히 새로운 카드 생성:", { projectId, newCardData });

        await promptCardAPI.createPromptCard(projectId, newCardData);
        toast.success("프롬프트 카드가 생성되었습니다!");
      }

      await loadPromptCards();
      setShowForm(false);
      setEditingCard(null);

      if (onCardsChanged) onCardsChanged();
      if (onPromptUpdated) {
        // 프롬프트 업데이트 후 재생성 트리거
        setTimeout(() => onPromptUpdated(), 500); // 약간의 지연 후 실행
      }
    } catch (error) {
      console.error("❌ 프롬프트 카드 업데이트 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(
        `카드 ${editingCard ? "수정" : "생성"} 실패: ${errorInfo.message}`
      );
    }
  };

  // 카드 토글 (최적화 UI)
  const handleToggleCard = async (promptId, enabled) => {
    // promptCards가 배열인지 확인
    if (!Array.isArray(promptCards)) {
      console.error("promptCards is not an array:", promptCards);
      return;
    }

    // 낙관적 업데이트: 즉시 UI 반영
    const originalCards = [...promptCards];
    const updatedCards = promptCards.map((card) =>
      (card.promptId || card.id) === promptId ? { ...card, enabled } : card
    );
    setPromptCards(updatedCards);

    try {
      // 템플릿 카드가 아닌 경우에만 API 호출
      const card = promptCards.find((c) => (c.promptId || c.id) === promptId);
      if (card && !card.isTemplate) {
        await promptCardAPI.updatePromptCard(projectId, promptId, { enabled });
      }
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      // 실패 시 복원
      setPromptCards(originalCards);
      const errorInfo = handleAPIError(error);
      toast.error(`카드 상태 변경 실패: ${errorInfo.message}`);
    }
  };

  // 카드 삭제 또는 초기화
  const handleDeleteCard = async (promptId) => {
    const card = promptCards.find((c) => (c.promptId || c.id) === promptId);

    if (card && card.isTemplate) {
      if (!window.confirm("이 프롬프트를 기본값으로 초기화하시겠습니까?")) {
        return;
      }
      // 템플릿 카드는 기본값으로 초기화
      const template = FIXED_PROMPT_CATEGORIES.find(
        (t) => t.id === card.category
      );
      if (template) {
        const updatedCards = promptCards.map((c) =>
          (c.promptId || c.id) === promptId
            ? { ...c, prompt_text: template.defaultContent }
            : c
        );
        setPromptCards(updatedCards);
        toast.success("프롬프트가 기본값으로 초기화되었습니다!");
      }
      return;
    }

    if (!window.confirm("정말로 이 프롬프트 카드를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      await loadPromptCards();
      toast.success("프롬프트 카드가 삭제되었습니다!");
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  // deleteSavedChat 함수 제거

  // promptCards가 배열인지 확인하고 안전하게 처리
  const safePromptCards = Array.isArray(promptCards) ? promptCards : [];

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 메인 콘텐츠 - 채팅 인터페이스 */}
      <div className="flex-1 min-w-0 relative p-3 lg:pr-2">
        {/* 우측 상단 메뉴 버튼 (모바일용) */}
        <div className="absolute top-6 right-6 z-10 lg:hidden">
          <button className="p-3 text-slate-500 hover:text-slate-700 hover:bg-white/80 hover:shadow-lg rounded-xl backdrop-blur-sm transition-all duration-300">
            <Bars3Icon className="h-5 w-5" />
          </button>
        </div>

        <div className="h-full bg-white rounded-lg border border-gray-200 overflow-hidden">
          <ChatInterface
            projectId={projectId}
            projectName={projectName}
            promptCards={safePromptCards}
          />
        </div>
      </div>

      {/* 우측 사이드바 */}
      <div className="w-[350px] flex flex-col hidden lg:flex p-3 pl-2">
        <div className="h-full bg-white rounded-lg border border-gray-200 flex flex-col overflow-hidden">
          {/* 사이드바 헤더 */}
          <div className="flex-shrink-0 p-3 border-b border-gray-200">
            <div className="flex items-center justify-between mb-2">
              <div className="flex items-center">
                <h3 className="text-sm font-medium text-blue-600">
                  프롬프트 카드
                </h3>
              </div>
              <div className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded-full">
                {safePromptCards.filter((card) => card.enabled).length}/
                {safePromptCards.length}
              </div>
            </div>
            <p className="text-xs text-gray-600">
              각 카테고리별 프롬프트를 설정하세요
            </p>
          </div>

          {/* 콘텐츠 영역 */}
          <div className="flex-1 overflow-y-auto p-3">
            {/* 워크플로우 헤더 */}
            <div className="mb-4 p-3 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg border border-blue-100">
              <h4 className="text-sm font-semibold text-blue-800 mb-1">AI 제목 생성 워크플로우</h4>
              <p className="text-xs text-blue-600">각 단계가 순차적으로 실행되어 최적의 제목을 생성합니다</p>
              <div className="mt-2 flex items-center text-xs text-blue-500">
                <span className="inline-block w-2 h-2 bg-green-400 rounded-full mr-1"></span>
                활성화된 단계: {safePromptCards.filter((card) => card.enabled).length}개
              </div>
            </div>

            {/* 프롬프트 카드 목록 */}
            <div className="space-y-3">
              {loading ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="text-sm text-gray-500 mt-2">로딩 중...</p>
                </div>
              ) : safePromptCards.length === 0 ? (
                <div className="text-center py-8">
                  <ExclamationTriangleIcon className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-sm text-gray-500">
                    프롬프트 카드가 없습니다
                  </p>
                  <p className="text-xs text-gray-400 mt-1">
                    기본 템플릿을 생성하거나 새 카드를 추가해주세요
                  </p>
                  <button
                    onClick={() =>
                      handleUpdateCard({
                        // Changed from handleCreateCard to handleUpdateCard
                        category: "instruction",
                        title: "새 프롬프트 카드",
                        step_order: 1,
                      })
                    }
                    className="mt-3 px-3 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700 transition-colors"
                  >
                    첫 번째 카드 만들기
                  </button>
                </div>
              ) : (
                safePromptCards.map((card, index) => (
                  <div key={card.id || card.promptId} className="relative">
                    {/* 단계 연결선 (마지막 카드가 아닌 경우) */}
                    {index < safePromptCards.length - 1 && (
                      <div className="absolute left-6 top-full w-0.5 h-3 bg-gradient-to-b from-blue-200 to-blue-100 z-0"></div>
                    )}

                    <div
                      className={`relative bg-white border rounded-lg p-3 hover:border-gray-300 transition-all duration-200 hover:shadow-sm ${
                        card.enabled 
                          ? "border-green-200 shadow-sm" 
                          : "border-gray-200 opacity-75"
                      }`}
                    >
                      <div className="flex items-start gap-3">
                        {/* 단계 번호 원형 배지 */}
                        <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center text-xs font-semibold ${
                          card.enabled 
                            ? "bg-blue-100 text-blue-700 border-2 border-blue-200" 
                            : "bg-gray-100 text-gray-500 border-2 border-gray-200"
                        }`}>
                          {card.step_order || card.stepOrder || index + 1}
                        </div>

                        <div className="flex-1 min-w-0">
                          <div className="flex items-center gap-2 mb-1">
                            <h4 className="text-sm font-medium text-gray-900 truncate">
                              {FIXED_PROMPT_CATEGORIES.find(
                                (cat) => cat.id === card.category
                              )?.name || card.category}
                            </h4>
                            {card.enabled ? (
                              <CheckCircleIcon className="h-4 w-4 text-green-500 flex-shrink-0" />
                            ) : (
                              <div className="h-4 w-4 rounded-full border border-gray-300 flex-shrink-0" />
                            )}
                          </div>
                          <p className="text-xs text-gray-500 mb-2">
                            {FIXED_PROMPT_CATEGORIES.find(
                              (cat) => cat.id === card.category
                            )?.description || card.category}
                          </p>

                          {/* 프롬프트 미리보기 */}
                          <div className="text-xs text-gray-400 bg-gray-50 rounded p-2 mb-2 max-h-12 overflow-hidden">
                            {card.prompt_text?.substring(0, 60) || "프롬프트 내용 없음"}
                            {card.prompt_text?.length > 60 && "..."}
                          </div>

                          {/* 단계별 특성 태그 */}
                          <div className="flex gap-1 mb-1">
                            {card.isCoT && (
                              <span className="inline-block px-2 py-0.5 text-xs bg-purple-100 text-purple-600 rounded">
                                CoT
                              </span>
                            )}
                            {card.isReAct && (
                              <span className="inline-block px-2 py-0.5 text-xs bg-orange-100 text-orange-600 rounded">
                                ReAct
                              </span>
                            )}
                            {card.isTemplate && (
                              <span className="inline-block px-2 py-0.5 text-xs bg-yellow-100 text-yellow-600 rounded">
                                템플릿
                              </span>
                            )}
                          </div>
                        </div>

                        <div className="flex flex-col items-center gap-1 ml-2">
                          <button
                            onClick={() => handleEditCard(card)}
                            className="p-1.5 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
                            title="편집"
                          >
                            <PencilIcon className="h-4 w-4" />
                          </button>
                          {!card.isTemplate && (
                            <button
                              onClick={() =>
                                handleDeleteCard(card.promptId || card.id)
                              }
                              className="p-1.5 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded transition-colors"
                              title="삭제"
                            >
                              <TrashIcon className="h-4 w-4" />
                            </button>
                          )}

                          {/* 활성화/비활성화 토글 */}
                          <button
                            onClick={() => 
                              handleToggleCard(card.promptId || card.id, !card.enabled)
                            }
                            className={`p-1.5 rounded transition-colors ${
                              card.enabled 
                                ? "text-green-600 hover:bg-green-50" 
                                : "text-gray-400 hover:bg-gray-50"
                            }`}
                            title={card.enabled ? "비활성화" : "활성화"}
                          >
                            <CheckCircleIcon className="h-4 w-4" />
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* 하단 정보 */}
          <div className="flex-shrink-0 p-3 border-t border-gray-200 bg-gradient-to-r from-gray-50 to-blue-50">
            <div className="text-xs text-gray-600">
              <p className="font-medium mb-2 text-blue-800">🔄 단계별 오케스트레이션 시스템</p>
              <div className="space-y-1">
                <p className="flex items-center">
                  <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mr-2"></span>
                  각 단계가 순차적으로 실행
                </p>
                <p className="flex items-center">
                  <span className="inline-block w-2 h-2 bg-green-400 rounded-full mr-2"></span>
                  사용자 요청도 함께 처리
                </p>
                <p className="flex items-center">
                  <span className="inline-block w-2 h-2 bg-purple-400 rounded-full mr-2"></span>
                  실시간 편집 및 즉시 반영
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* 편집 모달 */}
      <PromptFormModal
        isOpen={showForm}
        onSubmit={handleUpdateCard}
        onCancel={handleCancelForm}
        initialData={editingCard}
      />
    </div>
  );
};

export default PromptCardManager;
