import React, { useState, useEffect, useRef } from "react";
import { XMarkIcon, CheckIcon } from "@heroicons/react/24/outline";
import { promptCardAPI } from "../../services/api";
import { toast } from "react-hot-toast";

const PromptEditModal = ({ 
  isOpen, 
  onClose, 
  card, 
  projectId,
  onSave 
}) => {
  const [content, setContent] = useState("");
  const [originalContent, setOriginalContent] = useState("");
  const [isSaving, setIsSaving] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const textareaRef = useRef(null);

  // 모달이 열릴 때 프롬프트 내용 로드
  useEffect(() => {
    if (isOpen && card && projectId) {
      loadPromptContent();
    }
  }, [isOpen, card, projectId]);

  // 텍스트 영역 자동 크기 조절
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [content]);

  const loadPromptContent = async () => {
    setIsLoading(true);
    try {
      const response = await promptCardAPI.getPromptContent(projectId, card.promptId || card.id);
      const promptContent = response.content || "";
      setContent(promptContent);
      setOriginalContent(promptContent);
    } catch (error) {
      console.error("프롬프트 내용 로드 실패:", error);
      toast.error("프롬프트 내용을 불러올 수 없습니다");
      setContent("");
      setOriginalContent("");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    if (content === originalContent) {
      onClose();
      return;
    }

    setIsSaving(true);
    try {
      await promptCardAPI.updatePromptCard(projectId, card.promptId || card.id, {
        promptText: content
      });

      toast.success("프롬프트가 저장되었습니다");

      // 부모 컴포넌트에 저장 완료 알림
      if (onSave) {
        onSave(card.promptId || card.id, content);
      }

      onClose();
    } catch (error) {
      console.error("프롬프트 저장 실패:", error);
      toast.error("프롬프트 저장에 실패했습니다");
    } finally {
      setIsSaving(false);
    }
  };

  const handleCancel = () => {
    if (content !== originalContent) {
      if (window.confirm("변경사항이 저장되지 않았습니다. 정말 닫으시겠습니까?")) {
        setContent(originalContent);
        onClose();
      }
    } else {
      onClose();
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      handleCancel();
    } else if (e.key === 'Enter' && (e.ctrlKey || e.metaKey)) {
      handleSave();
    }
  };

  const getCategoryInfo = (categoryId) => {
    const categories = {
      instruction: { name: "역할 및 목표", color: "text-blue-600" },
      knowledge: { name: "지식 베이스", color: "text-green-600" },
      secondary: { name: "CoT (사고 과정)", color: "text-purple-600" },
      style_guide: { name: "스타일 가이드", color: "text-orange-600" },
      validation: { name: "ReAct (추론+행동)", color: "text-red-600" },
      enhancement: { name: "품질 검증", color: "text-indigo-600" },
    };
    return categories[categoryId] || { name: categoryId, color: "text-gray-600" };
  };

  if (!isOpen) return null;

  const categoryInfo = getCategoryInfo(card?.category);

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      {/* 배경 오버레이 */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 transition-opacity"
        onClick={handleCancel}
      ></div>

      {/* 모달 컨테이너 */}
      <div className="flex min-h-full items-center justify-center p-4">
        <div className="relative w-full max-w-2xl bg-white rounded-lg shadow-xl transform transition-all">
          {/* 모달 헤더 */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200">
            <div>
              <h2 className="text-lg font-semibold text-gray-900">프롬프트 편집</h2>
              <p className={`text-sm ${categoryInfo.color} font-medium mt-1`}>
                {categoryInfo.name}
              </p>
            </div>
            <button
              onClick={handleCancel}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
              title="닫기"
            >
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>

          {/* 모달 본문 */}
          <div className="p-6">
            {/* 카드 정보 */}
            <div className="mb-4">
              <h3 className="text-sm font-medium text-gray-700 mb-1">카드 설명</h3>
              <p className="text-sm text-gray-600">
                {card?.description || "프롬프트 카드 설명이 없습니다."}
              </p>
            </div>

            {/* 프롬프트 내용 편집 영역 */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                프롬프트 내용
              </label>

              {isLoading ? (
                <div className="flex items-center justify-center p-8 bg-gray-50 rounded-lg">
                  <div className="flex items-center gap-3">
                    <div className="animate-spin rounded-full h-5 w-5 border-2 border-blue-600 border-t-transparent"></div>
                    <span className="text-gray-600">내용을 불러오는 중...</span>
                  </div>
                </div>
              ) : (
                <textarea
                  ref={textareaRef}
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  onKeyDown={handleKeyDown}
                  className="w-full p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-none transition-colors"
                  placeholder="프롬프트 내용을 입력하세요..."
                  style={{ minHeight: '200px' }}
                />
              )}

              {/* 키보드 단축키 안내 */}
              <div className="mt-2 text-xs text-gray-500">
                <span className="font-medium">키보드 단축키:</span> ESC (취소) • Ctrl+Enter (저장)
              </div>
            </div>

            {/* 변경사항 표시 */}
            {content !== originalContent && (
              <div className="mb-4 p-3 bg-amber-50 border border-amber-200 rounded-lg">
                <div className="flex items-center gap-2">
                  <div className="w-2 h-2 bg-amber-400 rounded-full"></div>
                  <span className="text-sm text-amber-800 font-medium">
                    변경사항이 있습니다
                  </span>
                </div>
                <p className="text-xs text-amber-700 mt-1">
                  저장하기 전에는 변경사항이 적용되지 않습니다.
                </p>
              </div>
            )}
          </div>

          {/* 모달 푸터 */}
          <div className="flex items-center justify-end gap-3 p-6 border-t border-gray-200 bg-gray-50">
            <button
              onClick={handleCancel}
              disabled={isSaving}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors disabled:opacity-50"
            >
              취소
            </button>
            <button
              onClick={handleSave}
              disabled={isSaving || content === originalContent}
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-lg hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
            >
              {isSaving ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-2 border-white border-t-transparent"></div>
                  저장 중...
                </>
              ) : (
                <>
                  <CheckIcon className="h-4 w-4" />
                  저장
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PromptEditModal;
