import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { SunIcon, MoonIcon } from "@heroicons/react/24/outline";

const DarkModeToggle = ({ size = "md" }) => {
  const { theme, isDark, toggleTheme } = useTheme();

  const sizeClasses = {
    sm: "w-8 h-8",
    md: "w-12 h-12",
    lg: "w-14 h-14",
  };

  const iconSizes = {
    sm: "h-4 w-4",
    md: "h-5 w-5",
    lg: "h-6 w-6",
  };

  return (
    <button
      onClick={toggleTheme}
      className={`${sizeClasses[size]} relative group`}
      title={isDark ? "라이트 모드로 전환" : "다크 모드로 전환"}
    >
      {/* 배경 컨테이너 - 글라스모피즘 */}
      <div
        className={`
        absolute inset-0 rounded-2xl transition-all duration-500 ease-out
        ${
          isDark
            ? "glass-dark glow-cyan"
            : "bg-white/70 backdrop-blur-xl border border-white/30 shadow-lg"
        }
        group-hover:scale-110 group-active:scale-95
      `}
      />

      {/* 아이콘 컨테이너 */}
      <div className="relative z-10 flex items-center justify-center w-full h-full">
        {/* 다크모드 아이콘 */}
        <div
          className={`
          absolute transition-all duration-500 ease-out
          ${
            isDark
              ? "opacity-100 rotate-0 scale-100"
              : "opacity-0 rotate-180 scale-75"
          }
        `}
        >
          <SunIcon
            className={`${iconSizes[size]} text-amber-300 drop-shadow-lg filter`}
          />
        </div>

        {/* 라이트모드 아이콘 */}
        <div
          className={`
          absolute transition-all duration-500 ease-out
          ${
            !isDark
              ? "opacity-100 rotate-0 scale-100"
              : "opacity-0 rotate-180 scale-75"
          }
        `}
        >
          <MoonIcon
            className={`${iconSizes[size]} text-slate-600 drop-shadow-lg filter`}
          />
        </div>
      </div>

      {/* 호버 링 효과 */}
      <div
        className={`
        absolute inset-0 rounded-2xl opacity-0 group-hover:opacity-100 
        transition-opacity duration-300 pointer-events-none
        ${isDark ? "ring-2 ring-cyan-400/50" : "ring-2 ring-blue-400/50"}
      `}
      />

      {/* 클릭 시 펄스 효과 */}
      <div
        className={`
        absolute inset-0 rounded-2xl opacity-0 group-active:opacity-100 
        transition-opacity duration-150 pointer-events-none
        ${isDark ? "bg-cyan-400/20" : "bg-blue-400/20"}
      `}
      />
    </button>
  );
};

export default DarkModeToggle;
