import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  ArrowLeftIcon,
  DocumentTextIcon,
  SparklesIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  ChatBubbleLeftRightIcon,
  InformationCircleIcon,
  SunIcon,
  MoonIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  generateAPI,
  handleAPIError,
  promptCardAPI,
} from "../services/api";
import PromptCardManager from "./PromptCardManager";
import ArticleInput from "./ArticleInput";
import ResultDisplay from "./ResultDisplay";
import ChatInterface from "./ChatInterface";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();

  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState("prompt-cards");
  const [promptCards, setPromptCards] = useState([]);
  const [generationResult, setGenerationResult] = useState(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [executionProgress, setExecutionProgress] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  // 다크모드 토글
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle("dark");
  };

  const loadProject = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const projectData = await projectAPI.getProject(projectId);
      setProject(projectData);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  const loadPromptCards = useCallback(async () => {
    try {
      const response = await promptCardAPI.getPromptCards(
        projectId,
        false,
        true
      );
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("프롬프트 카드 로딩 실패:", error);
      setPromptCards([]);
    }
  }, [projectId]);

  useEffect(() => {
    loadProject();
    loadPromptCards();
  }, [loadProject, loadPromptCards]);

  const canGenerate = () => {
    // 최소 1개 이상의 활성화된 프롬프트 카드가 있으면 생성 가능
    return promptCards.some((card) => card.enabled !== false);
  };

  const handleGenerateTitle = async (article, onProgress) => {
    try {
      setIsGenerating(true);
      setExecutionProgress(null);

      const result = await generateAPI.generateTitle(
        projectId,
        article,
        (progress) => {
          setExecutionProgress(progress);
          if (onProgress) onProgress(progress);
        }
      );

      setGenerationResult(result);
      setActiveTab("result");
      toast.success("제목 생성이 완료되었습니다!");
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`제목 생성 실패: ${errorInfo.message}`);
      setExecutionProgress({ status: "failed", message: errorInfo.message });
    } finally {
      setIsGenerating(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">
            프로젝트 로딩 중...
          </p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <ExclamationTriangleIcon className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <p className="text-red-600 dark:text-red-400 mb-4">{error}</p>
          <button
            onClick={() => navigate(-1)}
            className="text-blue-600 dark:text-blue-400 hover:underline"
          >
            돌아가기
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* 헤더 */}
      <div className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700 mb-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate(-1)}
                className="flex items-center text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100 transition-colors"
              >
                <ArrowLeftIcon className="h-5 w-5 mr-2" />
                프로젝트 목록
              </button>

              <div className="flex items-center space-x-3">
                <div className="w-2 h-8 bg-blue-600 rounded-full"></div>
                <div>
                  <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                    {project?.name}
                  </h1>
                  <p className="text-gray-600 dark:text-gray-400 mt-1">
                    {project?.description || "AI 기반 제목 생성 프로젝트"}
                  </p>
                </div>
              </div>
            </div>

            {/* 다크모드 토글 */}
            <button
              onClick={toggleDarkMode}
              className="p-2 rounded-lg bg-gray-100 dark:bg-gray-700 hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors"
              title="다크모드 토글"
            >
              {darkMode ? (
                <SunIcon className="h-5 w-5 text-yellow-500" />
              ) : (
                <MoonIcon className="h-5 w-5 text-gray-600" />
              )}
            </button>
          </div>

          {/* 프로젝트 상태 정보 */}
          <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4">
              <div className="flex items-center">
                <DocumentTextIcon className="h-5 w-5 text-blue-600 dark:text-blue-400 mr-2" />
                <span className="text-sm font-medium text-blue-900 dark:text-blue-100">
                  프롬프트 카드
                </span>
              </div>
              <p className="text-2xl font-bold text-blue-600 dark:text-blue-400 mt-1">
                {promptCards.length}
              </p>
              <p className="text-xs text-blue-600 dark:text-blue-400">
                활성:{" "}
                {promptCards.filter((card) => card.enabled !== false).length}개
              </p>
            </div>

            <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-4">
              <div className="flex items-center">
                <CheckCircleIcon className="h-5 w-5 text-green-600 dark:text-green-400 mr-2" />
                <span className="text-sm font-medium text-green-900 dark:text-green-100">
                  생성 준비
                </span>
              </div>
              <p className="text-2xl font-bold text-green-600 dark:text-green-400 mt-1">
                {canGenerate() ? "완료" : "대기"}
              </p>
              <p className="text-xs text-green-600 dark:text-green-400">
                {canGenerate() ? "제목 생성 가능" : "프롬프트 카드 필요"}
              </p>
            </div>

            <div className="bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800 rounded-lg p-4">
              <div className="flex items-center">
                <SparklesIcon className="h-5 w-5 text-purple-600 dark:text-purple-400 mr-2" />
                <span className="text-sm font-medium text-purple-900 dark:text-purple-100">
                  AI 모델
                </span>
              </div>
              <p className="text-sm font-bold text-purple-600 dark:text-purple-400 mt-1">
                Claude 3.5 Sonnet
              </p>
              <p className="text-xs text-purple-600 dark:text-purple-400">
                최고 성능 모델
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* 탭 네비게이션 */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <nav className="flex space-x-8">
            <button
              onClick={() => setActiveTab("prompt-cards")}
              className={`py-3 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === "prompt-cards"
                  ? "border-blue-500 text-blue-600 dark:text-blue-400"
                  : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600"
              }`}
            >
              <DocumentTextIcon className="h-5 w-5 inline mr-2" />
              프롬프트 카드
              <span className="ml-2 px-2 py-1 text-xs bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 rounded-full">
                {promptCards.length}
              </span>
            </button>

            <button
              onClick={() => setActiveTab("generate")}
              className={`py-3 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === "generate"
                  ? "border-blue-500 text-blue-600 dark:text-blue-400"
                  : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600"
              }`}
            >
              <SparklesIcon className="h-5 w-5 inline mr-2" />
              제목 생성
              {!canGenerate() && (
                <span className="ml-2 px-2 py-1 text-xs bg-yellow-100 dark:bg-yellow-900 text-yellow-800 dark:text-yellow-200 rounded-full">
                  대기
                </span>
              )}
            </button>

            <button
              onClick={() => setActiveTab("chat")}
              className={`py-3 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === "chat"
                  ? "border-blue-500 text-blue-600 dark:text-blue-400"
                  : "border-transparent text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300 hover:border-gray-300 dark:hover:border-gray-600"
              }`}
            >
              <ChatBubbleLeftRightIcon className="h-5 w-5 inline mr-2" />
              AI 채팅
            </button>
          </nav>
        </div>

        {/* 탭 설명 */}
        <div className="mb-6">
          {activeTab === "prompt-cards" && (
            <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4">
              <div className="flex items-start">
                <InformationCircleIcon className="h-5 w-5 text-blue-600 dark:text-blue-400 mr-2 mt-0.5" />
                <div>
                  <h3 className="font-medium text-blue-900 dark:text-blue-100 mb-1">
                    프롬프트 카드 관리
                  </h3>
                  <p className="text-sm text-blue-800 dark:text-blue-200">
                    AI가 제목을 생성할 때 사용할 지침과 규칙을 카드 형태로
                    관리합니다. 카드의 순서를 조정하고 활성/비활성을 설정할 수
                    있습니다.
                  </p>
                </div>
              </div>
            </div>
          )}

          {activeTab === "generate" && (
            <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-4">
              <div className="flex items-start">
                <InformationCircleIcon className="h-5 w-5 text-green-600 dark:text-green-400 mr-2 mt-0.5" />
                <div>
                  <h3 className="font-medium text-green-900 dark:text-green-100 mb-1">
                    AI 제목 생성
                  </h3>
                  <p className="text-sm text-green-800 dark:text-green-200">
                    기사 내용을 입력하면 설정된 프롬프트 카드를 바탕으로 AI가
                    최적의 제목을 생성합니다. 생성 과정을 시각적으로 확인할 수
                    있습니다.
                  </p>
                </div>
              </div>
            </div>
          )}

          {activeTab === "chat" && (
            <div className="bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800 rounded-lg p-4">
              <div className="flex items-start">
                <InformationCircleIcon className="h-5 w-5 text-purple-600 dark:text-purple-400 mr-2 mt-0.5" />
                <div>
                  <h3 className="font-medium text-purple-900 dark:text-purple-100 mb-1">
                    AI 채팅 어시스턴트
                  </h3>
                  <p className="text-sm text-purple-800 dark:text-purple-200">
                    프로젝트의 프롬프트 카드를 활용한 대화형 AI
                    어시스턴트입니다. 제목 생성 관련 질문이나 조언을 받을 수
                    있습니다.
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* 탭 콘텐츠 */}
        <div className="min-h-screen">
          {activeTab === "prompt-cards" && (
            <PromptCardManager
              projectId={projectId}
              onCardsChanged={loadPromptCards}
            />
          )}

          {activeTab === "generate" && (
            <ArticleInput
              canGenerate={canGenerate()}
              isGenerating={isGenerating}
              onGenerate={handleGenerateTitle}
              executionProgress={executionProgress}
            />
          )}

          {activeTab === "chat" && (
            <div className="h-[calc(100vh-16rem)]">
              <ChatInterface
                projectId={projectId}
                projectName={project?.name}
                promptCards={promptCards}
              />
            </div>
          )}

          {activeTab === "result" && generationResult && (
            <ResultDisplay
              result={generationResult}
              projectName={project?.name}
              onBackToGenerate={() => setActiveTab("generate")}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectDetail;
