import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { 
  PaperAirplaneIcon, 
  ArrowLeftIcon, 
  DocumentPlusIcon,
  XMarkIcon 
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import ChatMessage from "./chat/ChatMessage";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === 'text/plain') {
      const reader = new FileReader();
      reader.onload = (e) => {
        setInputValue(e.target?.result || '');
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'text/plain') {
        const reader = new FileReader();
        reader.onload = (e) => {
          setInputValue(e.target?.result || '');
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    setInputValue('');
  };

  return (
    <div className="flex flex-col h-full">
      {/* 헤더 */}
      <div className="flex-shrink-0 bg-gradient-to-r from-slate-50/80 to-white/90 backdrop-blur-sm border-b border-slate-200/60 px-6 py-5">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/")}
              className="text-slate-600 hover:text-slate-900 transition-all duration-300 p-3 hover:bg-white/80 rounded-xl shadow-sm hover:shadow-md hover:scale-105"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <div>
              <h1 className="text-2xl font-bold text-slate-800 tracking-tight">
                {projectName}
              </h1>
              <p className="text-sm text-slate-600 font-medium">🤖 생성형 AI 업그레이드 서비스</p>
            </div>
          </div>
          <div className="flex items-center gap-4">
            <div className="text-sm text-slate-700 bg-gradient-to-r from-white to-slate-50 px-4 py-2 rounded-xl border border-slate-200/60 shadow-sm font-semibold">
              <span className="text-emerald-600">{promptCards.filter((card) => card.enabled).length}개</span>
              <span className="text-slate-500 mx-1">/</span>
              <span>6단계 활성화</span>
            </div>
            <div className="flex items-center gap-2 bg-gradient-to-r from-emerald-50 to-green-50 px-3 py-2 rounded-xl border border-emerald-200/60">
              <div className="w-3 h-3 bg-gradient-to-r from-emerald-400 to-green-500 rounded-full animate-pulse"></div>
              <span className="text-sm text-emerald-700 font-semibold">시스템 준비완료</span>
            </div>
          </div>
        </div>

        {/* 워크플로우 진행 상태 표시 */}
        {promptCards.filter(card => card.enabled).length > 0 && (
          <div className="mt-5 p-4 bg-gradient-to-r from-white/90 to-slate-50/80 rounded-xl border border-slate-200/60 shadow-sm backdrop-blur-sm">
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center gap-2">
                <span className="text-base font-bold text-slate-800">🚀 AI 워크플로우 단계</span>
              </div>
              <span className="text-sm text-slate-600 bg-slate-100 px-3 py-1 rounded-lg font-semibold">
                {promptCards.filter(card => card.enabled).length}/6단계
              </span>
            </div>
            <div className="flex items-center gap-2">
              {promptCards
                .filter(card => card.enabled)
                .sort((a, b) => (a.order || a.step_order || 0) - (b.order || b.step_order || 0))
                .map((card, index) => (
                  <div
                    key={card.id || card.promptId}
                    className={`flex-1 h-3 rounded-full transition-all duration-500 ${
                      isGenerating && index === 0 
                        ? 'bg-gradient-to-r from-blue-500 to-indigo-500 animate-pulse shadow-lg shadow-blue-300/50' 
                        : 'bg-gradient-to-r from-slate-200 to-slate-300'
                    }`}
                    title={card.name || `${index + 1}단계`}
                  />
                ))}
            </div>
          </div>
        )}
      </div>

      {/* 메시지 영역 */}
      <div className="flex-1 overflow-y-auto px-6 py-6 min-h-0 bg-gradient-to-b from-transparent via-slate-50/20 to-transparent">
        <div className="max-w-5xl mx-auto space-y-6">
          {messages.map((message) => (
            <ChatMessage
              key={message.id}
              message={message}
              onCopyMessage={handleCopyMessage}
              onCopyTitle={handleCopyTitle}
              copiedMessage={copiedMessage}
            />
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* 입력 영역 */}
      <div className="flex-shrink-0 bg-gradient-to-r from-slate-50/80 to-white/90 backdrop-blur-sm border-t border-slate-200/60 px-6 py-5">
        <div className="max-w-5xl mx-auto">
          {/* 업로드된 파일 표시 */}
          {uploadedFile && (
            <div className="mb-4 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200/60 rounded-xl flex items-center justify-between shadow-sm">
              <div className="flex items-center gap-3">
                <DocumentPlusIcon className="h-5 w-5 text-blue-600" />
                <span className="text-sm text-blue-700 font-semibold">{uploadedFile.name}</span>
              </div>
              <button
                onClick={removeFile}
                className="text-blue-600 hover:text-blue-800 p-2 hover:bg-blue-100 rounded-lg transition-all duration-200"
              >
                <XMarkIcon className="h-4 w-4" />
              </button>
            </div>
          )}

          <div 
            className={`flex items-end space-x-4 ${dragOver ? 'opacity-60' : ''}`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            <div className="flex-1 relative">
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="기사 내용을 입력하거나 텍스트 파일을 드래그하세요..."
                className={`w-full px-5 py-4 border-2 rounded-2xl focus:outline-none resize-none transition-all duration-300 shadow-sm text-slate-700 font-medium placeholder-slate-400 ${
                  dragOver 
                    ? 'border-blue-400 bg-blue-50/60 border-dashed backdrop-blur-sm' 
                    : 'border-slate-300/60 focus:ring-2 focus:ring-blue-400/50 focus:border-blue-400 bg-white/90 backdrop-blur-sm hover:border-slate-400/60'
                }`}
                rows={4}
                disabled={isGenerating}
              />

              {/* 파일 업로드 버튼 */}
              <div className="absolute bottom-4 right-4 flex items-center gap-2">
                <label className="cursor-pointer p-2.5 text-slate-400 hover:text-slate-600 transition-all duration-200 hover:bg-slate-100 rounded-xl">
                  <DocumentPlusIcon className="h-5 w-5" />
                  <input
                    type="file"
                    accept=".txt"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </label>
              </div>
            </div>

            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isGenerating}
              className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-4 rounded-2xl hover:from-blue-700 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-blue-400/50 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 disabled:hover:scale-100"
            >
              {isGenerating ? (
                <div className="animate-spin rounded-full h-6 w-6 border-2 border-t-transparent border-white"></div>
              ) : (
                <PaperAirplaneIcon className="h-6 w-6" />
              )}
            </button>
          </div>

          {/* 도움말 텍스트 */}
          <div className="mt-4 flex items-center justify-between text-sm text-slate-500">
            <span className="font-medium">💡 Enter로 전송 • Shift+Enter로 줄바꿈</span>
            <span className="text-blue-600 font-semibold">📁 .txt 파일 지원</span>
          </div>

          {/* 드래그 오버레이 */}
          {dragOver && (
            <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-indigo-500/10 border-2 border-blue-400 border-dashed rounded-2xl flex items-center justify-center backdrop-blur-sm">
              <div className="text-center">
                <DocumentPlusIcon className="h-16 w-16 text-blue-500 mx-auto mb-3" />
                <p className="text-blue-700 font-bold text-lg">📁 파일을 여기에 놓으세요</p>
                <p className="text-blue-600 text-sm mt-1">.txt 파일만 지원됩니다</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
