export const handleAPIError = (error) => {
  if (error.response) {
    const { status, data } = error.response;
    return {
      message: data?.error || `서버 오류 (${status})`,
      status,
    };
  } else if (error.request) {
    return {
      message: "네트워크 연결을 확인해주세요",
      status: 0,
    };
  } else {
    return {
      message: error.message || "알 수 없는 오류가 발생했습니다",
      status: -1,
    };
  }
};

