import { useState, useEffect, useRef, useCallback, useMemo } from "react";
import { toast } from "react-hot-toast";
import { copyToClipboard } from "../utils/clipboard";
import { useOrchestration } from "./useOrchestration";

/**
 * AI 응답을 파싱하고 UI에 맞는 메시지 객체로 변환
 */
const processAIResponse = (result) => {
  if (!result || !result.result) {
    console.error("AI 응답 오류: 결과가 없습니다", result);
    return {
      id: "error-" + Date.now(),
      type: "assistant",
      content: "처리 중 오류가 발생했습니다. 다시 시도해주세요.",
      timestamp: new Date(),
      isError: true,
    };
  }

  // LangChain과 직접 통신하므로, 결과가 바로 content가 됨
  const responseContent = result.result;

  return {
    id: "response-" + Date.now(),
    type: "assistant",
    content: responseContent,
    timestamp: new Date(),
    // 성능 메트릭 포함
    performance_metrics: result.performance_metrics,
    model_info: result.model_info,
  };
};

/**
 * 채팅 기능을 위한 커스텀 훅
 * @param {string} projectId - 프로젝트 ID
 * @param {string} projectName - 프로젝트 이름
 * @param {Array} promptCards - 프롬프트 카드 배열
 * @returns {Object} - 채팅 관련 상태와 함수들
 */
export const useChat = (projectId, projectName, promptCards = []) => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [copiedMessage, setCopiedMessage] = useState(null);
  // 핵심 수정: useRef를 사용하여 ID 관리
  const streamingMessageIdRef = useRef(null);
  const [connectionError, setConnectionError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [streamingMetrics, setStreamingMetrics] = useState({
    startTime: null,
    firstChunkTime: null,
    lastChunkTime: null,
    totalChunks: 0,
    avgChunkSize: 0,
  });

  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const {
    isExecuting: isGenerating,
    isStreaming,
    executeOrchestration,
    pollOrchestrationResult,
    resetOrchestration,
  } = useOrchestration(projectId);

  // 초기 환영 메시지 설정 - 제거됨 (빈 상태로 시작)
  useEffect(() => {
    setMessages([]);
    setConnectionError(null);
    setRetryCount(0);
    streamingMessageIdRef.current = null; // ref 초기화
  }, [projectName]);

  // 메시지 추가 시 스크롤 하단으로
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  /**
   * 개선: 스트리밍 응답 처리 함수 - ref 사용
   */
  const handleStreamingResponse = useCallback(
    (chunk, metadata) => {
      const currentStreamingId = streamingMessageIdRef.current;

      console.log("청크 수신:", chunk, "메타데이터:", metadata);
      console.log("현재 스트리밍 ID:", currentStreamingId);

      if (!currentStreamingId) {
        console.error("스트리밍 ID가 null입니다!");
        return;
      }

      // 연결 오류 해결
      if (connectionError) {
        setConnectionError(null);
        setRetryCount(0);
      }

      setMessages((prev) => {
        const updatedMessages = [...prev];
        const streamingMsgIndex = updatedMessages.findIndex(
          (msg) => msg.id === currentStreamingId
        );

        console.log("스트리밍 메시지 찾기:", {
          streamingMessageId: currentStreamingId,
          foundIndex: streamingMsgIndex,
          totalMessages: updatedMessages.length,
          availableIds: updatedMessages.map((msg) => msg.id),
        });

        if (streamingMsgIndex !== -1) {
          // 기존 스트리밍 메시지 업데이트
          console.log("스트리밍 메시지 업데이트 성공");
          updatedMessages[streamingMsgIndex] = {
            ...updatedMessages[streamingMsgIndex],
            content: updatedMessages[streamingMsgIndex].content + chunk,
            isLoading: true,
            isStreaming: true,
          };
        } else {
          console.log("스트리밍 메시지를 찾을 수 없음!", {
            streamingMessageId: currentStreamingId,
            availableIds: updatedMessages.map((msg) => msg.id),
          });

          // 새로운 해결책: 마지막 로딩 중인 메시지 찾기
          const lastLoadingIndex = updatedMessages.findIndex(
            (msg) => msg.isLoading === true && msg.isStreaming === true
          );

          if (lastLoadingIndex !== -1) {
            console.log("마지막 로딩 메시지 업데이트:", lastLoadingIndex);
            updatedMessages[lastLoadingIndex] = {
              ...updatedMessages[lastLoadingIndex],
              content: updatedMessages[lastLoadingIndex].content + chunk,
              isLoading: true,
              isStreaming: true,
            };
          }
        }

        return updatedMessages;
      });

      // 스크롤 조정
      scrollToBottom();
    },
    [connectionError, scrollToBottom]
  );

  /**
   * 개선: 스트리밍 완료 처리 함수 - ref 사용
   */
  const handleStreamingComplete = useCallback(
    (result) => {
      const currentStreamingId = streamingMessageIdRef.current;

      console.log("스트리밍 완료 처리:", {
        streamingMessageId: currentStreamingId,
        resultLength: result.result?.length || 0,
        timestamp: new Date().toISOString(),
      });

      // 연결 오류 해결
      if (connectionError) {
        setConnectionError(null);
        setRetryCount(0);
      }

      setMessages((prev) => {
        const updatedMessages = [...prev];
        let streamingMsgIndex = -1;

        // 1차: ID로 찾기
        if (currentStreamingId) {
          streamingMsgIndex = updatedMessages.findIndex(
            (msg) => msg.id === currentStreamingId
          );
        }

        // 2차: 로딩 중인 메시지 찾기
        if (streamingMsgIndex === -1) {
          streamingMsgIndex = updatedMessages.findIndex(
            (msg) => msg.isLoading === true && msg.isStreaming === true
          );
        }

        console.log("완료 처리 - 메시지 찾기:", {
          streamingMessageId: currentStreamingId,
          foundIndex: streamingMsgIndex,
          totalMessages: updatedMessages.length,
        });

        if (streamingMsgIndex !== -1) {
          console.log("완료 처리 - 스트리밍 메시지 업데이트 성공");
          updatedMessages[streamingMsgIndex] = {
            ...updatedMessages[streamingMsgIndex],
            content: result.result,
            isLoading: false,
            isStreaming: false,
            performance_metrics: result.performance_metrics,
            model_info: result.model_info,
            timestamp: new Date(),
          };
        } else {
          console.log("완료 처리 - 스트리밍 메시지를 찾을 수 없음!");
          // 새 메시지로 추가
          updatedMessages.push({
            id: "response-" + Date.now(),
            type: "assistant",
            content: result.result,
            timestamp: new Date(),
            performance_metrics: result.performance_metrics,
            model_info: result.model_info,
          });
        }

        return updatedMessages;
      });

      // 스트리밍 ID 초기화
      streamingMessageIdRef.current = null;

      // 스크롤 조정
      scrollToBottom();
    },
    [connectionError, scrollToBottom]
  );

  /**
   * 개선: 스트리밍 오류 처리 함수
   */
  const handleStreamingError = useCallback(
    (error) => {
      const currentStreamingId = streamingMessageIdRef.current;

      console.error("스트리밍 오류 처리:", error);

      // 연결 오류 상태 업데이트
      if (
        error.message?.includes("CORS") ||
        error.code === "ERR_NETWORK" ||
        error.message?.includes("Access-Control-Allow-Origin")
      ) {
        setConnectionError({
          type: "CORS",
          message: "CORS 정책 오류가 발생했습니다. 서버 설정을 확인해주세요.",
          timestamp: new Date(),
        });
      } else if (error.code === "ECONNABORTED") {
        setConnectionError({
          type: "TIMEOUT",
          message: "요청 시간이 초과되었습니다.",
          timestamp: new Date(),
        });
      } else {
        setConnectionError({
          type: "GENERAL",
          message: "서버 연결 오류가 발생했습니다.",
          timestamp: new Date(),
        });
      }

      // 재시도 횟수 증가
      setRetryCount((prev) => prev + 1);

      // 오류 메시지로 변환
      setMessages((prev) => {
        const updatedMessages = [...prev];
        let streamingMsgIndex = -1;

        // 1차: ID로 찾기
        if (currentStreamingId) {
          streamingMsgIndex = updatedMessages.findIndex(
            (msg) => msg.id === currentStreamingId
          );
        }

        // 2차: 로딩 중인 메시지 찾기
        if (streamingMsgIndex === -1) {
          streamingMsgIndex = updatedMessages.findIndex(
            (msg) => msg.isLoading === true && msg.isStreaming === true
          );
        }

        if (streamingMsgIndex !== -1) {
          let errorMessage = "메시지 처리 중 오류가 발생했습니다.";

          if (error.message?.includes("CORS")) {
            errorMessage =
              "서버 연결 오류가 발생했습니다. 관리자에게 문의하세요.";
          } else if (error.code === "ECONNABORTED") {
            errorMessage =
              "처리 시간이 초과되었습니다. 요청을 단순화하거나 잠시 후 다시 시도해주세요.";
          } else if (error.message?.includes("Network Error")) {
            errorMessage = "네트워크 연결을 확인하고 다시 시도해주세요.";
          }

          updatedMessages[streamingMsgIndex] = {
            ...updatedMessages[streamingMsgIndex],
            content: errorMessage,
            isLoading: false,
            isStreaming: false,
            isError: true,
            timestamp: new Date(),
            errorDetails: {
              type: error.code || "UNKNOWN",
              message: error.message,
              status: error.response?.status,
              retryCount: retryCount + 1,
            },
          };
        }

        return updatedMessages;
      });

      // 스트리밍 ID 초기화
      streamingMessageIdRef.current = null;
    },
    [retryCount]
  );

  /**
   * 개선: 메시지 전송 함수 - ref 사용
   */
  const handleSendMessage = useCallback(async () => {
    if (!inputValue.trim() || isGenerating) return;

    const userMessage = {
      id: "user-" + Date.now(),
      type: "user",
      content: inputValue.trim(),
      timestamp: new Date(),
    };

    // 개선: 스트리밍 메시지 ID 생성 및 ref에 저장
    const streamMsgId = "streaming-" + Date.now();
    streamingMessageIdRef.current = streamMsgId;

    console.log("새 스트리밍 메시지 ID 생성:", streamMsgId);

    // 스트리밍 응답을 위한 초기 메시지
    const streamingMessage = {
      id: streamMsgId,
      type: "assistant",
      content: "",
      timestamp: new Date(),
      isLoading: true,
      isStreaming: true,
    };

    setMessages((prev) => [...prev, userMessage, streamingMessage]);
    setInputValue("");

    try {
      const orchestrationData = {
        userInput: userMessage.content,
        chat_history: messages
          .filter((msg) => !msg.isLoading && !msg.isError)
          .map((msg) => ({
            role: msg.type === "user" ? "user" : "assistant",
            content: msg.content,
          })),
      };

      // 스트리밍 옵션 설정
      const streamingOptions = {
        useStreaming: true,
        chat_history: orchestrationData.chat_history,
        onChunk: handleStreamingResponse,
        onError: handleStreamingError,
        onComplete: handleStreamingComplete,
      };

      // 스트리밍 방식으로 실행
      await executeOrchestration(userMessage.content, streamingOptions);
    } catch (error) {
      console.error("메시지 전송 실패:", error);
      handleStreamingError(error);
    }
  }, [
    inputValue,
    isGenerating,
    executeOrchestration,
    handleStreamingResponse,
    handleStreamingComplete,
    handleStreamingError,
    messages,
  ]);

  /**
   * 재시도 함수
   */
  const handleRetry = useCallback(async () => {
    if (isGenerating || !messages.length) return;

    // 마지막 사용자 메시지 찾기
    const lastUserMessage = messages
      .slice()
      .reverse()
      .find((msg) => msg.type === "user");

    if (lastUserMessage) {
      // 기존 오류 메시지 제거
      setMessages((prev) => prev.filter((msg) => !msg.isError));

      // 입력값 설정 후 다시 전송
      setInputValue(lastUserMessage.content);

      // 짧은 지연 후 전송
      setTimeout(() => {
        handleSendMessage();
      }, 100);
    }
  }, [messages, isGenerating, handleSendMessage]);

  /**
   * Enter 키로 전송
   */
  const handleKeyPress = useCallback(
    (e) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault();
        handleSendMessage();
      }
    },
    [handleSendMessage]
  );

  /**
   * 메시지 복사
   */
  const handleCopyMessage = useCallback(async (content, messageId) => {
    const success = await copyToClipboard(content);
    if (success) {
      setCopiedMessage(messageId);
      setTimeout(() => setCopiedMessage(null), 2000);
    }
  }, []);

  /**
   * 개별 제목 복사
   */
  const handleCopyTitle = useCallback(async (title, messageId, index) => {
    const success = await copyToClipboard(title, "복사되었습니다!");
    if (success) {
      setCopiedMessage(`${messageId}_title_${index}`);
      setTimeout(() => setCopiedMessage(null), 2000);
    }
  }, []);

  /**
   * 채팅 초기화
   */
  const resetChat = useCallback(() => {
    setMessages([]);
    setInputValue("");
    setCopiedMessage(null);
    streamingMessageIdRef.current = null; // ref 초기화
    setConnectionError(null);
    setRetryCount(0);
    resetOrchestration();
  }, [resetOrchestration]);

  /**
   * 연결 상태 확인 함수
   */
  const checkConnection = useCallback(async () => {
    try {
      // 간단한 연결 테스트 (프로젝트 정보 조회)
      const response = await fetch(`/api/projects/${projectId}`, {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      });

      if (response.ok) {
        setConnectionError(null);
        setRetryCount(0);
        return true;
      }
      return false;
    } catch (error) {
      console.error("연결 확인 실패:", error);
      return false;
    }
  }, [projectId]);

  // 스트리밍 디버그 정보 (개발 환경에서만)
  const debugInfo = useMemo(() => {
    if (process.env.NODE_ENV !== 'development') return null;

    return {
      streamingMessageId: streamingMessageIdRef.current,
      messageCount: messages.length,
      isGenerating,
      isStreaming,
      connectionError,
      retryCount,
      hasLoadingMessages: messages.some(msg => msg.isLoading),
      lastMessageType: messages[messages.length - 1]?.type,
    };
  }, [messages, isGenerating, isStreaming, connectionError, retryCount]);

  return {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    isStreaming,
    streamingMessageId: streamingMessageIdRef.current, // ref 값 반환
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    resetChat,
    scrollToBottom,
    connectionError,
    retryCount,
    handleRetry,
    checkConnection,
    debugInfo, // 개발 환경에서 디버깅용
  };
};
