        // 🔧 폴백 응답을 스트리밍 형태로 시뮬레이션
        if (fallbackResponse.data.result && onChunk) {
          const fullText = fallbackResponse.data.result;
          const words = fullText.split(" ");

          // 빠른 속도로 워드 단위 스트리밍 시뮬레이션
          for (let i = 0; i < words.length; i++) {
            const word = words[i] + (i < words.length - 1 ? " " : "");
            onChunk(word, { content: word });

            // 매우 짧은 지연 (실시간 느낌)
            await new Promise((resolve) => setTimeout(resolve, 50));
          }
        }

        // 완료 콜백 호출
        if (onComplete) {
          onComplete({
            result: fallbackResponse.data.result,
            model_info: fallbackResponse.data.model_info,
            performance_metrics: fallbackResponse.data.performance_metrics,
            timestamp: new Date().toISOString(),
          });
        }

        return fallbackResponse.data;
      } catch (fallbackError) {
        console.error("❌ 폴백도 실패:", fallbackError);
        if (onError) {
          onError(
            new Error(
              "모든 API 엔드포인트가 실패했습니다. 서버 상태를 확인해주세요."
            )
          );
        }
        throw new Error(
          "서비스를 사용할 수 없습니다. 잠시 후 다시 시도해주세요."
        );
      }
    }

    // 🔧 실제 스트리밍 시도 (사전 테스트 통과한 경우에만)
    try {
      console.log("🔧 실제 스트리밍 API 호출 시도...");

      const response = await api.post(
        `/projects/${projectId}/generate/stream`,
        data,
        {
          timeout: 120000, // 🔧 2분으로 단축
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      );

      if (!response || !response.data) {
        throw new Error("스트리밍 응답이 없습니다");
      }

      const responseData = response.data;

      console.log("✅ 스트리밍 응답 수신:", {
        mode: responseData.mode,
        resultLength: responseData.result?.length || 0,
        chunksCount: responseData.chunks?.length || 0,
        timestamp: new Date().toISOString(),
      });

      // 청크 데이터 처리
      if (responseData.chunks && Array.isArray(responseData.chunks)) {
        for (const chunk of responseData.chunks) {
          if (chunk.content && onChunk) {
            onChunk(chunk.content, chunk);
            await new Promise((resolve) => setTimeout(resolve, 30)); // 스트리밍 효과
          }
        }
      } else if (responseData.result && onChunk) {
        // 청크 없는 경우 텍스트를 단어 단위로 분할
        const words = responseData.result.split(" ");
        const chunkSize = Math.max(1, Math.floor(words.length / 20)); // 20개 청크

        for (let i = 0; i < words.length; i += chunkSize) {
          const chunkWords = words.slice(i, i + chunkSize);
          const chunkContent =
            chunkWords.join(" ") + (i + chunkSize < words.length ? " " : "");

          if (chunkContent.trim()) {
            onChunk(chunkContent, { content: chunkContent });
            await new Promise((resolve) => setTimeout(resolve, 80));
          }
        }
      }

      // 완료 콜백 호출
      if (onComplete) {
        onComplete({
          result: responseData.result,
          model_info: responseData.model_info,
          performance_metrics: responseData.performance_metrics,
          timestamp: responseData.timestamp || new Date().toISOString(),
        });
      }

      return responseData;
    } catch (error) {
      console.error("스트리밍 대화 생성 실패:", {
        code: error.code,
        message: error.message,
        status: error.response?.status,
        timestamp: new Date().toISOString(),
