import React from "react";
import {
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import { AVAILABLE_MODELS } from "../../services/api";

const PromptCard = ({
  card,
  onEdit,
  onToggle,
  onDelete,
  isCurrentStep = false,
  isExecuted = false,
  isExecuting = false,
  stepNumber = 0,
  hideDeleteButton = false, // 삭제 버튼 숨김 옵션 추가
}) => {
  const getModelDisplayName = (modelId) => {
    const model = AVAILABLE_MODELS.find((m) => m.id === modelId);
    return model ? model.name : modelId;
  };

  const handleToggleClick = async () => {
    try {
      await onToggle(card.promptId || card.id, !card.enabled);
    } catch (error) {
      console.error("카드 토글 실패:", error);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  const getCardStyles = () => {
    let baseStyles =
      "bg-white border rounded-lg p-4 transition-all duration-300 hover:shadow-md hover:border-gray-300";

    if (isCurrentStep) {
      baseStyles += " ring-2 ring-blue-500 shadow-lg border-blue-200";
    } else if (isExecuted) {
      baseStyles += " border-green-200 bg-green-50";
    } else if (card.enabled) {
      baseStyles += " border-gray-200 hover:bg-gray-50";
    } else {
      baseStyles += " border-gray-100 bg-gray-50 opacity-70";
    }

    return baseStyles;
  };

  const getCategoryInfo = (categoryId) => {
    const categories = {
      role: { name: "역할", color: "bg-blue-100 text-blue-800" },
      guidelines: {
        name: "가이드라인",
        color: "bg-purple-100 text-purple-800",
      },
      workflow: { name: "워크플로우", color: "bg-green-100 text-green-800" },
      output_results: {
        name: "출력결과",
        color: "bg-orange-100 text-orange-800",
      },
      examples: { name: "예시", color: "bg-yellow-100 text-yellow-800" },
      review: { name: "검수", color: "bg-red-100 text-red-800" },
    };
    return (
      categories[categoryId] || {
        name: categoryId,
        color: "bg-gray-100 text-gray-800",
      }
    );
  };

  const categoryInfo = getCategoryInfo(card.category);

  return (
    <div className={getCardStyles()}>
      {/* 카드 헤더 */}
      <div className="flex items-start justify-between mb-3">
        <div className="flex-1">
          {/* 실행 단계 표시 */}
          {isExecuting && (
            <div className="flex items-center mb-2">
              <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-blue-600 mr-2"></div>
              <span className="text-xs text-blue-600 font-medium">
                단계 {stepNumber} 실행 중...
              </span>
            </div>
          )}

          {/* 카테고리 태그 */}
          <span
            className={`inline-block px-2 py-0.5 rounded-full text-xs font-semibold mb-2 ${categoryInfo.color}`}
          >
            {categoryInfo.name}
          </span>

          {/* 카드 제목 */}
          <h4 className="font-semibold text-gray-900 text-sm mb-1">
            {card.title || `${categoryInfo.name} 프롬프트`}
          </h4>

          {/* 모델 정보 */}
          <div className="flex items-center text-xs text-gray-500">
            <Cog6ToothIcon className="h-3 w-3 mr-1" />
            {getModelDisplayName(card.model_name)}
            <span className="mx-1">•</span>
            <span>온도: {card.temperature || 0.7}</span>
          </div>
        </div>

        {/* 액션 버튼들 */}
        <div className="flex items-center space-x-1 ml-2">
          {/* 활성화/비활성화 토글 */}
          <button
            onClick={handleToggleClick}
            className={`p-1.5 rounded-lg transition-all duration-200 ${
              card.enabled
                ? "text-green-600 hover:bg-green-100 bg-green-50"
                : "text-gray-400 hover:bg-gray-100"
            }`}
            title={card.enabled ? "비활성화" : "활성화"}
          >
            {card.enabled ? (
              <EyeIcon className="h-4 w-4" />
            ) : (
              <EyeSlashIcon className="h-4 w-4" />
            )}
          </button>

          {/* 편집 버튼 */}
          <button
            onClick={handleEdit}
            className="p-1.5 text-gray-600 hover:bg-blue-100 hover:text-blue-600 rounded-lg transition-all duration-200"
            title="편집"
          >
            <PencilIcon className="h-4 w-4" />
          </button>

          {/* 삭제 버튼 - hideDeleteButton이 true면 숨김 */}
          {!hideDeleteButton && (
            <button
              onClick={() => onDelete(card.promptId || card.id)}
              className="p-2 text-red-600 hover:bg-red-100 rounded-lg transition-all duration-200"
              title="삭제"
            >
              <TrashIcon className="h-5 w-5" />
            </button>
          )}
        </div>
      </div>

      {/* 프롬프트 미리보기 */}
      <div className="text-sm text-gray-600 leading-relaxed">
        <div className="bg-gray-50 rounded-lg p-4 max-h-24 overflow-hidden border border-gray-100">
          {card.prompt_text ? (
            <div className="line-clamp-3">
              {card.prompt_text.length > 120
                ? `${card.prompt_text.substring(0, 120)}...`
                : card.prompt_text}
            </div>
          ) : (
            <span className="text-gray-400 italic">
              프롬프트 내용이 없습니다.
            </span>
          )}
        </div>
      </div>

      {/* 실행 완료 표시 */}
      {isExecuted && (
        <div className="mt-3 flex items-center text-green-600 text-sm">
          <SparklesIcon className="h-4 w-4 mr-1" />
          실행 완료
        </div>
      )}
    </div>
  );
};

export default PromptCard;
