import { useState, useCallback } from "react";
import { toast } from "react-hot-toast";
import { generateAPI } from "../services/api";

/**
 * 제목 생성 실행 및 결과 폴링을 위한 커스텀 훅
 * @param {string} projectId - 프로젝트 ID
 * @returns {Object} - 제목 생성 관련 상태와 함수들
 */
export const useOrchestration = (projectId) => {
  const [isExecuting, setIsExecuting] = useState(false);
  const [currentExecution, setCurrentExecution] = useState(null);
  const [executionStatus, setExecutionStatus] = useState(null);

  /**
   * 제목 생성 실행
   * @param {string} content - 입력 내용 (기사 텍스트)
   * @param {Object} config - 생성 설정
   * @returns {Promise<Object>} - 생성 결과
   */
  const executeOrchestration = useCallback(
    async (content, config = {}) => {
      if (!content.trim()) {
        toast.error("기사 내용을 입력해주세요");
        return null;
      }

      try {
        setIsExecuting(true);
        setExecutionStatus("STARTING");

        const userRequest = config.userRequest || "";

        console.log("🚀 제목 생성 요청 시작:", {
          projectId,
          contentLength: content.length,
          userRequest,
          timestamp: new Date().toISOString()
        });

        const response = await generateAPI.generateTitle(
          projectId,
          content,
          userRequest
        );

        console.log("✅ 제목 생성 완료:", {
          mode: response.mode,
          message: response.message,
          timestamp: new Date().toISOString()
        });

        setIsExecuting(false);
        setExecutionStatus("COMPLETED");

        return response;
      } catch (error) {
        console.error("❌ 제목 생성 실패:", {
          error: error.message,
          code: error.code,
          status: error.response?.status,
          timestamp: new Date().toISOString()
        });
        setIsExecuting(false);
        setExecutionStatus("FAILED");

        // 프롬프트 카드 관련 에러 처리
        if (error.response?.status === 400 && error.response?.data?.setup_required) {
          toast.error("프롬프트 카드를 먼저 설정해주세요!");
        } else if (error.code === 'ECONNABORTED') {
          toast.error("요청 시간이 초과되었습니다. 다시 시도해주세요.");
        } else {
          toast.error("제목 생성 중 오류가 발생했습니다.");
        }

        throw error;
      }
    },
    [projectId]
  );

  /**
   * 실행 상태 조회 (Step Functions 사용 시)
   * @param {string} executionArn - 실행 ARN
   * @param {Function} onComplete - 완료 시 콜백
   * @param {Function} onError - 에러 시 콜백
   */
  const pollOrchestrationResult = useCallback(
    async (executionArn, onComplete, onError) => {
      const poll = async () => {
        try {
          const result = await generateAPI.getExecutionStatus(executionArn);

          setExecutionStatus(result.status);

          if (result.status === "SUCCEEDED") {
            setIsExecuting(false);
            setExecutionStatus("COMPLETED");

            if (onComplete) {
              onComplete(result);
            }
          } else if (result.status === "FAILED") {
            setIsExecuting(false);
            setExecutionStatus("FAILED");

            if (onError) {
              onError(new Error("제목 생성 실패"));
            }
          } else if (result.status === "RUNNING") {
            // 3초 후 다시 폴링
            setTimeout(poll, 3000);
          }
        } catch (error) {
          console.error("실행 상태 조회 실패:", error);
          setIsExecuting(false);
          setExecutionStatus("FAILED");

          if (onError) {
            onError(error);
          }
        }
      };

      poll();
    },
    [projectId]
  );

  /**
   * 오케스트레이션 상태 초기화
   */
  const resetOrchestration = useCallback(() => {
    setIsExecuting(false);
    setCurrentExecution(null);
    setExecutionStatus(null);
  }, []);

  return {
    isExecuting,
    currentExecution,
    executionStatus,
    executeOrchestration,
    pollOrchestrationResult,
    resetOrchestration,
  };
};
