import React, { createContext, useContext, useReducer } from "react";

// 초기 상태
const initialState = {
  projects: [],
  currentProject: null,
  prompts: [],
  loading: false,
  error: null,
};

// 액션 타입
const ActionTypes = {
  SET_LOADING: "SET_LOADING",
  SET_ERROR: "SET_ERROR",
  SET_PROJECTS: "SET_PROJECTS",
  SET_CURRENT_PROJECT: "SET_CURRENT_PROJECT",
  SET_PROMPTS: "SET_PROMPTS",
  ADD_PROJECT: "ADD_PROJECT",
  REMOVE_PROJECT: "REMOVE_PROJECT",
  ADD_PROMPT: "ADD_PROMPT",
  UPDATE_PROMPT: "UPDATE_PROMPT",
  REMOVE_PROMPT: "REMOVE_PROMPT",
};

// 리듀서
const appReducer = (state, action) => {
  switch (action.type) {
    case ActionTypes.SET_LOADING:
      return { ...state, loading: action.payload };

    case ActionTypes.SET_ERROR:
      return { ...state, error: action.payload, loading: false };

    case ActionTypes.SET_PROJECTS:
      return { ...state, projects: action.payload, loading: false };

    case ActionTypes.SET_CURRENT_PROJECT:
      return { ...state, currentProject: action.payload, loading: false };

    case ActionTypes.SET_PROMPTS:
      return { ...state, prompts: action.payload, loading: false };

    case ActionTypes.ADD_PROJECT:
      return {
        ...state,
        projects: [...state.projects, action.payload],
        loading: false,
      };

    case ActionTypes.REMOVE_PROJECT:
      return {
        ...state,
        projects: state.projects.filter((p) => p.id !== action.payload),
        loading: false,
      };

    case ActionTypes.ADD_PROMPT:
      return {
        ...state,
        prompts: [...state.prompts, action.payload],
        loading: false,
      };

    case ActionTypes.UPDATE_PROMPT:
      return {
        ...state,
        prompts: state.prompts.map((p) =>
          p.id === action.payload.id ? action.payload : p
        ),
        loading: false,
      };

    case ActionTypes.REMOVE_PROMPT:
      return {
        ...state,
        prompts: state.prompts.filter((p) => p.id !== action.payload),
        loading: false,
      };

    default:
      return state;
  }
};

// Context 생성
const AppContext = createContext();

// Provider 컴포넌트
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  const actions = {
    setLoading: (loading) =>
      dispatch({ type: ActionTypes.SET_LOADING, payload: loading }),
    setError: (error) =>
      dispatch({ type: ActionTypes.SET_ERROR, payload: error }),
    setProjects: (projects) =>
      dispatch({ type: ActionTypes.SET_PROJECTS, payload: projects }),
    setCurrentProject: (project) =>
      dispatch({ type: ActionTypes.SET_CURRENT_PROJECT, payload: project }),
    setPrompts: (prompts) =>
      dispatch({ type: ActionTypes.SET_PROMPTS, payload: prompts }),
    addProject: (project) =>
      dispatch({ type: ActionTypes.ADD_PROJECT, payload: project }),
    removeProject: (projectId) =>
      dispatch({ type: ActionTypes.REMOVE_PROJECT, payload: projectId }),
    addPrompt: (prompt) =>
      dispatch({ type: ActionTypes.ADD_PROMPT, payload: prompt }),
    updatePrompt: (prompt) =>
      dispatch({ type: ActionTypes.UPDATE_PROMPT, payload: prompt }),
    removePrompt: (promptId) =>
      dispatch({ type: ActionTypes.REMOVE_PROMPT, payload: promptId }),
  };

  return (
    <AppContext.Provider value={{ state, actions }}>
      {children}
    </AppContext.Provider>
  );
};

// Hook
export const useApp = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error("useApp must be used within an AppProvider");
  }
  return context;
};
