import React, { useState } from "react";
import {
  PaperAirplaneIcon,
  DocumentDuplicateIcon,
  CheckIcon,
} from "@heroicons/react/24/outline";
import { useAI } from "../hooks/useAI";

const TitleGenerator = ({ projectId, projectName }) => {
  const { isGenerating, generatedTitles, generateTitle, copyTitle } = useAI();
  const [inputValue, setInputValue] = useState("");
  const [copiedTitle, setCopiedTitle] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputValue.trim() || isGenerating) return;

    try {
      await generateTitle(projectId, inputValue.trim());
      setInputValue("");
    } catch (error) {
      // 에러는 hook에서 처리됨
    }
  };

  const handleCopyTitle = async (title) => {
    await copyTitle(title);
    setCopiedTitle(title);
    setTimeout(() => setCopiedTitle(null), 2000);
  };

  return (
    <div className="flex flex-col h-full">
      {/* 헤더 */}
      <div className="flex-shrink-0 bg-white border-b border-gray-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-xl font-semibold text-slate-800">
              {projectName}
            </h1>
            <p className="text-sm text-gray-500">AI 제목 생성기</p>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            <span className="text-sm text-slate-600">준비완료</span>
          </div>
        </div>
      </div>

      {/* 생성된 제목 목록 */}
      <div className="flex-1 overflow-y-auto px-6 py-4">
        <div className="max-w-4xl mx-auto space-y-4">
          {generatedTitles.length === 0 ? (
            <div className="text-center py-12">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                AI 제목 생성기
              </h3>
              <p className="text-gray-500">
                기사 내용을 입력하면 AI가 최적의 제목을 생성해드립니다
              </p>
            </div>
          ) : (
            generatedTitles.map((title) => (
              <TitleCard
                key={title.id}
                title={title}
                onCopy={handleCopyTitle}
                isCopied={copiedTitle === title.content}
              />
            ))
          )}
        </div>
      </div>

      {/* 입력 영역 */}
      <div className="flex-shrink-0 bg-white border-t border-gray-200 px-6 py-4">
        <div className="max-w-4xl mx-auto">
          <form onSubmit={handleSubmit} className="flex items-end space-x-3">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                기사 내용
              </label>
              <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="기사 내용을 입력하고 제목을 생성해보세요..."
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
                rows={4}
                disabled={isGenerating}
              />
            </div>
            <button
              type="submit"
              disabled={isGenerating || !inputValue.trim()}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center"
            >
              {isGenerating ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                <>
                  <PaperAirplaneIcon className="h-5 w-5 mr-2" />
                  제목 생성
                </>
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

// 제목 카드 컴포넌트
const TitleCard = ({ title, onCopy, isCopied }) => {
  return (
    <div className="bg-white rounded-lg border border-gray-200 p-6 shadow-sm">
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-center mb-2">
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
              AI 생성
            </span>
            <span className="ml-2 text-xs text-gray-500">
              {title.timestamp.toLocaleTimeString("ko-KR", {
                hour: "2-digit",
                minute: "2-digit",
              })}
            </span>
          </div>
          <h3 className="text-lg font-semibold text-gray-900 mb-3">
            {title.content}
          </h3>
          <div className="text-sm text-gray-600">
            <strong>원본 기사:</strong>
            <p className="mt-1 line-clamp-3">{title.original}</p>
          </div>
        </div>

        <button
          onClick={() => onCopy(title.content)}
          className={`ml-4 p-2 rounded-lg transition-colors ${
            isCopied
              ? "bg-green-100 text-green-600"
              : "bg-gray-100 text-gray-600 hover:bg-gray-200"
          }`}
          title="제목 복사"
        >
          {isCopied ? (
            <CheckIcon className="h-5 w-5" />
          ) : (
            <DocumentDuplicateIcon className="h-5 w-5" />
          )}
        </button>
      </div>
    </div>
  );
};

export default TitleGenerator;
