import React, { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext(null);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState({
    email: "dev@example.com",
    name: "Í∞úÎ∞úÏûê",
    sub: "dev-user",
  });
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(true);

  // Í∞úÎ∞ú Î™®Îìú: Ìï≠ÏÉÅ Ïù∏Ï¶ùÎêú ÏÉÅÌÉú
  useEffect(() => {
    console.log("üß™ Í∞úÎ∞ú Î™®Îìú: Ïù∏Ï¶ù Í∏∞Îä• ÎπÑÌôúÏÑ±Ìôî - Ìï≠ÏÉÅ Ïù∏Ï¶ùÎêú ÏÉÅÌÉú");
    setLoading(false);
  }, []);

  const login = async (credentials) => {
    console.log("üß™ Í∞úÎ∞ú Î™®Îìú: Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠ Î¨¥Ïãú", credentials);
    return { success: true };
  };

  const logout = async () => {
    console.log("üß™ Í∞úÎ∞ú Î™®Îìú: Î°úÍ∑∏ÏïÑÏõÉ ÏöîÏ≤≠ Î¨¥Ïãú");
  };

  const signup = async (userData) => {
    console.log("üß™ Í∞úÎ∞ú Î™®Îìú: ÌöåÏõêÍ∞ÄÏûÖ ÏöîÏ≤≠ Î¨¥Ïãú", userData);
    throw new Error("Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎäî ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÎπÑÌôúÏÑ±ÌôîÎêòÏñ¥ ÏûàÏäµÎãàÎã§");
  };

  const verifyEmail = async (verificationData) => {
    console.log("üß™ Í∞úÎ∞ú Î™®Îìú: Ïù¥Î©îÏùº Ïù∏Ï¶ù ÏöîÏ≤≠ Î¨¥Ïãú", verificationData);
    throw new Error("Í∞úÎ∞ú Î™®ÎìúÏóêÏÑúÎäî Ïù¥Î©îÏùº Ïù∏Ï¶ùÏù¥ ÎπÑÌôúÏÑ±ÌôîÎêòÏñ¥ ÏûàÏäµÎãàÎã§");
  };

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    logout,
    signup,
    verifyEmail,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export default AuthContext;
