import React, { useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ArrowLeftIcon } from "@heroicons/react/24/outline";
import { useProjects } from "../hooks/useProjects";
import { usePrompts } from "../hooks/usePrompts";
import LoadingSpinner from "./LoadingSpinner";
import PromptManager from "./PromptManager";
import TitleGenerator from "./TitleGenerator";

const ProjectDetail = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const { currentProject, loading: projectLoading, getProject } = useProjects();
  const { prompts, loading: promptsLoading, loadPrompts } = usePrompts();

  useEffect(() => {
    if (projectId) {
      getProject(projectId);
      loadPrompts(projectId);
    }
  }, [projectId, getProject, loadPrompts]);

  if (projectLoading) {
    return <LoadingSpinner message="프로젝트를 불러오는 중..." />;
  }

  if (!currentProject) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-900 mb-2">
            프로젝트를 찾을 수 없습니다
          </h2>
          <button
            onClick={() => navigate("/")}
            className="text-blue-600 hover:text-blue-800"
          >
            홈으로 돌아가기
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 프롬프트 관리 사이드바 */}
      <div className="w-1/3 bg-white border-r border-gray-200 flex flex-col">
        {/* 헤더 */}
        <div className="p-4 border-b border-gray-200">
          <div className="flex items-center justify-between mb-2">
            <button
              onClick={() => navigate("/")}
              className="text-gray-600 hover:text-gray-900 transition-colors p-2 hover:bg-gray-100 rounded-lg"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <h1 className="text-lg font-semibold text-gray-900">
              {currentProject.name}
            </h1>
            <div></div>
          </div>
        </div>

        {/* 프롬프트 관리 */}
        <div className="flex-1 overflow-hidden">
          <PromptManager
            projectId={projectId}
            prompts={prompts}
            loading={promptsLoading}
          />
        </div>
      </div>

      {/* 제목 생성 영역 */}
      <div className="flex-1">
        <TitleGenerator
          projectId={projectId}
          projectName={currentProject.name}
        />
      </div>
    </div>
  );
};

export default ProjectDetail;
