import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { 
  PaperAirplaneIcon, 
  ArrowLeftIcon, 
  DocumentPlusIcon,
  XMarkIcon 
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import ChatMessage from "./chat/ChatMessage";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    copiedMessage,
    isGenerating,
    messagesEndRef,
    inputRef,
    handleSendMessage,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === 'text/plain') {
      const reader = new FileReader();
      reader.onload = (e) => {
        setInputValue(e.target?.result || '');
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === 'text/plain') {
        const reader = new FileReader();
        reader.onload = (e) => {
          setInputValue(e.target?.result || '');
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    setInputValue('');
  };

  return (
    <div className="flex flex-col h-full">
      {/* 헤더 */}
      <div className="flex-shrink-0 bg-gradient-to-r from-white to-gray-50 border-b border-gray-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/")}
              className="text-gray-600 hover:text-gray-900 transition-colors p-2 hover:bg-white rounded-lg"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <div>
              <h1 className="text-xl font-semibold text-gray-900">
                {projectName}
              </h1>
              <p className="text-sm text-gray-600">AI 제목 생성 채팅</p>
            </div>
          </div>
          <div className="flex items-center gap-4">
            <div className="text-sm text-gray-500 bg-white px-3 py-1 rounded-full border shadow-sm">
              {promptCards.filter((card) => card.enabled).length}개 프롬프트 활성화
            </div>
            <div className="flex items-center gap-2">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-xs text-gray-500">준비완료</span>
            </div>
          </div>
        </div>

        {/* 워크플로우 진행 상태 표시 */}
        {promptCards.filter(card => card.enabled).length > 0 && (
          <div className="mt-4 p-3 bg-white rounded-lg border border-gray-200 shadow-sm">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium text-gray-700">워크플로우 단계</span>
              <span className="text-xs text-gray-500">
                {promptCards.filter(card => card.enabled).length}단계
              </span>
            </div>
            <div className="flex items-center gap-2">
              {promptCards
                .filter(card => card.enabled)
                .sort((a, b) => (a.order || a.step_order || 0) - (b.order || b.step_order || 0))
                .map((card, index) => (
                  <div
                    key={card.id || card.promptId}
                    className={`flex-1 h-2 rounded-full transition-all duration-300 ${
                      isGenerating && index === 0 
                        ? 'bg-blue-500 animate-pulse' 
                        : 'bg-gray-200'
                    }`}
                    title={card.name || `${index + 1}단계`}
                  />
                ))}
            </div>
          </div>
        )}
      </div>

      {/* 메시지 영역 */}
      <div className="flex-1 overflow-y-auto px-6 py-4 min-h-0">
        <div className="max-w-4xl mx-auto">
          {messages.map((message) => (
            <ChatMessage
              key={message.id}
              message={message}
              onCopyMessage={handleCopyMessage}
              onCopyTitle={handleCopyTitle}
              copiedMessage={copiedMessage}
            />
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* 입력 영역 */}
      <div className="flex-shrink-0 bg-gradient-to-r from-white to-gray-50 border-t border-gray-200 px-6 py-4">
        <div className="max-w-4xl mx-auto">
          {/* 업로드된 파일 표시 */}
          {uploadedFile && (
            <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded-lg flex items-center justify-between">
              <div className="flex items-center gap-2">
                <DocumentPlusIcon className="h-4 w-4 text-blue-600" />
                <span className="text-sm text-blue-700 font-medium">{uploadedFile.name}</span>
              </div>
              <button
                onClick={removeFile}
                className="text-blue-600 hover:text-blue-800 p-1"
              >
                <XMarkIcon className="h-4 w-4" />
              </button>
            </div>
          )}

          <div 
            className={`flex items-end space-x-4 ${dragOver ? 'opacity-50' : ''}`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            <div className="flex-1 relative">
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="기사 내용을 입력하거나 텍스트 파일을 드래그하세요..."
                className={`w-full px-4 py-3 border-2 rounded-xl focus:outline-none resize-none transition-all duration-200 ${
                  dragOver 
                    ? 'border-blue-400 bg-blue-50 border-dashed' 
                    : 'border-gray-300 focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white'
                }`}
                rows={3}
                disabled={isGenerating}
              />

              {/* 파일 업로드 버튼 */}
              <div className="absolute bottom-3 right-3 flex items-center gap-2">
                <label className="cursor-pointer p-2 text-gray-400 hover:text-gray-600 transition-colors">
                  <DocumentPlusIcon className="h-5 w-5" />
                  <input
                    type="file"
                    accept=".txt"
                    onChange={handleFileUpload}
                    className="hidden"
                  />
                </label>
              </div>
            </div>

            <button
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isGenerating}
              className="bg-gradient-to-r from-blue-600 to-blue-700 text-white p-3 rounded-xl hover:from-blue-700 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg hover:shadow-xl"
            >
              {isGenerating ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                <PaperAirplaneIcon className="h-5 w-5" />
              )}
            </button>
          </div>

          {/* 도움말 텍스트 */}
          <div className="mt-3 flex items-center justify-between text-xs text-gray-500">
            <span>Enter로 전송 • Shift+Enter로 줄바꿈</span>
            <span className="text-blue-600">.txt 파일 지원</span>
          </div>

          {/* 드래그 오버레이 */}
          {dragOver && (
            <div className="absolute inset-0 bg-blue-500 bg-opacity-10 border-2 border-blue-400 border-dashed rounded-xl flex items-center justify-center">
              <div className="text-center">
                <DocumentPlusIcon className="h-12 w-12 text-blue-500 mx-auto mb-2" />
                <p className="text-blue-700 font-medium">파일을 여기에 놓으세요</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
