import React, { useState, useEffect } from "react";
import {
  StarIcon,
  CheckCircleIcon,
  ClipboardDocumentIcon,
} from "@heroicons/react/24/outline";
import { toast } from "react-hot-toast";

const ResultDisplay = ({ result, projectName }) => {
  const [copiedTitle, setCopiedTitle] = useState(null);
  const [visibleSections, setVisibleSections] = useState({});
  const [typingEffect, setTypingEffect] = useState({});

  // JSON 파싱 처리
  const parseResult = () => {
    try {
      if (typeof result.result === "string") {
        return JSON.parse(result.result);
      }
      return result.result;
    } catch (error) {
      console.error("JSON parsing error:", error);
      return {
        analysis: {},
        titles: {},
        final_recommendation: {},
      };
    }
  };

  const parsedResult = parseResult();

  // 애니메이션 효과를 위한 useEffect
  useEffect(() => {
    const sections = ['recommendation', 'categories'];

    sections.forEach((section, index) => {
      setTimeout(() => {
        setVisibleSections(prev => ({ ...prev, [section]: true }));
      }, index * 300);
    });
  }, []);

  // 타이핑 효과 훅
  const useTypingEffect = (text, speed = 50) => {
    const [displayText, setDisplayText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    useEffect(() => {
      setDisplayText('');
      setIsComplete(false);

      if (!text) return;

      let i = 0;
      const timer = setInterval(() => {
        if (i < text.length) {
          setDisplayText(prev => prev + text.charAt(i));
          i++;
        } else {
          setIsComplete(true);
          clearInterval(timer);
        }
      }, speed);

      return () => clearInterval(timer);
    }, [text, speed]);

    return { displayText, isComplete };
  };

  const copyToClipboard = (text, titleType) => {
    navigator.clipboard
      .writeText(text)
      .then(() => {
        setCopiedTitle(titleType);
        toast.success("제목이 클립보드에 복사되었습니다!");
        setTimeout(() => setCopiedTitle(null), 2000);
      })
      .catch((err) => {
        console.error("복사 실패:", err);
        toast.error("복사에 실패했습니다.");
      });
  };

  const getScoreColor = (score) => {
    if (score >= 85) return "text-green-600";
    if (score >= 70) return "text-yellow-600";
    return "text-red-600";
  };

  const getScoreBackground = (score) => {
    if (score >= 85) return "bg-green-100";
    if (score >= 70) return "bg-yellow-100";
    return "bg-red-100";
  };

  // 최종 추천 제목의 타이핑 효과
  const finalTitle = parsedResult.final_recommendation?.title || '';
  const { displayText: typedTitle, isComplete: titleComplete } = useTypingEffect(
    visibleSections.recommendation ? finalTitle : '', 
    30
  );

  return (
    <div className="space-y-6">

      {/* 최종 추천 제목 */}
      <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900 flex items-center">
            <StarIcon className="h-5 w-5 mr-2 text-orange-500" />
            추천 제목
          </h3>
          <span className="text-sm text-gray-500">
            {new Date(result.timestamp).toLocaleString("ko-KR", {
              timeZone: "Asia/Seoul",
              year: "numeric",
              month: "2-digit", 
              day: "2-digit",
              hour: "2-digit",
              minute: "2-digit"
            })}
          </span>
        </div>

        {parsedResult.final_recommendation && (
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center justify-between">
              <h4 className="text-xl font-medium text-gray-900 flex-1 mr-4">
                {visibleSections.recommendation ? typedTitle : ""}
                {visibleSections.recommendation && !titleComplete && (
                  <span className="animate-pulse text-gray-400">|</span>
                )}
              </h4>
              {titleComplete && (
                <button
                  onClick={() =>
                    copyToClipboard(
                      parsedResult.final_recommendation.title,
                      "final"
                    )
                  }
                  className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-200 rounded-lg transition-colors"
                >
                  {copiedTitle === "final" ? (
                    <CheckCircleIcon className="h-5 w-5 text-green-600" />
                  ) : (
                    <ClipboardDocumentIcon className="h-5 w-5" />
                  )}
                </button>
              )}
            </div>
          </div>
        )}
      </div>

      {/* 카테고리별 제목 */}
      <div className={`space-y-6 transition-all duration-500 ${ 
        visibleSections.categories ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
      }`}>
        <div className="bg-gradient-to-r from-white to-gray-50 p-6 rounded-2xl border border-gray-200 shadow-sm">
          <h3 className="text-xl font-bold text-gray-900 flex items-center mb-2">
            <EyeIcon className="h-6 w-6 mr-3 text-purple-600" />
            카테고리별 제목 결과
          </h3>
          <p className="text-gray-600">AI가 분석한 다양한 제목 스타일을 확인해보세요</p>
        </div>

        {parsedResult.titles &&
          Object.entries(parsedResult.titles).map(([category, titles], categoryIndex) => (
            <div
              key={category}
              className={`bg-white border border-gray-200 rounded-2xl shadow-lg transition-all duration-300 hover:shadow-xl ${
                visibleSections.categories ? 'animate-slideInUp' : ''
              }`}
              style={{ animationDelay: `${categoryIndex * 150}ms` }}
            >
              <div className="p-6">
                <div className="flex items-center justify-between mb-6">
                  <h4 className="text-lg font-bold text-gray-900 flex items-center">
                    <span className="w-3 h-3 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 mr-3"></span>
                    {category === "straight"
                      ? "📝 직설적 제목"
                      : category === "question"
                      ? "❓ 질문형 제목"
                      : category === "impact"
                      ? "⚡ 임팩트 제목"
                      : `📋 ${category} 제목`}
                  </h4>
                  <span className="text-sm text-gray-500 bg-gray-100 px-3 py-1 rounded-full font-medium">
                    {titles.length}개
                  </span>
                </div>

                <div className="space-y-4">
                  {titles.map((titleObj, index) => (
                    <div
                      key={index}
                      className="border border-gray-200 rounded-xl p-5 hover:border-blue-300 hover:bg-blue-50/30 transition-all duration-200 group"
                    >
                      <div className="flex items-center justify-between mb-2">
                        <h5 className="text-base font-medium text-gray-900">
                          {titleObj.title || "제목 없음"}
                        </h5>
                        <button
                          onClick={() =>
                            copyToClipboard(
                              titleObj.title,
                              `${category}-${index}`
                            )
                          }
                          className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-100 rounded-full transition-all duration-200 group-hover:scale-110"
                        >
                          {copiedTitle === `${category}-${index}` ? (
                            <CheckCircleIcon className="h-5 w-5 text-green-600" />
                          ) : (
                            <ClipboardDocumentIcon className="h-5 w-5" />
                          )}
                        </button>
                      </div>

                      {titleObj.evaluation && (
                        <div className="flex flex-wrap gap-3 text-sm mt-3">
                          {Object.entries(titleObj.evaluation).map(
                            ([metric, score]) => (
                              <div
                                key={metric}
                                className="flex items-center space-x-2 bg-gray-50 rounded-lg px-3 py-2"
                              >
                                <span className="text-gray-600 capitalize font-medium">
                                  {metric}:
                                </span>
                                <span
                                  className={`px-3 py-1 rounded-full text-xs font-bold ${getScoreBackground(
                                    score
                                  )} ${getScoreColor(score)} min-w-[50px] text-center`}
                                >
                                  {score}점
                                </span>
                              </div>
                            )
                          )}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* 사용 통계 */}
      <div className="bg-gradient-to-r from-gray-50 to-blue-50/30 border border-gray-200 rounded-2xl p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6 flex items-center">
          <svg className="h-5 w-5 mr-2 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
          </svg>
          사용 통계
        </h3>
        <div className="grid gap-6 md:grid-cols-3">
          <div className="bg-white rounded-xl p-6 text-center shadow-sm border border-gray-100">
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <svg className="h-6 w-6 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z" />
              </svg>
            </div>
            <p className="text-3xl font-bold text-blue-600 mb-1">
              {result.usage?.input_tokens?.toLocaleString() || "정보 없음"}
            </p>
            <p className="text-sm text-gray-600 font-medium">입력 토큰</p>
          </div>
          <div className="bg-white rounded-xl p-6 text-center shadow-sm border border-gray-100">
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <svg className="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
              </svg>
            </div>
            <p className="text-3xl font-bold text-green-600 mb-1">
              {result.usage?.output_tokens?.toLocaleString() || "정보 없음"}
            </p>
            <p className="text-sm text-gray-600 font-medium">출력 토큰</p>
          </div>
          <div className="bg-white rounded-xl p-6 text-center shadow-sm border border-gray-100">
            <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <svg className="h-6 w-6 text-purple-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <p className="text-3xl font-bold text-purple-600 mb-1">
              {result.usage?.execution_time
                ? `${result.usage.execution_time.toFixed(1)}초`
                : "정보 없음"}
            </p>
            <p className="text-sm text-gray-600 font-medium">실행 시간</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResultDisplay;
