import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import { PlusIcon, PencilIcon, TrashIcon, XMarkIcon, SparklesIcon, DocumentTextIcon } from "@heroicons/react/24/outline";
import { promptCardAPI, handleAPIError } from "../services/api";
import ChatInterface from "./ChatInterface";

const PromptCardManager = ({ projectId, projectName }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingCard, setEditingCard] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    loadPromptCards();
  }, [projectId]);

  const loadPromptCards = async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(projectId, true);
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("프롬프트 카드 로드 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveCard = async (cardData) => {
    try {
      if (editingCard) {
        // 수정
        await promptCardAPI.updatePromptCard(
          projectId,
          editingCard.promptId || editingCard.prompt_id,
          cardData
        );
        toast.success("프롬프트가 수정되었습니다");
      } else {
        // 새로 생성
        await promptCardAPI.createPromptCard(projectId, cardData);
        toast.success("프롬프트가 생성되었습니다");
      }

      setShowForm(false);
      setEditingCard(null);
      loadPromptCards();
    } catch (error) {
      console.error("프롬프트 저장 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    }
  };

  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트가 삭제되었습니다");
      loadPromptCards();
    } catch (error) {
      console.error("프롬프트 삭제 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    }
  };

  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  const handleNewCard = () => {
    setEditingCard(null);
    setShowForm(true);
  };

  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 채팅 인터페이스 */}
      <div className="flex-1 pr-6">
        <ChatInterface projectId={projectId} projectName={projectName} />
      </div>

      {/* 프롬프트 관리 사이드바 (우측) */}
      <div className="w-80 bg-white border-l border-gray-200 flex flex-col shadow-lg">
        <div className="p-6 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">
              프롬프트 카드
            </h2>
            <button
              onClick={handleNewCard}
              className="inline-flex items-center px-3 py-1.5 text-sm bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              <PlusIcon className="h-4 w-4 mr-1" />추가
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-6 space-y-4">
          {promptCards.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500 mb-3">프롬프트 카드가 없습니다</p>
              <button
                onClick={handleNewCard}
                className="text-blue-600 hover:text-blue-800 text-sm font-medium"
              >
                첫 번째 카드 만들기
              </button>
            </div>
          ) : (
            promptCards.map((card) => (
              <PromptCard
                key={card.promptId || card.prompt_id}
                card={card}
                onEdit={() => handleEditCard(card)}
                onDelete={() => handleDeleteCard(card.promptId || card.prompt_id)}
              />
            ))
          )}
        </div>
      </div>

      {/* 프롬프트 편집 폼 모달 */}
      {showForm && (
        <PromptFormModal
          isOpen={showForm}
          onSubmit={handleSaveCard}
          onCancel={handleCancelForm}
          initialData={editingCard}
        />
      )}
    </div>
  );
};

// 간단한 프롬프트 카드 컴포넌트
const PromptCard = ({ card, onEdit, onDelete }) => {
  const getCategoryColor = (category) => {
    const colors = {
      workflow: "bg-blue-100 text-blue-800",
      stylebook_guidelines: "bg-green-100 text-green-800",
      seo_optimization: "bg-purple-100 text-purple-800",
      title_type_guidelines: "bg-orange-100 text-orange-800",
      default: "bg-gray-100 text-gray-800"
    };
    return colors[category] || colors.default;
  };

  const getCategoryName = (category) => {
    const names = {
      workflow: "워크플로우",
      stylebook_guidelines: "스타일 가이드",
      seo_optimization: "SEO 최적화",
      title_type_guidelines: "제목 유형",
      default: "기본"
    };
    return names[category] || names.default;
  };

  return (
    <div className="bg-white rounded-lg p-4 border border-gray-200 hover:border-gray-300 transition-colors shadow-sm">
      <div className="flex items-start justify-between mb-3">
        <h3 className="font-medium text-gray-900 text-sm leading-tight">
          {card.title || `프롬프트 ${card.promptId || card.prompt_id}`}
        </h3>
        <div className="flex space-x-1 ml-2">
          <button
            onClick={onEdit}
            className="p-1.5 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
          >
            <PencilIcon className="h-3.5 w-3.5" />
          </button>
          <button
            onClick={onDelete}
            className="p-1.5 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded transition-colors"
          >
            <TrashIcon className="h-3.5 w-3.5" />
          </button>
        </div>
      </div>

      {(card.content || card.prompt_text) && (
        <p className="text-xs text-gray-600 mb-3 line-clamp-3 leading-relaxed">
          {(card.content || card.prompt_text).substring(0, 100)}
          {(card.content || card.prompt_text).length > 100 && "..."}
        </p>
      )}

      <div className="flex items-center justify-between">
        <span className={`inline-block px-2 py-1 rounded-full text-xs font-medium ${getCategoryColor(card.category)}`}>
          {getCategoryName(card.category)}
        </span>
        <span className="text-xs text-gray-400">
          순서: {card.stepOrder || 1}
        </span>
      </div>
    </div>
  );
};

// 간단한 프롬프트 편집 폼 모달
const PromptFormModal = ({ isOpen, onSubmit, onCancel, initialData }) => {
  const [formData, setFormData] = useState({
    title: "",
    prompt_text: "",
    category: "workflow",
    enabled: true,
  });

  useEffect(() => {
    if (initialData) {
      setFormData({
        title: initialData.title || "",
        prompt_text: initialData.prompt_text || initialData.content || "",
        category: initialData.category || "workflow",
        enabled: initialData.enabled !== false,
      });
    }
  }, [initialData]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.prompt_text.trim()) {
      toast.error("프롬프트 내용을 입력해주세요");
      return;
    }
    onSubmit(formData);
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  if (!isOpen) return null;

  // 카테고리 옵션들
  const categoryOptions = [
    { value: "instruction", label: "🎯 역할 및 목표", color: "blue" },
    { value: "knowledge", label: "📚 지식 베이스", color: "green" },
    { value: "workflow", label: "⚙️ 워크플로우", color: "purple" },
    { value: "stylebook_guidelines", label: "🎨 스타일 가이드", color: "pink" },
    { value: "seo_optimization", label: "🔍 SEO 최적화", color: "orange" },
    { value: "title_type_guidelines", label: "📝 제목 유형", color: "red" },
    { value: "secondary", label: "🧠 사고 과정", color: "indigo" },
    { value: "output_format", label: "📊 출력 형식", color: "yellow" }
  ];

  const selectedCategory = categoryOptions.find(cat => cat.value === formData.category);

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl max-w-3xl w-full max-h-[90vh] overflow-hidden shadow-2xl">
        {/* 헤더 */}
        <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-6 text-white relative">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-white/20 rounded-lg backdrop-blur-sm">
                {initialData ? (
                  <PencilIcon className="h-6 w-6" />
                ) : (
                  <SparklesIcon className="h-6 w-6" />
                )}
              </div>
              <div>
                <h3 className="text-xl font-bold">
                  {initialData ? "프롬프트 편집" : "새 프롬프트 생성"}
                </h3>
                <p className="text-blue-100 text-sm">
                  {initialData ? "기존 프롬프트를 수정해보세요" : "AI 제목 생성을 위한 새로운 프롬프트를 만들어보세요"}
                </p>
              </div>
            </div>
            <button
              type="button"
              onClick={onCancel}
              className="p-2 hover:bg-white/20 rounded-lg transition-colors"
            >
              <XMarkIcon className="h-6 w-6" />
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6 overflow-y-auto max-h-[calc(90vh-120px)]">
          {/* 제목 필드 */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <DocumentTextIcon className="h-4 w-4 mr-2 text-blue-500" />
              프롬프트 제목
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-4 focus:ring-blue-100 transition-all duration-200 text-gray-800 placeholder-gray-400"
              placeholder="예: 경제 뉴스 제목 생성 가이드라인"
            />
          </div>

          {/* 카테고리 선택 */}
          <div className="space-y-3">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <SparklesIcon className="h-4 w-4 mr-2 text-purple-500" />
              카테고리
            </label>
            <div className="grid grid-cols-2 gap-3">
              {categoryOptions.map((option) => (
                <button
                  key={option.value}
                  type="button"
                  onClick={() => handleChange("category", option.value)}
                  className={`p-3 rounded-xl border-2 transition-all duration-200 text-left ${
                    formData.category === option.value
                      ? `border-${option.color}-300 bg-${option.color}-50 text-${option.color}-700`
                      : "border-gray-200 bg-gray-50 text-gray-600 hover:border-gray-300 hover:bg-gray-100"
                  }`}
                >
                  <div className="text-sm font-medium">{option.label}</div>
                </button>
              ))}
            </div>
          </div>

          {/* 프롬프트 내용 */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <PencilIcon className="h-4 w-4 mr-2 text-green-500" />
              프롬프트 내용 <span className="text-red-500 ml-1">*</span>
            </label>
            <div className="relative">
              <textarea
                value={formData.prompt_text}
                onChange={(e) => handleChange("prompt_text", e.target.value)}
                rows={10}
                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-green-500 focus:ring-4 focus:ring-green-100 transition-all duration-200 text-gray-800 placeholder-gray-400 resize-none"
                placeholder="예: 당신은 경제 뉴스 전문 기자입니다. 다음 가이드라인에 따라 제목을 생성해주세요:\n\n1. 핵심 내용을 15-25자로 요약\n2. 독자의 관심을 끌는 키워드 포함\n3. 정확하고 객관적인 표현 사용"
                required
              />
              <div className="absolute bottom-3 right-3 text-xs text-gray-400">
                {formData.prompt_text.length} 자
              </div>
            </div>
          </div>

          {/* 활성화 옵션 */}
          <div className="bg-gray-50 rounded-xl p-4">
            <label className="flex items-center cursor-pointer">
              <div className="relative">
                <input
                  type="checkbox"
                  id="enabled"
                  checked={formData.enabled}
                  onChange={(e) => handleChange("enabled", e.target.checked)}
                  className="sr-only"
                />
                <div className={`w-12 h-6 rounded-full transition-colors duration-200 ${
                  formData.enabled ? 'bg-blue-500' : 'bg-gray-300'
                }`}>
                  <div className={`w-5 h-5 bg-white rounded-full shadow-md transform transition-transform duration-200 mt-0.5 ${
                    formData.enabled ? 'translate-x-6' : 'translate-x-0.5'
                  }`} />
                </div>
              </div>
              <div className="ml-3">
                <span className="text-sm font-medium text-gray-800">활성화</span>
                <p className="text-xs text-gray-500">이 프롬프트를 제목 생성 시 사용합니다</p>
              </div>
            </label>
          </div>

          {/* 버튼 영역 */}
          <div className="flex justify-end space-x-3 pt-6 border-t border-gray-100">
            <button
              type="button"
              onClick={onCancel}
              className="px-6 py-3 text-gray-600 bg-gray-100 rounded-xl hover:bg-gray-200 transition-colors duration-200 font-medium"
            >
              취소
            </button>
            <button
              type="submit"
              disabled={!formData.prompt_text.trim()}
              className="px-6 py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-xl hover:from-blue-600 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 font-medium shadow-lg"
            >
              {initialData ? "✨ 수정 완료" : "✨ 프롬프트 생성"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PromptCardManager;
