import React from "react";
import { useNavigate } from "react-router-dom";
import { useApp } from "../contexts/AppContext";
import DarkModeToggle from "./DarkModeToggle";

const ModeSelection = () => {
  const navigate = useNavigate();
  const { setMode } = useApp();

  const handleModeSelect = (selectedMode) => {
    setMode(selectedMode);
    navigate("/projects");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 dark:from-slate-950 dark:via-indigo-950 dark:to-purple-950 flex flex-col items-center justify-center p-4 transition-all duration-500 relative overflow-hidden">
      {/* 배경 장식 요소들 */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-gradient-to-r from-cyan-400/20 to-purple-500/20 rounded-full blur-3xl animate-pulse" />
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-gradient-to-r from-purple-500/20 to-pink-500/20 rounded-full blur-3xl animate-pulse delay-1000" />
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[800px] h-[800px] bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-full blur-3xl" />
      </div>

      {/* 다크모드 토글 - 상단 우측에 고정 */}
      <div className="absolute top-8 right-8 z-20">
        <DarkModeToggle size="md" />
      </div>

      {/* 메인 컨텐츠 */}
      <div className="relative z-10">
        {/* 헤더 섹션 */}
        <div className="text-center mb-20">
          <div className="mb-8">
            <h1 className="text-7xl md:text-8xl font-bold bg-gradient-to-r from-gray-900 via-blue-800 to-purple-800 dark:from-white dark:via-cyan-300 dark:to-purple-300 bg-clip-text text-transparent mb-6 animate-fade-in">
              AI 제목 생성기
            </h1>
            <div className="w-32 h-1 bg-gradient-to-r from-cyan-400 to-purple-500 mx-auto rounded-full" />
          </div>
          <p className="text-xl md:text-2xl text-gray-600 dark:text-gray-300 max-w-2xl mx-auto leading-relaxed font-light">
            원하시는 모드를 선택하여
            <span className="text-transparent bg-gradient-to-r from-cyan-500 to-purple-500 bg-clip-text font-medium">
              {" "}
              트렌디한 AI 경험
            </span>
            을 시작해보세요
          </p>
        </div>

        {/* 모드 선택 카드들 */}
        <div className="flex flex-col lg:flex-row gap-8 w-full max-w-5xl mx-auto">
          {/* 사용자 모드 카드 */}
          <div className="flex-1 group">
            <button
              onClick={() => handleModeSelect("user")}
              className="w-full aspect-square relative overflow-hidden card-elevated group-hover:scale-105 transition-all duration-500 p-8 text-white"
            >
              {/* 그라데이션 배경 */}
              <div className="absolute inset-0 bg-gradient-to-br from-cyan-500 via-blue-600 to-purple-600 dark:from-cyan-400 dark:via-blue-500 dark:to-purple-700" />

              {/* 호버 시 글로우 효과 */}
              <div className="absolute inset-0 bg-gradient-to-br from-cyan-300/20 to-purple-400/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />

              {/* 배경 장식 효과 */}
              <div className="absolute top-0 right-0 w-32 h-32 bg-white/10 rounded-full -translate-y-16 translate-x-16 group-hover:scale-110 transition-transform duration-700" />
              <div className="absolute bottom-0 left-0 w-24 h-24 bg-white/10 rounded-full translate-y-12 -translate-x-12 group-hover:scale-110 transition-transform duration-700 delay-100" />
              <div className="absolute -bottom-20 -right-20 w-64 h-64 bg-white/5 rounded-full group-hover:scale-110 transition-transform duration-700 delay-200" />

              <div className="relative z-10 h-full flex flex-col items-center justify-center">
                <div className="text-8xl mb-8 transform group-hover:scale-110 group-hover:rotate-6 transition-all duration-500">
                  👥
                </div>
                <h2 className="text-4xl font-bold mb-4 group-hover:text-cyan-200 transition-colors duration-300">
                  사용자 모드
                </h2>
                <p className="text-cyan-100 text-lg text-center leading-relaxed px-6 opacity-90">
                  직관적이고 깔끔한 채팅 화면으로
                  <br />
                  <span className="font-semibold">바로 대화를 시작하세요</span>
                </p>

                {/* 호버 인디케이터 */}
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 w-12 h-1 bg-white/50 rounded-full group-hover:w-20 group-hover:bg-white transition-all duration-300" />
              </div>
            </button>
          </div>

          {/* 관리자 모드 카드 */}
          <div className="flex-1 group">
            <button
              onClick={() => handleModeSelect("admin")}
              className="w-full aspect-square relative overflow-hidden card-elevated group-hover:scale-105 transition-all duration-500 p-8 text-white"
            >
              {/* 그라데이션 배경 */}
              <div className="absolute inset-0 bg-gradient-to-br from-emerald-500 via-green-600 to-teal-600 dark:from-emerald-400 dark:via-green-500 dark:to-teal-700" />

              {/* 호버 시 글로우 효과 */}
              <div className="absolute inset-0 bg-gradient-to-br from-emerald-300/20 to-teal-400/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />

              {/* 배경 장식 효과 */}
              <div className="absolute top-0 right-0 w-32 h-32 bg-white/10 rounded-full -translate-y-16 translate-x-16 group-hover:scale-110 transition-transform duration-700" />
              <div className="absolute bottom-0 left-0 w-24 h-24 bg-white/10 rounded-full translate-y-12 -translate-x-12 group-hover:scale-110 transition-transform duration-700 delay-100" />
              <div className="absolute -bottom-20 -right-20 w-64 h-64 bg-white/5 rounded-full group-hover:scale-110 transition-transform duration-700 delay-200" />

              <div className="relative z-10 h-full flex flex-col items-center justify-center">
                <div className="text-8xl mb-8 transform group-hover:scale-110 group-hover:rotate-6 transition-all duration-500">
                  ⚙️
                </div>
                <h2 className="text-4xl font-bold mb-4 group-hover:text-emerald-200 transition-colors duration-300">
                  관리자 모드
                </h2>
                <p className="text-emerald-100 text-lg text-center leading-relaxed px-6 opacity-90">
                  고급 프롬프트를 관리하고
                  <br />
                  <span className="font-semibold">시스템을 최적화하세요</span>
                </p>

                {/* 호버 인디케이터 */}
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 w-12 h-1 bg-white/50 rounded-full group-hover:w-20 group-hover:bg-white transition-all duration-300" />
              </div>
            </button>
          </div>
        </div>

        {/* 부가 정보 */}
        <div className="mt-20 text-center">
          <div className="glass-card px-8 py-4 rounded-2xl inline-block">
            <p className="text-gray-600 dark:text-gray-300 text-sm font-medium">
              💡 모드는 언제든지 변경할 수 있습니다
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

