import React, { useState, useEffect } from "react";
import {
  StarIcon,
  CheckCircleIcon,
  ClipboardDocumentIcon,
} from "@heroicons/react/24/outline";
import { toast } from "react-hot-toast";

const ResultDisplay = ({ result, projectName }) => {
  const [copiedTitle, setCopiedTitle] = useState(null);
  const [visibleSections, setVisibleSections] = useState({});
  const [typingEffect, setTypingEffect] = useState({});

  // JSON 파싱 처리
  const parseResult = () => {
    try {
      if (typeof result.result === "string") {
        return JSON.parse(result.result);
      }
      return result.result;
    } catch (error) {
      console.error("JSON parsing error:", error);
      return {
        analysis: {},
        titles: {},
        final_recommendation: {},
      };
    }
  };

  const parsedResult = parseResult();

  // 애니메이션 효과를 위한 useEffect
  useEffect(() => {
    const sections = ['header', 'analysis', 'recommendation', 'categories'];

    sections.forEach((section, index) => {
      setTimeout(() => {
        setVisibleSections(prev => ({ ...prev, [section]: true }));
      }, index * 400);
    });
  }, []);

  // 타이핑 효과 훅
  const useTypingEffect = (text, speed = 50) => {
    const [displayText, setDisplayText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    useEffect(() => {
      setDisplayText('');
      setIsComplete(false);

      if (!text) return;

      let i = 0;
      const timer = setInterval(() => {
        if (i < text.length) {
          setDisplayText(prev => prev + text.charAt(i));
          i++;
        } else {
          setIsComplete(true);
          clearInterval(timer);
        }
      }, speed);

      return () => clearInterval(timer);
    }, [text, speed]);

    return { displayText, isComplete };
  };

  const copyToClipboard = (text, titleType) => {
    navigator.clipboard
      .writeText(text)
      .then(() => {
        setCopiedTitle(titleType);
        toast.success("제목이 클립보드에 복사되었습니다!");
        setTimeout(() => setCopiedTitle(null), 2000);
      })
      .catch((err) => {
        console.error("복사 실패:", err);
        toast.error("복사에 실패했습니다.");
      });
  };

  const getScoreColor = (score) => {
    if (score >= 85) return "text-green-600";
    if (score >= 70) return "text-yellow-600";
    return "text-red-600";
  };

  const getScoreBackground = (score) => {
    if (score >= 85) return "bg-green-100";
    if (score >= 70) return "bg-yellow-100";
    return "bg-red-100";
  };

  // 최종 추천 제목의 타이핑 효과
  const finalTitle = parsedResult.final_recommendation?.title || '';
  const { displayText: typedTitle, isComplete: titleComplete } = useTypingEffect(
    visibleSections.recommendation ? finalTitle : '', 
    30
  );

  return (
    <div className="space-y-8">
      {/* 결과 헤더 */}
      <div className={`bg-gradient-to-r from-white to-blue-50 border border-gray-200 rounded-2xl shadow-lg p-6 transition-all duration-500 ${
        visibleSections.header ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
      }`}>
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-blue-600 bg-clip-text text-transparent flex items-center">
              <TrophyIcon className="h-8 w-8 text-yellow-500 mr-3 animate-bounce" />
              제목 생성 완료!
            </h2>
            <p className="text-gray-600 mt-2 text-lg">
              {projectName} 프로젝트 •{" "}
              {new Date(result.timestamp).toLocaleString("ko-KR")}
            </p>
          </div>

          <div className="text-right bg-white rounded-xl p-4 shadow-sm">
            <p className="text-sm text-gray-500">생성 시간</p>
            <p className="text-2xl font-bold text-blue-600">
              {result.usage?.execution_time
                ? `${result.usage.execution_time.toFixed(1)}초`
                : "⚡ 즉시"}
            </p>
          </div>
        </div>
      </div>

      {/* 기사 분석 결과 */}
      <div className={`bg-white border border-gray-200 rounded-2xl shadow-lg transition-all duration-500 ${ 
        visibleSections.analysis ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
      }`}>
        <div className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
            <ChartBarIcon className="h-5 w-5 mr-2 text-blue-600" />
            기사 분석 결과
          </h3>

          {parsedResult.analysis && (
            <div className="grid gap-4 md:grid-cols-2">
              <div className="space-y-3">
                <div>
                  <p className="text-sm font-medium text-gray-700">핵심 주제</p>
                  <p className="text-base text-gray-900">
                    {parsedResult.analysis.main_topic || "정보 없음"}
                  </p>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-700">대상 독자</p>
                  <p className="text-base text-gray-900">
                    {parsedResult.analysis.target_audience || "정보 없음"}
                  </p>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-700">톤앤매너</p>
                  <p className="text-base text-gray-900">
                    {parsedResult.analysis.tone || "정보 없음"}
                  </p>
                </div>
              </div>

              <div className="space-y-3">
                <div>
                  <p className="text-sm font-medium text-gray-700">긴급성</p>
                  <p className="text-base text-gray-900">
                    {parsedResult.analysis.urgency || "정보 없음"}
                  </p>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-700">
                    핵심 키워드
                  </p>
                  <div className="flex flex-wrap gap-2 mt-1">
                    {parsedResult.analysis.key_keywords?.map(
                      (keyword, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                        >
                          {keyword}
                        </span>
                      )
                    ) || <span className="text-gray-500">정보 없음</span>}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* 최종 추천 제목 */}
      <div className={`bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-2xl shadow-lg transition-all duration-500 ${ 
        visibleSections.recommendation ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
      }`}>
        <div className="p-6">
          <h3 className="text-lg font-semibold text-blue-900 mb-4 flex items-center">
            <StarIcon className="h-5 w-5 mr-2 text-yellow-500 animate-pulse" />
            최종 추천 제목
          </h3>

          {parsedResult.final_recommendation && (
            <div className="space-y-4">
              <div className="bg-white/80 backdrop-blur-sm rounded-xl p-6 border border-blue-200/50 shadow-sm">
                <div className="flex items-center justify-between mb-4">
                  <h4 className="text-2xl font-bold text-gray-900 leading-relaxed">
                    {visibleSections.recommendation ? typedTitle : ""}
                    {visibleSections.recommendation && !titleComplete && (
                      <span className="animate-pulse text-blue-600">|</span>
                    )}
                  </h4>
                  {titleComplete && (
                    <button
                      onClick={() =>
                        copyToClipboard(
                          parsedResult.final_recommendation.title,
                          "final"
                        )
                      }
                      className="p-3 text-blue-600 hover:text-blue-800 hover:bg-blue-100 rounded-full transition-all duration-200 hover:scale-110"
                    >
                      {copiedTitle === "final" ? (
                        <CheckCircleIcon className="h-6 w-6 text-green-600" />
                      ) : (
                        <ClipboardDocumentIcon className="h-6 w-6" />
                      )}
                    </button>
                  )}
                </div>

                {titleComplete && (
                  <div className="space-y-3 animate-fadeIn">
                    <div className="flex items-center space-x-4">
                      <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-to-r from-blue-100 to-indigo-100 text-blue-800 border border-blue-200">
                        {parsedResult.final_recommendation.type || "유형 없음"}
                      </span>
                    </div>
                    <div className="bg-gray-50 rounded-lg p-4">
                      <p className="text-sm text-gray-700 leading-relaxed">
                        <strong className="text-gray-900">선정 이유:</strong>{" "}
                        {parsedResult.final_recommendation.reason || "정보 없음"}
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* 카테고리별 제목 */}
      <div className={`space-y-6 transition-all duration-500 ${ 
        visibleSections.categories ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'
      }`}>
        <div className="bg-gradient-to-r from-white to-gray-50 p-6 rounded-2xl border border-gray-200 shadow-sm">
          <h3 className="text-xl font-bold text-gray-900 flex items-center mb-2">
            <EyeIcon className="h-6 w-6 mr-3 text-purple-600" />
            카테고리별 제목 결과
          </h3>
          <p className="text-gray-600">AI가 분석한 다양한 제목 스타일을 확인해보세요</p>
        </div>

        {parsedResult.titles &&
          Object.entries(parsedResult.titles).map(([category, titles], categoryIndex) => (
            <div
              key={category}
              className={`bg-white border border-gray-200 rounded-2xl shadow-lg transition-all duration-300 hover:shadow-xl ${
                visibleSections.categories ? 'animate-slideInUp' : ''
              }`}
              style={{ animationDelay: `${categoryIndex * 150}ms` }}
            >
              <div className="p-6">
                <div className="flex items-center justify-between mb-6">
                  <h4 className="text-lg font-bold text-gray-900 flex items-center">
                    <span className="w-3 h-3 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 mr-3"></span>
                    {category === "straight"
                      ? "📝 직설적 제목"
                      : category === "question"
                      ? "❓ 질문형 제목"
                      : category === "impact"
                      ? "⚡ 임팩트 제목"
                      : `📋 ${category} 제목`}
                  </h4>
                  <span className="text-sm text-gray-500 bg-gray-100 px-3 py-1 rounded-full font-medium">
                    {titles.length}개
                  </span>
                </div>

                <div className="space-y-4">
                  {titles.map((titleObj, index) => (
                    <div
                      key={index}
                      className="border border-gray-200 rounded-xl p-5 hover:border-blue-300 hover:bg-blue-50/30 transition-all duration-200 group"
                    >
                      <div className="flex items-center justify-between mb-2">
                        <h5 className="text-base font-medium text-gray-900">
                          {titleObj.title || "제목 없음"}
                        </h5>
                        <button
                          onClick={() =>
                            copyToClipboard(
                              titleObj.title,
                              `${category}-${index}`
                            )
                          }
                          className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-100 rounded-full transition-all duration-200 group-hover:scale-110"
                        >
                          {copiedTitle === `${category}-${index}` ? (
                            <CheckCircleIcon className="h-5 w-5 text-green-600" />
                          ) : (
                            <ClipboardDocumentIcon className="h-5 w-5" />
                          )}
                        </button>
                      </div>

                      {titleObj.evaluation && (
                        <div className="flex flex-wrap gap-3 text-sm mt-3">
                          {Object.entries(titleObj.evaluation).map(
                            ([metric, score]) => (
                              <div
                                key={metric}
                                className="flex items-center space-x-2 bg-gray-50 rounded-lg px-3 py-2"
                              >
                                <span className="text-gray-600 capitalize font-medium">
                                  {metric}:
                                </span>
                                <span
                                  className={`px-3 py-1 rounded-full text-xs font-bold ${getScoreBackground(
                                    score
                                  )} ${getScoreColor(score)} min-w-[50px] text-center`}
                                >
                                  {score}점
                                </span>
                              </div>
                            )
                          )}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* 사용 통계 */}
      <div className="bg-gradient-to-r from-gray-50 to-blue-50/30 border border-gray-200 rounded-2xl p-6 shadow-sm">
        <h3 className="text-lg font-semibold text-gray-900 mb-6 flex items-center">
          <svg className="h-5 w-5 mr-2 text-blue-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
          </svg>
          사용 통계
        </h3>
        <div className="grid gap-6 md:grid-cols-3">
          <div className="bg-white rounded-xl p-6 text-center shadow-sm border border-gray-100">
