import React, { useState, useRef, useEffect } from "react";
import {
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  EllipsisHorizontalIcon,
  CheckIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import { AVAILABLE_MODELS, promptCardAPI } from "../../services/api";
import { toast } from "react-hot-toast";
import PromptEditModal from "./PromptEditModal";

const PromptCard = ({
  card,
  onEdit,
  onToggle,
  onDelete,
  isCurrentStep = false,
  isExecuted = false,
  isExecuting = false,
  stepNumber = 0,
  hideDeleteButton = false, // 삭제 버튼 숨김 옵션 추가
  projectId, // 프로젝트 ID 추가
  enableInlineEdit = false, // 인라인 편집 기능 활성화 옵션
}) => {
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [promptContent, setPromptContent] = useState("");

  // 프롬프트 내용 로드
  useEffect(() => {
    if (enableInlineEdit && card.s3Key && projectId) {
      loadPromptContent();
    }
  }, [card.s3Key, projectId, enableInlineEdit]);

  const loadPromptContent = async () => {
    try {
      // S3에서 프롬프트 내용 로드하는 API 호출
      const response = await promptCardAPI.getPromptContent(projectId, card.promptId || card.id);
      setPromptContent(response.content || "프롬프트 내용을 불러올 수 없습니다.");
    } catch (error) {
      console.error("프롬프트 내용 로드 실패:", error);
      setPromptContent("프롬프트 내용을 불러올 수 없습니다.");
    }
  };

  // 인라인 편집 시작
  const startInlineEdit = () => {
    if (!enableInlineEdit) return;

    setOriginalContent(promptContent);
    setEditingContent(promptContent);
    setIsEditing(true);
  };

  // 편집 취소
  const cancelEdit = () => {
    setEditingContent(originalContent);
    setIsEditing(false);
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current);
    }
  };

  // 편집 저장
  const saveEdit = async () => {
    if (editingContent === originalContent) {
      setIsEditing(false);
      return;
    }

    setIsSaving(true);
    try {
      await promptCardAPI.updatePromptCard(projectId, card.promptId || card.id, {
        promptText: editingContent
      });

      setPromptContent(editingContent);
      setIsEditing(false);
      toast.success("프롬프트가 수정되었습니다");
    } catch (error) {
      console.error("프롬프트 수정 실패:", error);
      toast.error("프롬프트 수정에 실패했습니다");
      setEditingContent(originalContent);
    } finally {
      setIsSaving(false);
    }
  };

  // 2초 자동 저장
  const handleContentChange = (value) => {
    setEditingContent(value);

    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current);
    }

    saveTimeoutRef.current = setTimeout(() => {
      if (value !== originalContent) {
        saveEdit();
      }
    }, 2000);
  };

  // 키보드 이벤트 처리
  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      cancelEdit();
    } else if (e.key === 'Enter' && (e.ctrlKey || e.metaKey)) {
      saveEdit();
    }
  };

  // 텍스트 영역 자동 크기 조절
  useEffect(() => {
    if (isEditing && textareaRef.current) {
      textareaRef.current.focus();
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [isEditing]);

  useEffect(() => {
    if (isEditing && textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [editingContent]);
  const getModelDisplayName = (modelId) => {
    const model = AVAILABLE_MODELS.find((m) => m.id === modelId);
    return model ? model.name : modelId;
  };

  const handleToggleClick = async () => {
    try {
      await onToggle(card.promptId || card.id, !card.enabled);
    } catch (error) {
      console.error("카드 토글 실패:", error);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  const getCardStyles = () => {
    let baseStyles =
      "relative bg-white border border-gray-200 rounded-lg p-3 transition-all duration-200 hover:border-gray-300 group";

    if (isCurrentStep) {
      baseStyles += " ring-2 ring-blue-400 border-blue-200 bg-blue-50";
    } else if (isExecuted) {
      baseStyles += " ring-1 ring-green-400 border-green-200 bg-green-50";
    } else if (card.enabled) {
      baseStyles += " hover:ring-1 hover:ring-gray-300";
    } else {
      baseStyles += " bg-gray-50 opacity-60 hover:opacity-80";
    }

    return baseStyles;
  };

  const getCategoryInfo = (categoryId) => {
    const categories = {
      instruction: { 
        name: "역할 및 목표", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "" 
      },
      knowledge: {
        name: "지식 베이스",
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80", 
        icon: ""
      },
      secondary: { 
        name: "CoT (사고 과정)", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: ""
      },
      style_guide: {
        name: "스타일 가이드",
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: ""
      },
      validation: { 
        name: "ReAct (추론+행동)", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: ""
      },
      enhancement: { 
        name: "품질 검증", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: ""
      },
    };
    return (
      categories[categoryId] || {
        name: categoryId,
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: ""
      }
    );
  };

  const categoryInfo = getCategoryInfo(card.category);

  return (
    <div className={getCardStyles()}>
      {/* 실행 상태 표시 */}
      {isExecuting && (
        <div className="absolute -top-1 -right-1 z-10">
          <div className="flex items-center justify-center w-5 h-5 bg-blue-500 text-white rounded-full">
            <div className="animate-spin rounded-full h-2 w-2 border border-t-transparent border-white"></div>
          </div>
        </div>
      )}

      {/* 카드 헤더 */}
      <div className="flex items-start justify-between mb-2">
        <div className="flex items-center gap-2 flex-1 min-w-0">
          {/* 아이콘과 카테고리 */}
          <div className={`inline-flex items-center px-2 py-1 rounded text-xs font-medium border ${categoryInfo.color}`}>
            <span>{categoryInfo.name}</span>
          </div>
        </div>

        {/* 액션 버튼들 */}
        <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
          {/* 활성화/비활성화 토글 */}
          <button
            onClick={handleToggleClick}
            className={`p-1.5 rounded transition-colors ${
              card.enabled
                ? "text-green-600 hover:bg-green-50"
                : "text-gray-400 hover:bg-gray-100"
            }`}
            title={card.enabled ? "비활성화" : "활성화"}
          >
            {card.enabled ? (
              <EyeIcon className="h-3 w-3" />
            ) : (
              <EyeSlashIcon className="h-3 w-3" />
            )}
          </button>

          {/* 편집 버튼 */}
          <button
            onClick={handleEdit}
            className="p-1.5 text-gray-600 hover:bg-blue-50 hover:text-blue-600 rounded transition-colors"
            title="편집"
          >
            <PencilIcon className="h-3 w-3" />
          </button>

          {/* 삭제 버튼 - hideDeleteButton이 true면 숨김 */}
          {!hideDeleteButton && (
            <button
              onClick={() => onDelete(card.promptId || card.id)}
              className="p-1.5 text-gray-400 hover:bg-red-50 hover:text-red-600 rounded transition-colors"
              title="삭제"
            >
              <TrashIcon className="h-3 w-3" />
            </button>
          )}
        </div>
      </div>

      {/* 프롬프트 설명 */}
      <div className="text-xs text-gray-500 leading-relaxed mb-2">
        {card.description || categoryInfo.description || "프롬프트 설명이 없습니다."}
      </div>

      {/* 인라인 편집 가능한 프롬프트 내용 */}
      {enableInlineEdit && (
        <div className="mb-3">
          {isEditing ? (
            <div className="border-2 border-blue-300 rounded-lg bg-white">
              <div className="p-3 border-b border-gray-200 bg-blue-50">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-blue-800">프롬프트 편집 중</span>
                  <div className="flex gap-2">
                    <button
                      onClick={saveEdit}
                      disabled={isSaving}
                      className="px-2 py-1 bg-green-600 text-white text-xs rounded hover:bg-green-700 transition-colors disabled:opacity-50"
                    >
                      저장
                    </button>
                    <button
                      onClick={cancelEdit}
                      disabled={isSaving}
                      className="px-2 py-1 bg-gray-500 text-white text-xs rounded hover:bg-gray-600 transition-colors disabled:opacity-50"
                    >
                      취소
                    </button>
                  </div>
                </div>
              </div>
              <textarea
                ref={textareaRef}
                value={editingContent}
                onChange={(e) => handleContentChange(e.target.value)}
                onKeyDown={handleKeyDown}
                className="w-full p-3 border-0 rounded-b-lg resize-none focus:outline-none text-sm leading-relaxed"
                placeholder="프롬프트 내용을 입력하세요..."
                style={{ minHeight: '120px' }}
              />
              <div className="px-3 pb-2">
                {isSaving ? (
                  <div className="flex items-center gap-2 text-xs text-blue-600">
                    <div className="animate-spin rounded-full h-3 w-3 border border-blue-600 border-t-transparent"></div>
                    저장 중...
                  </div>
                ) : (
                  <div className="text-xs text-gray-500">
                    Esc: 취소 • Ctrl+Enter: 저장 • 2초 후 자동저장
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div 
              onClick={startInlineEdit}
              className="p-4 bg-gradient-to-br from-gray-50 to-gray-100 border border-gray-200 rounded-lg cursor-pointer hover:from-blue-50 hover:to-blue-100 hover:border-blue-300 transition-all duration-200"
            >
              <div className="text-sm text-gray-700 whitespace-pre-wrap leading-relaxed min-h-[60px]">
                {promptContent || (
                  <span className="text-gray-400 italic">
                    프롬프트 내용을 클릭하여 수정하세요...
                  </span>
                )}
              </div>
              <div className="flex items-center justify-between mt-3 pt-2 border-t border-gray-200">
                <div className="text-xs text-gray-500">
                  마지막 수정: 방금 전
                </div>
                <div className="text-xs text-blue-600 opacity-0 group-hover:opacity-100 transition-opacity">
                  클릭하여 편집
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* 특수 기능 표시 */}
      {(card.isCoT || card.isReAct) && (
        <div className="flex gap-1 mb-2">
          {card.isCoT && (
            <span className="inline-flex items-center px-2 py-0.5 bg-purple-50 text-purple-700 rounded text-xs font-medium">
              <span>CoT</span>
            </span>
          )}
          {card.isReAct && (
            <span className="inline-flex items-center px-2 py-0.5 bg-orange-50 text-orange-700 rounded text-xs font-medium">
              <span>ReAct</span>
            </span>
          )}
        </div>
      )}


      {/* 실행 완료 표시 */}
      {isExecuted && (
        <div className="mt-2 flex items-center text-green-600 text-xs bg-green-50 p-2 rounded border border-green-200">
          <SparklesIcon className="h-3 w-3 mr-1" />
          <span>실행 완료</span>
        </div>
      )}
    </div>
  );
};

export default PromptCard;
