import React from "react";
import {
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import { AVAILABLE_MODELS } from "../../services/api";

const PromptCard = ({
  card,
  onEdit,
  onToggle,
  onDelete,
  isCurrentStep = false,
  isExecuted = false,
  isExecuting = false,
  stepNumber = 0,
  hideDeleteButton = false, // ÏÇ≠Ï†ú Î≤ÑÌäº Ïà®ÍπÄ ÏòµÏÖò Ï∂îÍ∞Ä
}) => {
  const getModelDisplayName = (modelId) => {
    const model = AVAILABLE_MODELS.find((m) => m.id === modelId);
    return model ? model.name : modelId;
  };

  const handleToggleClick = async () => {
    try {
      await onToggle(card.promptId || card.id, !card.enabled);
    } catch (error) {
      console.error("Ïπ¥Îìú ÌÜ†Í∏Ä Ïã§Ìå®:", error);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  const getCardStyles = () => {
    let baseStyles =
      "relative bg-white border border-slate-200 rounded-xl p-4 transition-all duration-300 hover:shadow-lg hover:shadow-slate-200/50 hover:border-slate-300 group";

    if (isCurrentStep) {
      baseStyles += " ring-2 ring-blue-400/30 shadow-xl shadow-blue-100/50 border-blue-200 bg-gradient-to-br from-blue-50/30 to-white";
    } else if (isExecuted) {
      baseStyles += " ring-1 ring-emerald-400/30 border-emerald-200 bg-gradient-to-br from-emerald-50/30 to-white";
    } else if (card.enabled) {
      baseStyles += " hover:ring-1 hover:ring-slate-300/50";
    } else {
      baseStyles += " bg-slate-50/50 border-slate-150 opacity-60 hover:opacity-80";
    }

    return baseStyles;
  };

  const getCategoryInfo = (categoryId) => {
    const categories = {
      instruction: { 
        name: "Ïó≠Ìï† Î∞è Î™©Ìëú", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "üéØ" 
      },
      knowledge: {
        name: "ÏßÄÏãù Î≤†Ïù¥Ïä§",
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80", 
        icon: "üìö"
      },
      secondary: { 
        name: "CoT (ÏÇ¨Í≥† Í≥ºÏ†ï)", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "üß†"
      },
      style_guide: {
        name: "Ïä§ÌÉÄÏùº Í∞ÄÏù¥Îìú",
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "‚ú®"
      },
      validation: { 
        name: "ReAct (Ï∂îÎ°†+ÌñâÎèô)", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "‚ö°"
      },
      enhancement: { 
        name: "ÌíàÏßà Í≤ÄÏ¶ù", 
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "üîç"
      },
    };
    return (
      categories[categoryId] || {
        name: categoryId,
        color: "bg-slate-100/80 text-slate-700 border-slate-200/80",
        icon: "üìù"
      }
    );
  };

  const categoryInfo = getCategoryInfo(card.category);

  return (
    <div className={getCardStyles()}>
      {/* Ïã§Ìñâ ÏÉÅÌÉú ÌëúÏãú */}
      {isExecuting && (
        <div className="absolute -top-1 -right-1 z-10">
          <div className="flex items-center justify-center w-6 h-6 bg-blue-500 text-white rounded-full shadow-lg">
            <div className="animate-spin rounded-full h-3 w-3 border-b border-white"></div>
          </div>
        </div>
      )}

      {/* Ïπ¥Îìú Ìó§Îçî */}
      <div className="flex items-start justify-between mb-3">
        <div className="flex items-center gap-3 flex-1 min-w-0">
          {/* ÏïÑÏù¥ÏΩòÍ≥º Ïπ¥ÌÖåÍ≥†Î¶¨ */}
          <div className={`inline-flex items-center gap-2 px-3 py-1.5 rounded-lg text-sm font-medium border ${categoryInfo.color}`}>
            <span className="text-base leading-none">{categoryInfo.icon}</span>
            <span className="font-medium">{categoryInfo.name}</span>
          </div>
        </div>

        {/* Ïï°ÏÖò Î≤ÑÌäºÎì§ */}
        <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-opacity duration-200">
          {/* ÌôúÏÑ±Ìôî/ÎπÑÌôúÏÑ±Ìôî ÌÜ†Í∏Ä */}
          <button
            onClick={handleToggleClick}
            className={`p-2 rounded-lg transition-all duration-200 ${
              card.enabled
                ? "text-emerald-600 hover:bg-emerald-50 bg-emerald-50/50"
                : "text-slate-400 hover:bg-slate-100"
            }`}
            title={card.enabled ? "ÎπÑÌôúÏÑ±Ìôî" : "ÌôúÏÑ±Ìôî"}
          >
            {card.enabled ? (
              <EyeIcon className="h-4 w-4" />
            ) : (
              <EyeSlashIcon className="h-4 w-4" />
            )}
          </button>

          {/* Ìé∏Ïßë Î≤ÑÌäº */}
          <button
            onClick={handleEdit}
            className="p-2 text-slate-600 hover:bg-blue-50 hover:text-blue-600 rounded-lg transition-all duration-200"
            title="Ìé∏Ïßë"
          >
            <PencilIcon className="h-4 w-4" />
          </button>

          {/* ÏÇ≠Ï†ú Î≤ÑÌäº - hideDeleteButtonÏù¥ trueÎ©¥ Ïà®ÍπÄ */}
          {!hideDeleteButton && (
            <button
              onClick={() => onDelete(card.promptId || card.id)}
              className="p-2 text-slate-400 hover:bg-red-50 hover:text-red-600 rounded-lg transition-all duration-200"
              title="ÏÇ≠Ï†ú"
            >
              <TrashIcon className="h-4 w-4" />
            </button>
          )}
        </div>
      </div>

      {/* ÌîÑÎ°¨ÌîÑÌä∏ ÏÑ§Î™Ö */}
      <div className="text-xs text-slate-500 leading-relaxed mb-3">
        {card.description || categoryInfo.description || "ÌîÑÎ°¨ÌîÑÌä∏ ÏÑ§Î™ÖÏù¥ ÏóÜÏäµÎãàÎã§."}
      </div>

      {/* ÌäπÏàò Í∏∞Îä• ÌëúÏãú */}
      {(card.isCoT || card.isReAct) && (
        <div className="flex gap-2 mb-3">
          {card.isCoT && (
            <span className="inline-flex items-center gap-1 px-2 py-1 bg-purple-50 text-purple-700 rounded-md text-xs font-medium">
              <span>üß†</span>
              Chain of Thought
            </span>
          )}
          {card.isReAct && (
            <span className="inline-flex items-center gap-1 px-2 py-1 bg-orange-50 text-orange-700 rounded-md text-xs font-medium">
              <span>‚ö°</span>
              ReAct Pattern
            </span>
          )}
        </div>
      )}


      {/* Ïã§Ìñâ ÏôÑÎ£å ÌëúÏãú */}
      {isExecuted && (
        <div className="mt-3 flex items-center text-green-600 text-sm bg-green-50 p-2 rounded-lg border border-green-200">
          <SparklesIcon className="h-4 w-4 mr-2" />
          Ïã§Ìñâ ÏôÑÎ£å
        </div>
      )}
    </div>
  );
};

export default PromptCard;
