import React, { useState, useEffect, useCallback, useRef } from 'react';
import { toast } from 'react-hot-toast';
import {
  PaperAirplaneIcon,
  ArrowPathIcon,
  DocumentDuplicateIcon,
  PlusIcon,
  BookOpenIcon,
  Cog6ToothIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XMarkIcon,
  CloudArrowUpIcon,
  DocumentTextIcon,
  TrashIcon,
  PencilIcon,
  Bars3Icon,
} from '@heroicons/react/24/outline';
import { orchestrationAPI, promptCardAPI } from '../../services/api';

// 메시지 컴포넌트
const Message = ({ message, onCopy }) => {
  const isUser = message.type === 'user';

  return (
    <div className={`group relative ${isUser ? 'ml-12' : 'mr-12'} mb-6`}>
      <div className={`flex ${isUser ? 'justify-end' : 'justify-start'}`}>
        <div className={`
          max-w-[80%] rounded-lg px-4 py-3 relative shadow-sm
          ${isUser 
            ? 'bg-blue-600 text-white' 
            : 'bg-white text-gray-800 border border-gray-200'
          }
        `}>
          {/* 메시지 내용 */}
          <div className="whitespace-pre-wrap text-sm leading-relaxed">
            {message.content}
          </div>

          {/* 제목 복사 버튼들 */}
          {message.titles && (
            <div className="mt-3 pt-3 border-t border-gray-200">
              <div className="flex flex-wrap gap-2">
                {message.titles.map((title, index) => (
                  <button
                    key={index}
                    onClick={() => onCopy(title)}
                    className="flex items-center px-2 py-1 bg-gray-100 hover:bg-gray-200 rounded text-xs text-gray-700 transition-colors"
                  >
                    <DocumentDuplicateIcon className="h-3 w-3 mr-1" />
                    제목 {index + 1} 복사
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* 타임스탬프 */}
          <div className={`text-xs mt-2 ${isUser ? 'text-blue-100' : 'text-gray-500'}`}>
            {message.timestamp.toLocaleTimeString('ko-KR', {
              hour: 'numeric',
              minute: '2-digit'
            })}
          </div>

          {/* 로딩 상태 */}
          {message.isLoading && (
            <div className="flex items-center text-blue-600 mt-2">
              <ArrowPathIcon className="h-4 w-4 mr-2 animate-spin" />
              <span className="text-sm">AI가 응답을 생성하고 있습니다...</span>
            </div>
          )}

          {/* 에러 상태 */}
          {message.isError && (
            <div className="flex items-center text-red-600 mt-2">
              <ExclamationTriangleIcon className="h-4 w-4 mr-2" />
              <span className="text-sm">오류가 발생했습니다</span>
            </div>
          )}
        </div>
      </div>

      {/* 사용자 아바타 */}
      {isUser && (
        <div className="absolute top-0 right-0 -mr-10 w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center text-sm text-white font-medium">
          U
        </div>
      )}

      {/* AI 아바타 */}
      {!isUser && (
        <div className="absolute top-0 left-0 -ml-10 w-8 h-8 bg-green-600 rounded-full flex items-center justify-center text-sm text-white font-medium">
          AI
        </div>
      )}
    </div>
  );
};

// 파일 업로드 컴포넌트
const FileUploadArea = ({ projectId, onFileUploaded }) => {
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const fileInputRef = useRef(null);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files);
    handleFileUpload(files);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    handleFileUpload(files);
  };

  const handleFileUpload = async (files) => {
    if (!files.length) return;

    setUploading(true);
    try {
      for (const file of files) {
        // TODO: S3 업로드 및 벡터 임베딩 API 호출
        toast.success(`${file.name} 업로드 완료`);
      }
      onFileUploaded?.();
    } catch (error) {
      console.error('파일 업로드 실패:', error);
      toast.error('파일 업로드에 실패했습니다');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div
      className={`
        border-2 border-dashed rounded-lg p-6 text-center transition-colors
        ${isDragging 
          ? 'border-blue-500 bg-blue-50' 
          : 'border-gray-300 hover:border-gray-400'
        }
      `}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <input
        ref={fileInputRef}
        type="file"
        multiple
        accept=".txt,.md,.pdf,.doc,.docx"
        onChange={handleFileSelect}
        className="hidden"
      />

      <CloudArrowUpIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />

      {uploading ? (
        <div className="text-blue-600">
          <ArrowPathIcon className="h-5 w-5 animate-spin mx-auto mb-2" />
          <p className="text-sm">파일을 업로드하고 있습니다...</p>
        </div>
      ) : (
        <>
          <p className="text-gray-600 mb-2">
            파일을 여기에 드래그하거나
          </p>
          <button
            onClick={() => fileInputRef.current?.click()}
            className="text-blue-600 hover:text-blue-800 font-medium"
          >
            기기에서 업로드
          </button>
          <p className="text-xs text-gray-500 mt-2">
            .txt, .md, .pdf, .doc, .docx 파일 지원
          </p>
        </>
      )}
    </div>
  );
};

// 직접 텍스트 입력 컴포넌트
const TextInputArea = ({ onTextSubmit }) => {
  const [text, setText] = useState('');
  const [title, setTitle] = useState('');

  const handleSubmit = () => {
    if (!text.trim() || !title.trim()) {
      toast.error('제목과 내용을 모두 입력해주세요');
      return;
    }

    onTextSubmit({ title, content: text });
    setText('');
    setTitle('');
    toast.success('텍스트가 추가되었습니다');
  };

  return (
    <div className="space-y-3">
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="제목을 입력하세요"
        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="내용을 입력하세요..."
        rows={6}
        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
      />
      <button
        onClick={handleSubmit}
        disabled={!text.trim() || !title.trim()}
        className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
      >
        텍스트 추가
      </button>
    </div>
  );
};

// 프로젝트 지식 사이드바 컴포넌트
const ProjectKnowledgeSidebar = ({ projectId, promptCards, onCardsChanged, isOpen, onClose }) => {
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('knowledge'); // 'knowledge' or 'prompts'

  const handleCardToggle = async (cardId, enabled) => {
    try {
      setLoading(true);
      await promptCardAPI.updatePromptCard(cardId, { enabled });
      onCardsChanged?.();
      toast.success(enabled ? '카드가 활성화되었습니다' : '카드가 비활성화되었습니다');
    } catch (error) {
      console.error('카드 상태 변경 실패:', error);
      toast.error('카드 상태 변경에 실패했습니다');
    } finally {
      setLoading(false);
    }
  };

  const categorizedCards = promptCards.reduce((acc, card) => {
    const category = card.category || 'other';
    if (!acc[category]) acc[category] = [];
    acc[category].push(card);
    return acc;
  }, {});

  const categoryNames = {
    role: '역할 정의',
    guideline: '작성 가이드라인', 
    workflow: '워크플로우',
    output_format: '출력 형식',
    few_shot: '예시 템플릿',
    scoring: '품질 평가',
    other: '기타 설정'
  };

  const categoryIcons = {
    role: '🎭',
    guideline: '📋',
    workflow: '🔄',
    output_format: '📝',
    few_shot: '💡',
    scoring: '⭐',
    other: '⚙️'
  };

  return (
    <div className={`
      fixed md:relative top-0 right-0 h-full w-80 bg-gray-850 border-l border-gray-700 
      transform transition-transform duration-300 ease-in-out z-50
      ${isOpen ? 'translate-x-0' : 'translate-x-full md:translate-x-0'}
    `}>
      {/* 사이드바 헤더 */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <div className="flex items-center space-x-2">
          <BookOpenIcon className="h-5 w-5 text-gray-400" />
          <h3 className="text-sm font-medium text-gray-200">프로젝트 지식</h3>
        </div>
        <button
          onClick={onClose}
          className="md:hidden p-1 text-gray-400 hover:text-gray-200 rounded"
        >
          <XMarkIcon className="h-4 w-4" />
        </button>
      </div>

      {/* 카드 상태 요약 */}
      <div className="p-4 border-b border-gray-700">
        <div className="bg-gray-800 rounded-lg p-3">
          <div className="text-xs text-gray-400 mb-1">프롬프트 상태</div>
          <div className="text-sm text-gray-200">
            <span className="text-green-400">{promptCards.filter(c => c.enabled).length}개 활성</span>
            {' / '}
            <span className="text-gray-400">{promptCards.length}개 총</span>
          </div>
          <div className="w-full bg-gray-700 rounded-full h-1.5 mt-2">
            <div 
              className="bg-orange-500 h-1.5 rounded-full transition-all duration-300" 
              style={{ width: `${(promptCards.filter(c => c.enabled).length / Math.max(promptCards.length, 1)) * 100}%` }}
            />
          </div>
        </div>
      </div>

      {/* 프롬프트 카드 목록 */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {Object.entries(categorizedCards).map(([category, cards]) => (
          <div key={category}>
            <div className="flex items-center space-x-2 mb-3">
              <span className="text-lg">{categoryIcons[category] || '⚙️'}</span>
              <h4 className="text-xs font-medium text-gray-400 uppercase tracking-wider">
                {categoryNames[category] || category}
              </h4>
            </div>
            <div className="space-y-2 ml-7">
              {cards.map((card) => (
                <div
                  key={card.id}
                  className={`
                    p-3 rounded-lg border transition-all duration-200 cursor-pointer
                    ${card.enabled
                      ? 'border-orange-600 bg-orange-950/30'
                      : 'border-gray-700 bg-gray-800/50 hover:bg-gray-800'
                    }
                  `}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center space-x-2 mb-1">
                        <h5 className="text-sm font-medium text-gray-200 truncate">
                          {card.title}
                        </h5>
                        {card.enabled && (
                          <CheckCircleIcon className="h-3 w-3 text-green-400 flex-shrink-0" />
                        )}
                      </div>
                      {card.description && (
                        <p className="text-xs text-gray-400 line-clamp-2 mb-2">
                          {card.description}
                        </p>
                      )}
                      <div className="flex items-center space-x-2 text-xs text-gray-500">
                        <span>{card.model_name || 'Claude'}</span>
                      </div>
                    </div>
                    <button
                      onClick={() => handleCardToggle(card.id, !card.enabled)}
                      disabled={loading}
                      className={`
                        ml-3 w-8 h-4 rounded-full transition-colors duration-200 focus:outline-none relative
                        ${card.enabled ? 'bg-orange-600' : 'bg-gray-600'}
                        ${loading ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}
                      `}
                    >
                      <div className={`
                        w-3 h-3 bg-white rounded-full shadow transform transition-transform duration-200 absolute top-0.5
                        ${card.enabled ? 'translate-x-4' : 'translate-x-0.5'}
                      `} />
