import React, { useState } from "react";
import {
  SparklesIcon,
  DocumentTextIcon,
  ExclamationTriangleIcon,
  ClockIcon,
  InformationCircleIcon,
  CheckCircleIcon,
  Cog8ToothIcon,
  CpuChipIcon,
  AdjustmentsHorizontalIcon,
  PlayIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import { AVAILABLE_MODELS } from "../services/api";

const ArticleInput = ({
  canGenerate,
  isGenerating,
  onGenerate,
  executionProgress,
}) => {
  const [article, setArticle] = useState("");
  const [wordCount, setWordCount] = useState(0);
  const [showAISettings, setShowAISettings] = useState(false);
  const [aiSettings, setAiSettings] = useState({
    model: "us.anthropic.claude-3-5-sonnet-20241022-v2:0",
    temperature: 0.7,
    maxTokens: 4000,
  });

  const handleArticleChange = (e) => {
    const text = e.target.value;
    setArticle(text);
    setWordCount(
      text
        .trim()
        .split(/\s+/)
        .filter((word) => word.length > 0).length
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!article.trim()) {
      alert("기사 내용을 입력해주세요.");
      return;
    }
    if (article.length < 100) {
      alert("더 자세한 기사 내용을 입력해주세요. (최소 100자)");
      return;
    }

    try {
      await onGenerate(article);
    } catch (error) {
      console.error("제목 생성 실패:", error);
    }
  };

  const getProgressSteps = () => {
    return [
      {
        id: "fetch_prompts",
        name: "프롬프트 조회",
        description: "설정된 프롬프트 카드를 가져옵니다",
        icon: DocumentTextIcon,
      },
      {
        id: "build_payload",
        name: "페이로드 구성",
        description: "AI 모델 입력 데이터를 준비합니다",
        icon: Cog8ToothIcon,
      },
      {
        id: "call_bedrock",
        name: "AI 모델 호출",
        description: "Bedrock AI 모델이 제목을 생성합니다",
        icon: SparklesIcon,
      },
      {
        id: "save_results",
        name: "결과 저장",
        description: "생성된 제목과 메타데이터를 저장합니다",
        icon: CheckCircleIcon,
      },
    ];
  };

  const getCurrentStepIndex = () => {
    if (!executionProgress) return -1;

    switch (executionProgress.status) {
      case "started":
        return 0;
      case "processing":
        return 1;
      case "generating":
        return 2;
      case "saving":
        return 3;
      case "completed":
        return 4;
      default:
        return -1;
    }
  };

  const getStepStatus = (stepIndex) => {
    const currentStep = getCurrentStepIndex();

    if (executionProgress?.status === "failed") {
      return stepIndex <= currentStep ? "failed" : "pending";
    }

    if (stepIndex < currentStep) return "completed";
    if (stepIndex === currentStep) return "active";
    return "pending";
  };

  const getStepIcon = (step, status) => {
    const IconComponent = step.icon;

    switch (status) {
      case "completed":
        return <CheckCircleIcon className="h-5 w-5 text-green-600" />;
      case "active":
        return (
          <IconComponent className="h-5 w-5 text-blue-600 animate-pulse" />
        );
      case "failed":
        return <XMarkIcon className="h-5 w-5 text-red-600" />;
      default:
        return <IconComponent className="h-5 w-5 text-gray-400" />;
    }
  };

  const selectedModel = AVAILABLE_MODELS.find((m) => m.id === aiSettings.model);

  return (
    <div className="space-y-6">
      {/* AI 설정 패널 */}
      <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 overflow-hidden">
        <button
          onClick={() => setShowAISettings(!showAISettings)}
          className="w-full px-6 py-4 flex items-center justify-between hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors"
        >
          <div className="flex items-center space-x-3">
            <AdjustmentsHorizontalIcon className="h-5 w-5 text-gray-600 dark:text-gray-400" />
            <div className="text-left">
              <h3 className="font-medium text-gray-900 dark:text-white">
                AI 모델 설정
              </h3>
              <p className="text-sm text-gray-500 dark:text-gray-400">
                {selectedModel?.name} • 온도: {aiSettings.temperature} • 최대
                토큰: {aiSettings.maxTokens}
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-500 dark:text-gray-400">
              {showAISettings ? "접기" : "펼치기"}
            </span>
            <div
              className={`transform transition-transform ${
                showAISettings ? "rotate-180" : ""
              }`}
            >
              <svg
                className="h-5 w-5 text-gray-400"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </div>
          </div>
        </button>

        {showAISettings && (
          <div className="px-6 pb-6 border-t border-gray-200 dark:border-gray-700 bg-gray-50 dark:bg-gray-800/50">
            <div className="pt-4 space-y-6">
              {/* 모델 선택 */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  AI 모델
                </label>
                <select
                  value={aiSettings.model}
                  onChange={(e) =>
                    setAiSettings((prev) => ({
                      ...prev,
                      model: e.target.value,
                    }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                >
                  {AVAILABLE_MODELS.map((model) => (
                    <option key={model.id} value={model.id}>
                      {model.name}
                    </option>
                  ))}
                </select>
                {selectedModel && (
                  <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    {selectedModel.description}
                  </p>
                )}
              </div>

              {/* 온도 설정 */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  창의성 (Temperature): {aiSettings.temperature}
                </label>
                <div className="flex items-center space-x-4">
                  <span className="text-sm text-gray-500 dark:text-gray-400">
                    보수적
                  </span>
                  <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={aiSettings.temperature}
                    onChange={(e) =>
                      setAiSettings((prev) => ({
                        ...prev,
                        temperature: parseFloat(e.target.value),
                      }))
                    }
                    className="flex-1 h-2 bg-gray-200 dark:bg-gray-600 rounded-lg appearance-none cursor-pointer slider"
                  />
                  <span className="text-sm text-gray-500 dark:text-gray-400">
                    창의적
                  </span>
                </div>
                <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">
                  낮을수록 일관된 결과, 높을수록 창의적인 결과를 생성합니다
                </p>
              </div>

              {/* 최대 토큰 설정 */}
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  최대 토큰 수
                </label>
                <input
                  type="number"
                  min="1000"
                  max={selectedModel?.maxTokens || 200000}
                  value={aiSettings.maxTokens}
                  onChange={(e) =>
                    setAiSettings((prev) => ({
                      ...prev,
                      maxTokens: parseInt(e.target.value),
                    }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                />
                <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">
                  생성할 수 있는 최대 토큰 수 (대략 단어 수의 3/4)
                </p>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* 제목 생성 안내 */}
      <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-6">
        <h3 className="text-lg font-semibold text-blue-900 dark:text-blue-100 mb-3 flex items-center">
          <InformationCircleIcon className="h-5 w-5 mr-2" />
          제목 생성 안내
        </h3>
        <div className="space-y-2 text-sm text-blue-800 dark:text-blue-200">
          <p>
            • 기사 원문을 입력하시면 설정된 프롬프트 카드를 바탕으로 AI가 최적의
            제목을 생성합니다
          </p>
          <p>
            • 생성 과정은 4단계로 진행되며, 각 단계별 진행 상황을 실시간으로
            확인할 수 있습니다
          </p>
          <p>• 최소 100자 이상의 기사 내용을 입력해주세요</p>
          <p>• 더 자세한 기사 내용일수록 더 정확한 제목이 생성됩니다</p>
        </div>
      </div>

      {/* 제목 생성 가능 여부 표시 */}
      {!canGenerate && (
        <div className="bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-lg p-4">
          <div className="flex items-center">
            <ExclamationTriangleIcon className="h-5 w-5 text-yellow-600 dark:text-yellow-400 mr-2" />
            <p className="text-sm font-medium text-yellow-800 dark:text-yellow-200">
              제목 생성을 위해서는 먼저 프롬프트 카드를 설정해야 합니다.
            </p>
          </div>
        </div>
      )}

      {/* 진행 상황 표시 */}
      {isGenerating && (
        <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 p-6">
          <div className="flex items-center mb-4">
            <Cog8ToothIcon className="h-5 w-5 text-blue-600 dark:text-blue-400 animate-spin mr-2" />
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              제목 생성 중...
            </h3>
          </div>

          <div className="space-y-4">
            {getProgressSteps().map((step, index) => {
              const status = getStepStatus(index);
              return (
                <div
                  key={step.id}
                  className={`flex items-center space-x-4 p-3 rounded-lg ${
                    status === "active"
                      ? "bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800"
                      : status === "completed"
                      ? "bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800"
                      : status === "failed"
                      ? "bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800"
                      : "bg-gray-50 dark:bg-gray-700/50 border border-gray-200 dark:border-gray-600"
                  }`}
                >
                  <div className="flex-shrink-0">
                    {getStepIcon(step, status)}
                  </div>
                  <div className="flex-1">
                    <h4
                      className={`font-medium ${
                        status === "active"
                          ? "text-blue-900 dark:text-blue-100"
                          : status === "completed"
                          ? "text-green-900 dark:text-green-100"
                          : status === "failed"
                          ? "text-red-900 dark:text-red-100"
                          : "text-gray-700 dark:text-gray-300"
                      }`}
                    >
                      {step.name}
                    </h4>
                    <p
                      className={`text-sm ${
                        status === "active"
                          ? "text-blue-700 dark:text-blue-200"
                          : status === "completed"
                          ? "text-green-700 dark:text-green-200"
                          : status === "failed"
                          ? "text-red-700 dark:text-red-200"
                          : "text-gray-500 dark:text-gray-400"
                      }`}
                    >
                      {step.description}
                    </p>
                  </div>
                  {status === "active" && (
                    <div className="flex-shrink-0">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 dark:border-blue-400"></div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {executionProgress?.message && (
            <div className="mt-4 p-3 bg-gray-100 dark:bg-gray-700 rounded-lg">
              <p className="text-sm text-gray-700 dark:text-gray-300">
                {executionProgress.message}
              </p>
            </div>
          )}
        </div>
      )}

      {/* 기사 입력 폼 */}
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="bg-white dark:bg-gray-800 rounded-lg border border-gray-200 dark:border-gray-700 p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white flex items-center">
              <DocumentTextIcon className="h-5 w-5 mr-2 text-blue-600 dark:text-blue-400" />
              기사 내용 입력
            </h3>
            <div className="flex items-center space-x-4 text-sm text-gray-500 dark:text-gray-400">
              <span>{wordCount} 단어</span>
              <span>{article.length} 글자</span>
            </div>
          </div>

          <textarea
            value={article}
            onChange={handleArticleChange}
            placeholder="제목을 생성할 기사 내용을 입력해주세요. 최소 100자 이상 입력해야 합니다."
            className="w-full h-64 px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg resize-none focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
            disabled={isGenerating}
          />

          <div className="mt-4 flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {article.length >= 100 ? (
                <CheckCircleIcon className="h-5 w-5 text-green-600 dark:text-green-400" />
              ) : (
                <ClockIcon className="h-5 w-5 text-yellow-600 dark:text-yellow-400" />
              )}
              <span
                className={`text-sm ${
                  article.length >= 100
                    ? "text-green-600 dark:text-green-400"
                    : "text-yellow-600 dark:text-yellow-400"
                }`}
              >
                {article.length >= 100
                  ? "입력 완료"
                  : `${100 - article.length}자 더 입력해주세요`}
              </span>
            </div>

            <button
              type="submit"
              disabled={
                !canGenerate ||
                !article.trim() ||
                article.length < 100 ||
                isGenerating
              }
              className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors font-medium"
            >
              {isGenerating ? (
                <>
                  <Cog8ToothIcon className="h-5 w-5 mr-2 animate-spin" />
                  생성 중...
                </>
              ) : (
                <>
                  <PlayIcon className="h-5 w-5 mr-2" />
                  제목 생성하기
                </>
              )}
            </button>
          </div>
        </div>
      </form>

      {/* 팁 */}
      <div className="bg-gray-50 dark:bg-gray-800/50 rounded-lg p-4 border border-gray-200 dark:border-gray-700">
        <h4 className="font-medium text-gray-900 dark:text-white mb-2 flex items-center">
          <InformationCircleIcon className="h-4 w-4 mr-1" />
          💡 더 나은 제목을 위한 팁
        </h4>
        <ul className="text-sm text-gray-600 dark:text-gray-400 space-y-1">
          <li>• 기사의 핵심 내용과 주요 키워드를 포함해주세요</li>
          <li>• 구체적인 수치나 데이터가 있다면 함께 입력해주세요</li>
          <li>• 기사의 톤앤매너(긍정적/부정적/중립적)를 명확히 해주세요</li>
          <li>• 대상 독자층을 고려한 내용을 작성해주세요</li>
        </ul>
      </div>
    </div>
  );
};

export default ArticleInput;
