import React, { useState, useEffect, useMemo, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  FolderOpenIcon,
  PlusIcon,
  EllipsisHorizontalIcon,
  TrashIcon,
  PencilIcon,
  CalendarIcon,
  DocumentTextIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  Squares2X2Icon,
  ListBulletIcon,
  TagIcon,
  XMarkIcon,
  ChevronDownIcon,
  CheckIcon,
} from "@heroicons/react/24/outline";
import { projectAPI, handleAPIError, filterProjects } from "../services/api";
import CreateProject from "./CreateProject";
import { usePrefetch } from "../hooks/usePrefetch";
import { ProjectListSkeleton } from "./skeleton/SkeletonComponents";

const ProjectList = () => {
  const navigate = useNavigate();
  const { prefetchProjectDetail, prefetchCreateProject } = usePrefetch();

  // ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("updated");
  const [viewMode, setViewMode] = useState("grid");
  const [sortDropdownOpen, setSortDropdownOpen] = useState(false);
  const sortDropdownRef = useRef(null);

  // ÌîÑÎ°úÏ†ùÌä∏ Ìé∏Ïßë ÏÉÅÌÉú
  const [editingProject, setEditingProject] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);

  useEffect(() => {
    loadProjects();
  }, []);

  // ÎìúÎ°≠Îã§Ïö¥ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        sortDropdownRef.current &&
        !sortDropdownRef.current.contains(event.target)
      ) {
        setSortDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const loadProjects = async () => {
    try {
      console.log("üîÑ loadProjects ÏãúÏûë");
      setLoading(true);
      setError(null);
      const data = await projectAPI.getProjects();
      const projectsWithStats = data.projects || [];
      console.log("üìä Î°úÎìúÎêú ÌîÑÎ°úÏ†ùÌä∏ Ïàò:", projectsWithStats.length);
      console.log("üìã ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù:", projectsWithStats);

      setProjects(projectsWithStats);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteProject = async (projectId, projectName) => {
    if (!window.confirm(`"${projectName}" ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
      return;
    }

    // ÎÇôÍ¥ÄÏ†Å ÏóÖÎç∞Ïù¥Ìä∏: Ï¶âÏãú UIÏóêÏÑú ÌîÑÎ°úÏ†ùÌä∏ Ï†úÍ±∞
    const originalProjects = [...projects];
    const updatedProjects = projects.filter((p) => p.projectId !== projectId);
    setProjects(updatedProjects);

    // Ï¶âÏãú ÏÑ±Í≥µ ÌÜ†Ïä§Ìä∏ ÌëúÏãú
    toast.success("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§");

    try {
      // Î∞±Í∑∏ÎùºÏö¥ÎìúÏóêÏÑú Ïã§Ï†ú ÏÇ≠Ï†ú ÏßÑÌñâ
      await projectAPI.deleteProject(projectId);
      console.log("ÌîÑÎ°úÏ†ùÌä∏ ÏÇ≠Ï†ú ÏÑ±Í≥µ:", projectId);
    } catch (err) {
      console.error("ÌîÑÎ°úÏ†ùÌä∏ ÏÇ≠Ï†ú Ïò§Î•ò:", err);

      // Ïã§Ìå® Ïãú ÏõêÎûò ÏÉÅÌÉúÎ°ú Î≥µÏõê
      setProjects(originalProjects);

      let errorMessage = "ÌîÑÎ°úÏ†ùÌä∏ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§";

      if (err.response?.status === 403) {
        errorMessage =
          "ÏÇ≠Ï†ú Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§. S3 ÌååÏùº ÏÇ≠Ï†ú Í∂åÌïúÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.";
      } else if (err.response?.status === 404) {
        errorMessage = "Ïù¥ÎØ∏ ÏÇ≠Ï†úÎêú ÌîÑÎ°úÏ†ùÌä∏ÏûÖÎãàÎã§";
        // 404Ïùò Í≤ΩÏö∞ Ïã§Ï†úÎ°úÎäî ÏÇ≠Ï†úÎêú Í≤ÉÏù¥ÎØÄÎ°ú Î≥µÏõêÌïòÏßÄ ÏïäÏùå
        return;
      } else if (err.response) {
        errorMessage =
          err.response.data?.message || `ÏÑúÎ≤Ñ Ïò§Î•ò (${err.response.status})`;
      } else if (err.request) {
        errorMessage = "ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•ò: CORS ÎòêÎäî Ïó∞Í≤∞ Î¨∏Ï†ú";
      }

      toast.error(errorMessage);
    }
  };

  // ÌîÑÎ°úÏ†ùÌä∏ Ìé∏Ïßë Ìï®ÏàòÎì§
  const handleEditProject = (project) => {
    setEditingProject(project);
    setShowEditModal(true);
  };

  const handleUpdateProject = async (projectData) => {
    try {
      await projectAPI.updateProject(editingProject.projectId, projectData);
      toast.success("ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§");
      setShowEditModal(false);
      setEditingProject(null);
      loadProjects();
    } catch (err) {
      const errorInfo = handleAPIError(err);
      toast.error(`ÌîÑÎ°úÏ†ùÌä∏ ÏàòÏ†ï Ïã§Ìå®: ${errorInfo.message}`);
    }
  };

  const handleCancelEdit = () => {
    setShowEditModal(false);
    setEditingProject(null);
  };

  // ÌïÑÌÑ∞ÎßÅÎêú ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù
  const filteredProjects = useMemo(() => {
    return filterProjects(projects, {
      searchQuery,
      sortBy,
    });
  }, [projects, searchQuery, sortBy]);

  // Ï†ïÎ†¨ ÏòµÏÖò Îç∞Ïù¥ÌÑ∞
  const sortOptions = [
    { value: "created", label: "ÏÉùÏÑ±ÏùºÏàú" },
    { value: "updated", label: "ÏàòÏ†ïÏùºÏàú" },
    { value: "name", label: "Ïù¥Î¶ÑÏàú" },
  ];

  const currentSortOption = sortOptions.find(
    (option) => option.value === sortBy
  );

  if (loading) {
    return <ProjectListSkeleton />;
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4">‚ö†Ô∏è Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§</div>
        <p className="text-gray-600 mb-6">{error}</p>
        <button
          onClick={loadProjects}
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
        >
          Îã§Ïãú ÏãúÎèÑ
        </button>
      </div>
    );
  }

  const handleCreateSuccess = () => {
    console.log("üìù handleCreateSuccess Ìò∏Ï∂úÎê® - ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ® ÏãúÏûë");
    loadProjects(); // ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
  };

  return (
    <div className="space-y-6">
      {/* Ìó§Îçî */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù</h2>
          <p className="text-gray-600 mt-1">
            AI Ï†úÎ™© ÏÉùÏÑ± ÌîÑÎ°úÏ†ùÌä∏Î•º Í¥ÄÎ¶¨ÌïòÍ≥† ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÉùÏÑ±ÌïòÏÑ∏Ïöî
          </p>
        </div>
      </div>

      {/* ÌïÑÌÑ∞ÎßÅ Î∞î */}
      <div className="bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 p-6 shadow-sm transition-colors duration-200">
        <div className="flex flex-col sm:flex-row gap-4">
          {/* Í≤ÄÏÉâÎ∞î */}
          <div className="flex-1 relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ, ÏÑ§Î™Ö, ÌÉúÍ∑∏Î°ú Í≤ÄÏÉâ..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors duration-200"
            />
          </div>

          {/* Ï†ïÎ†¨ ÏòµÏÖò - Ïª§Ïä§ÌÖÄ ÎìúÎ°≠Îã§Ïö¥ */}
          <div className="flex items-center space-x-3">
            <FunnelIcon className="h-5 w-5 text-gray-400 flex-shrink-0" />
            <div className="relative" ref={sortDropdownRef}>
              <button
                onClick={() => setSortDropdownOpen(!sortDropdownOpen)}
                className="flex items-center justify-between pl-4 pr-3 py-3 bg-white dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded-lg hover:border-gray-400 dark:hover:border-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 min-w-[140px]"
              >
                <div className="flex items-center space-x-2">
                  <span className="text-gray-700 dark:text-gray-300 font-medium">
                    {currentSortOption?.label}
                  </span>
                </div>
                <ChevronDownIcon
                  className={`h-4 w-4 text-gray-400 transition-transform duration-200 ${
                    sortDropdownOpen ? "rotate-180" : ""
                  }`}
                />
              </button>

              {/* ÎìúÎ°≠Îã§Ïö¥ Î©îÎâ¥ */}
              {sortDropdownOpen && (
                <div className="absolute top-full left-0 mt-1 w-full bg-white dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg shadow-lg z-50 overflow-hidden">
                  {sortOptions.map((option) => (
                    <button
                      key={option.value}
                      onClick={() => {
                        setSortBy(option.value);
                        setSortDropdownOpen(false);
                      }}
                      className={`w-full flex items-center justify-between px-4 py-3 text-left hover:bg-gray-50 dark:hover:bg-gray-600 transition-colors duration-150 ${
                        sortBy === option.value
                          ? "bg-blue-50 dark:bg-blue-900 text-blue-600 dark:text-blue-400"
                          : "text-gray-700 dark:text-gray-300"
                      }`}
                    >
                      <div className="flex items-center space-x-2">
                        <span className="font-medium">{option.label}</span>
                      </div>
                      {sortBy === option.value && (
                        <CheckIcon className="h-4 w-4 text-blue-600" />
                      )}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Î∑∞ Î™®Îìú */}
          <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setViewMode("grid")}
              className={`p-2 rounded-md ${
                viewMode === "grid" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <Squares2X2Icon className="h-4 w-4" />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-md ${
                viewMode === "list" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <ListBulletIcon className="h-4 w-4" />
            </button>
          </div>

          {/* ÏÉà ÌîÑÎ°úÏ†ùÌä∏ Î≤ÑÌäº */}
          <button
            onClick={() => setShowCreateModal(true)}
            onMouseEnter={prefetchCreateProject}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
          >
            <PlusIcon className="h-4 w-4 mr-2" />ÏÉà ÌîÑÎ°úÏ†ùÌä∏
          </button>
        </div>
      </div>

      {/* ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù */}
      {filteredProjects.length === 0 ? (
        <div className="text-center py-16 bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 transition-colors duration-200">
          <FolderOpenIcon className="mx-auto h-16 w-16 text-gray-400 mb-4" />
          <h3 className="text-xl font-medium text-gray-900 dark:text-white mb-2">
            {searchQuery
              ? "Ï°∞Í±¥Ïóê ÎßûÎäî ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§"
              : "ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§"}
          </h3>
          <p className="text-gray-500 dark:text-gray-400 mb-8">
            {searchQuery
              ? "Îã§Î•∏ Ï°∞Í±¥ÏúºÎ°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî"
              : "Ï≤´ Î≤àÏß∏ ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÉùÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî"}
          </p>
          <button
            onClick={() => setShowCreateModal(true)}
            onMouseEnter={prefetchCreateProject}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-medium"
          >
            <PlusIcon className="h-4 w-4 mr-2" />ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
          </button>
        </div>
      ) : (
        <div
          className={
            viewMode === "grid"
              ? "grid gap-6 md:grid-cols-2 lg:grid-cols-3"
              : "space-y-4"
          }
        >
          {filteredProjects.map((project) => (
            <ProjectCard
              key={project.projectId}
              project={project}
              onDelete={deleteProject}
              onEdit={handleEditProject}
              viewMode={viewMode}
              navigate={navigate}
            />
          ))}
        </div>
      )}

      {/* ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
      <CreateProject
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSuccess={handleCreateSuccess}
      />

      {/* ÌîÑÎ°úÏ†ùÌä∏ Ìé∏Ïßë Î™®Îã¨ */}
      <ProjectEditModal
        project={editingProject}
        isOpen={showEditModal}
        onSave={handleUpdateProject}
        onCancel={handleCancelEdit}
      />
    </div>
  );
};

// ÌîÑÎ°úÏ†ùÌä∏ Ïπ¥Îìú Ïª¥Ìè¨ÎÑåÌä∏
const ProjectCard = ({ project, onDelete, onEdit, viewMode, navigate }) => {
  const [showMenu, setShowMenu] = useState(false);
  const menuRef = useRef(null);

  // Ïπ¥Îìú ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleCardClick = (e) => {
    // Î©îÎâ¥ Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠Ìïú Í≤ΩÏö∞ Î¨¥Ïãú
