// ÌîÑÎ°úÏ†ùÌä∏ Ìé∏Ïßë Î™®Îã¨ Ïª¥Ìè¨ÎÑåÌä∏
const ProjectEditModal = ({ project, isOpen, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    tags: [],
  });
  const [tagInput, setTagInput] = useState("");

  useEffect(() => {
    if (project) {
      setFormData({
        name: project.name || "",
        description: project.description || "",
        tags: Array.isArray(project.tags) ? [...project.tags] : [],
      });
    }
  }, [project]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      toast.error("ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
      return;
    }
    onSave(formData);
  };

  const handleModalClose = () => {
    setTagInput("");
    onCancel();
  };

  // ÌÉúÍ∑∏ Ï∂îÍ∞Ä Ìï®Ïàò
  const addTag = () => {
    const tag = tagInput.trim();
    if (tag && !formData.tags.includes(tag) && formData.tags.length < 10) {
      setFormData({
        ...formData,
        tags: [...formData.tags, tag],
      });
      setTagInput("");
    }
  };

  // ÌÉúÍ∑∏ Ï†úÍ±∞ Ìï®Ïàò
  const removeTag = (tagToRemove) => {
    setFormData({
      ...formData,
      tags: formData.tags.filter((tag) => tag !== tagToRemove),
    });
  };

  // Enter ÌÇ§Î°ú ÌÉúÍ∑∏ Ï∂îÍ∞Ä
  const handleTagKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addTag();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-md w-full">
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">ÌîÑÎ°úÏ†ùÌä∏ Ìé∏Ïßë</h3>
          <button
            onClick={handleModalClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <XMarkIcon className="h-5 w-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-4 space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ *
            </label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ÏÑ§Î™Ö
            </label>
            <textarea
              value={formData.description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="ÌîÑÎ°úÏ†ùÌä∏ ÏÑ§Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            />
          </div>

          {/* ÌÉúÍ∑∏ Í¥ÄÎ¶¨ */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ÌÉúÍ∑∏ ({formData.tags.length}/10)
            </label>

            {/* ÌòÑÏû¨ ÌÉúÍ∑∏Îì§ */}
            {formData.tags.length > 0 && (
              <div className="flex flex-wrap gap-2 mb-3">
                {formData.tags.map((tag, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-800"
                  >
                    {tag}
                    <button
                      type="button"
                      onClick={() => removeTag(tag)}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >
                      <XMarkIcon className="h-3 w-3" />
                    </button>
                  </span>
                ))}
              </div>
            )}

            {/* ÌÉúÍ∑∏ ÏûÖÎ†• */}
            <div className="flex gap-2">
              <input
                type="text"
                value={tagInput}
                onChange={(e) => setTagInput(e.target.value)}
                onKeyPress={handleTagKeyPress}
                className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="ÌÉúÍ∑∏Î•º ÏûÖÎ†•ÌïòÍ≥† EnterÎ•º ÎàÑÎ•¥ÏÑ∏Ïöî"
                maxLength={20}
                disabled={formData.tags.length >= 10}
              />
              <button
                type="button"
                onClick={addTag}
                disabled={!tagInput.trim() || formData.tags.length >= 10}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Ï∂îÍ∞Ä
              </button>
            </div>
            <p className="text-xs text-gray-500 mt-1">
              ÌÉúÍ∑∏Îäî ÏµúÎåÄ 10Í∞úÍπåÏßÄ Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§. Í∞Å ÌÉúÍ∑∏Îäî 20Ïûê Ïù¥ÎÇ¥Î°ú
              ÏûÖÎ†•ÌïòÏÑ∏Ïöî.
            </p>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={handleModalClose}
              className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              Ï∑®ÏÜå
            </button>
            <button
              type="submit"
              disabled={!formData.name.trim()}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Ï†ÄÏû•
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProjectList;
