import React from "react";
import ReactMarkdown from "react-markdown";
import {
  ClipboardDocumentIcon,
  CheckCircleIcon,
  UserIcon,
  CpuChipIcon,
  DocumentDuplicateIcon,
} from "@heroicons/react/24/outline";

const ChatMessage = ({
  message,
  onCopyMessage,
  onCopyTitle,
  copiedMessage,
}) => {
  const isUser = message.type === "user";

  return (
    <div className={`group relative ${isUser ? "ml-12 pr-4" : "mr-12 pl-4"} mb-6`}>
      <div className={`flex ${isUser ? "justify-end" : "justify-start"}`}>
        <div
          className={`
          max-w-[80%] rounded-lg px-4 py-3 relative shadow-sm
          ${
            isUser
              ? "bg-blue-600 text-white"
              : message.isError
              ? "bg-red-50 text-red-800 border border-red-200"
              : message.isLoading
              ? "bg-yellow-50 text-yellow-800 border border-yellow-200"
              : "bg-white text-gray-800 border border-gray-200"
          }
        `}
        >
          {/* 메시지 내용 */}
          <div className="whitespace-pre-wrap text-sm leading-relaxed">
            {isUser ? (
              message.content
            ) : (
              <div className="prose prose-sm max-w-none prose-headings:text-gray-900 prose-p:text-gray-800 prose-strong:text-gray-900 prose-ul:text-gray-800 prose-li:text-gray-800">
                <ReactMarkdown>{message.content}</ReactMarkdown>
              </div>
            )}
          </div>

          {/* 제목 복사 버튼들 */}
          {message.titles && (
            <div className="mt-3 pt-3 border-t border-gray-200">
              <div className="flex flex-wrap gap-2">
                {message.titles.map((title, index) => (
                  <button
                    key={index}
                    onClick={() => onCopyTitle(title, message.id, index)}
                    className={`flex items-center px-2 py-1 rounded text-xs transition-colors ${
                      copiedMessage === `${message.id}_title_${index}`
                        ? "bg-green-100 text-green-700"
                        : "bg-gray-100 hover:bg-gray-200 text-gray-700"
                    }`}
                  >
                    {copiedMessage === `${message.id}_title_${index}` ? (
                      <CheckCircleIcon className="h-3 w-3 mr-1" />
                    ) : (
                      <DocumentDuplicateIcon className="h-3 w-3 mr-1" />
                    )}
                    제목 {index + 1} 복사
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* 복사 버튼 */}
          {!isUser && (
            <div className="mt-3 flex items-center space-x-2">
              <button
                onClick={() => onCopyMessage(message.content, message.id)}
                className={`flex items-center px-2 py-1 rounded text-xs transition-colors ${
                  copiedMessage === message.id
                    ? "bg-green-100 text-green-700"
                    : "bg-gray-100 hover:bg-gray-200 text-gray-700"
                }`}
              >
                {copiedMessage === message.id ? (
                  <CheckCircleIcon className="h-3 w-3 mr-1" />
                ) : (
                  <ClipboardDocumentIcon className="h-3 w-3 mr-1" />
                )}
                {copiedMessage === message.id ? "복사됨" : "복사"}
              </button>
            </div>
          )}

          {/* 타임스탬프 */}
          <div
            className={`text-xs mt-2 ${
              isUser ? "text-blue-100" : "text-gray-500"
            }`}
          >
            {message.timestamp.toLocaleTimeString("ko-KR", {
              hour: "numeric",
              minute: "2-digit",
            })}
          </div>

          {/* 로딩 상태 */}
          {message.isLoading && (
            <div className="flex items-center text-blue-600 mt-2">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
              <span className="text-sm">AI가 응답을 생성하고 있습니다...</span>
            </div>
          )}

          {/* 에러 상태 */}
          {message.isError && (
            <div className="flex items-center text-red-600 mt-2">
              <span className="text-sm">⚠️ 오류가 발생했습니다</span>
            </div>
          )}
        </div>
      </div>

      {/* 사용자 아바타 */}
      {isUser && (
        <div className="absolute top-0 right-0 -mr-10 w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center">
          <UserIcon className="h-5 w-5 text-white" />
        </div>
      )}

      {/* AI 아바타 */}
      {!isUser && (
        <div className="absolute top-0 left-0 -ml-10 w-8 h-8 bg-gray-600 rounded-full flex items-center justify-center">
          <CpuChipIcon className="h-5 w-5 text-white" />
        </div>
      )}
    </div>
  );
};

export default ChatMessage;
