import React, { useState, useEffect } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  XMarkIcon,
  SparklesIcon,
  DocumentTextIcon,
} from "@heroicons/react/24/outline";
import { promptCardAPI, handleAPIError, categoryAPI } from "../services/api";
import ChatInterface from "./ChatInterface";

const PromptCardManager = ({ projectId, projectName }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingCard, setEditingCard] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [userCategories, setUserCategories] = useState([]);

  useEffect(() => {
    loadPromptCards();
    loadUserCategories();
  }, [projectId]);

  const loadUserCategories = async () => {
    try {
      const data = await categoryAPI.getUserCategories();
      setUserCategories(data.categories || []);
    } catch (error) {
      console.error("카테고리 로드 실패:", error);
    }
  };

  const loadPromptCards = async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(projectId, true);
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("프롬프트 카드 로드 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSaveCard = async (cardData) => {
    try {
      if (editingCard) {
        // 수정
        await promptCardAPI.updatePromptCard(
          projectId,
          editingCard.promptId || editingCard.prompt_id,
          cardData
        );
        toast.success("프롬프트가 수정되었습니다");
      } else {
        // 새로 생성
        await promptCardAPI.createPromptCard(projectId, cardData);
        toast.success("프롬프트가 생성되었습니다");
      }

      setShowForm(false);
      setEditingCard(null);
      loadPromptCards();
    } catch (error) {
      console.error("프롬프트 저장 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    }
  };

  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트가 삭제되었습니다");
      loadPromptCards();
    } catch (error) {
      console.error("프롬프트 삭제 실패:", error);
      const errorInfo = handleAPIError(error);
      toast.error(errorInfo.message);
    }
  };

  const handleEditCard = (card) => {
    setEditingCard(card);
    setShowForm(true);
  };

  const handleNewCard = () => {
    setEditingCard(null);
    setShowForm(true);
  };

  const handleCancelForm = () => {
    setShowForm(false);
    setEditingCard(null);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 채팅 인터페이스 */}
      <div className="flex-1 pr-6">
        <ChatInterface projectId={projectId} projectName={projectName} />
      </div>

      {/* 프롬프트 관리 사이드바 (우측) */}
      <div className="w-80 bg-white border-l border-gray-200 flex flex-col shadow-lg">
        <div className="p-6 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">
              프롬프트 카드
            </h2>
            <button
              onClick={handleNewCard}
              className="inline-flex items-center px-3 py-1.5 text-sm bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              <PlusIcon className="h-4 w-4 mr-1" />
              추가
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-6 space-y-4">
          {promptCards.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500 mb-3">프롬프트 카드가 없습니다</p>
              <button
                onClick={handleNewCard}
                className="text-blue-600 hover:text-blue-800 text-sm font-medium"
              >
                첫 번째 카드 만들기
              </button>
            </div>
          ) : (
            promptCards.map((card) => (
              <PromptCard
                key={card.promptId || card.prompt_id}
                card={card}
                onEdit={() => handleEditCard(card)}
                onDelete={() =>
                  handleDeleteCard(card.promptId || card.prompt_id)
                }
                userCategories={userCategories}
              />
            ))
          )}
        </div>
      </div>

      {/* 프롬프트 편집 폼 모달 */}
      {showForm && (
        <PromptFormModal
          isOpen={showForm}
          onSubmit={handleSaveCard}
          onCancel={handleCancelForm}
          initialData={editingCard}
          userCategories={userCategories}
        />
      )}
    </div>
  );
};

// 간단한 프롬프트 카드 컴포넌트
const PromptCard = ({ card, onEdit, onDelete, userCategories = [] }) => {
  // 사용자 정의 카테고리에서 해당 카테고리 찾기
  const categoryInfo = userCategories.find(
    (cat) => cat.id === card.category
  ) || {
    name: "기본",
    color: "gray",
  };

  const getCategoryColorClasses = (color) => {
    const colorMap = {
      blue: "bg-blue-100 text-blue-800",
      green: "bg-green-100 text-green-800",
      purple: "bg-purple-100 text-purple-800",
      orange: "bg-orange-100 text-orange-800",
      red: "bg-red-100 text-red-800",
      yellow: "bg-yellow-100 text-yellow-800",
      pink: "bg-pink-100 text-pink-800",
      indigo: "bg-indigo-100 text-indigo-800",
      gray: "bg-gray-100 text-gray-800",
    };
    return colorMap[color] || colorMap.gray;
  };

  return (
    <div className="bg-white rounded-lg p-4 border border-gray-200 hover:border-gray-300 transition-colors shadow-sm">
      <div className="flex items-start justify-between mb-3">
        <h3 className="font-medium text-gray-900 text-sm leading-tight">
          {card.title || `프롬프트 ${card.promptId || card.prompt_id}`}
        </h3>
        <div className="flex space-x-1 ml-2">
          <button
            onClick={onEdit}
            className="p-1.5 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
          >
            <PencilIcon className="h-3.5 w-3.5" />
          </button>
          <button
            onClick={onDelete}
            className="p-1.5 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded transition-colors"
          >
            <TrashIcon className="h-3.5 w-3.5" />
          </button>
        </div>
      </div>

      {(card.content || card.prompt_text) && (
        <p className="text-xs text-gray-600 mb-3 line-clamp-3 leading-relaxed">
          {(card.content || card.prompt_text).substring(0, 100)}
          {(card.content || card.prompt_text).length > 100 && "..."}
        </p>
      )}

      <div className="flex items-center justify-between text-xs text-gray-400">
        <span>
          생성:{" "}
          {new Date(card.createdAt || new Date()).toLocaleDateString("ko-KR")}
        </span>
        {card.updatedAt && card.updatedAt !== card.createdAt && (
          <span>
            수정: {new Date(card.updatedAt).toLocaleDateString("ko-KR")}
          </span>
        )}
      </div>
    </div>
  );
};

// 간단한 프롬프트 편집 폼 모달
const PromptFormModal = ({
  isOpen,
  onSubmit,
  onCancel,
  initialData,
  userCategories = [],
}) => {
  const [formData, setFormData] = useState({
    title: "",
    prompt_text: "",
    category: "instruction", // 백엔드의 첫 번째 카테고리로 변경
    enabled: true,
  });

  useEffect(() => {
    if (initialData) {
      setFormData({
        title: initialData.title || "",
        prompt_text: initialData.prompt_text || initialData.content || "",
        category:
          initialData.category || userCategories[0]?.id || "instruction",
        enabled: initialData.enabled !== false,
      });
    } else if (userCategories.length > 0) {
      // 새 프롬프트일 경우 첫 번째 카테고리를 기본값으로 설정
      setFormData((prev) => ({
        ...prev,
        category: userCategories[0]?.id || "instruction",
      }));
    }
  }, [initialData, userCategories]);

  // 사용자 카테고리를 기반으로 옵션 생성
  const categoryOptions =
    userCategories.length > 0
      ? userCategories.map((cat) => ({
          value: cat.id,
          label: cat.name,
          color: cat.color,
        }))
      : [
          // 기본 카테고리 (사용자 카테고리가 없을 경우) - 백엔드와 일치
          { value: "instruction", label: "역할 및 목표", color: "blue" },
          { value: "knowledge", label: "지식 베이스", color: "green" },
          { value: "secondary", label: "CoT (사고 과정)", color: "purple" },
          { value: "style_guide", label: "스타일 가이드", color: "orange" },
          { value: "validation", label: "ReAct (추론+행동)", color: "red" },
          { value: "enhancement", label: "품질 검증", color: "indigo" },
        ];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.prompt_text.trim()) {
      toast.error("프롬프트 내용을 입력해주세요");
      return;
    }
    onSubmit(formData);
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  if (!isOpen) return null;

  const selectedCategory = categoryOptions.find(
    (cat) => cat.value === formData.category
  );

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl max-w-3xl w-full max-h-[90vh] overflow-hidden shadow-2xl">
        {/* 헤더 - 깔끔한 디자인 */}
        <div className="border-b border-gray-200 p-6 bg-white">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center">
                {initialData ? (
                  <PencilIcon className="h-5 w-5 text-gray-600" />
                ) : (
                  <DocumentTextIcon className="h-5 w-5 text-gray-600" />
                )}
              </div>
              <div>
                <h3 className="text-lg font-semibold text-gray-900">
                  {initialData ? "프롬프트 편집" : "새 프롬프트"}
                </h3>
                <p className="text-sm text-gray-500">
                  {initialData
                    ? "프롬프트 내용을 수정하세요"
                    : "새로운 프롬프트를 작성하세요"}
                </p>
              </div>
            </div>
            <button
              type="button"
              onClick={onCancel}
              className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <XMarkIcon className="h-5 w-5" />
            </button>
          </div>
        </div>

        <form
          onSubmit={handleSubmit}
          className="p-6 space-y-6 overflow-y-auto max-h-[calc(90vh-120px)]"
        >
          {/* 제목 필드 */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <DocumentTextIcon className="h-4 w-4 mr-2 text-blue-500" />
              프롬프트 제목
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-4 focus:ring-blue-100 transition-all duration-200 text-gray-800 placeholder-gray-400"
              placeholder="예: 경제 뉴스 제목 생성 가이드라인"
            />
          </div>

          {/* 카테고리 선택 */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <SparklesIcon className="h-4 w-4 mr-2 text-purple-500" />
              카테고리 <span className="text-red-500 ml-1">*</span>
            </label>
            <select
              value={formData.category}
              onChange={(e) => handleChange("category", e.target.value)}
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-purple-500 focus:ring-4 focus:ring-purple-100 transition-all duration-200 text-gray-800"
              required
            >
              {categoryOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {selectedCategory && (
              <p className="text-xs text-gray-500 mt-1">
                {selectedCategory.color && (
                  <span
                    className={`inline-block w-2 h-2 rounded-full mr-2 ${
                      selectedCategory.color === "blue"
                        ? "bg-blue-500"
                        : selectedCategory.color === "green"
                        ? "bg-green-500"
                        : selectedCategory.color === "purple"
                        ? "bg-purple-500"
                        : selectedCategory.color === "orange"
                        ? "bg-orange-500"
                        : selectedCategory.color === "red"
                        ? "bg-red-500"
                        : selectedCategory.color === "indigo"
                        ? "bg-indigo-500"
                        : "bg-gray-500"
                    }`}
                  />
                )}
                {
                  categoryOptions.find((cat) => cat.value === formData.category)
                    ?.label
                }
              </p>
            )}
          </div>

          {/* 프롬프트 내용 */}
          <div className="space-y-2">
            <label className="flex items-center text-sm font-semibold text-gray-800">
              <PencilIcon className="h-4 w-4 mr-2 text-green-500" />
              프롬프트 내용 <span className="text-red-500 ml-1">*</span>
            </label>
            <div className="relative">
              <textarea
                value={formData.prompt_text}
                onChange={(e) => handleChange("prompt_text", e.target.value)}
                rows={10}
                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-green-500 focus:ring-4 focus:ring-green-100 transition-all duration-200 text-gray-800 placeholder-gray-400 resize-none"
                placeholder="예: 당신은 경제 뉴스 전문 기자입니다. 다음 가이드라인에 따라 제목을 생성해주세요:\n\n1. 핵심 내용을 15-25자로 요약\n2. 독자의 관심을 끌는 키워드 포함\n3. 정확하고 객관적인 표현 사용"
                required
              />
              <div className="absolute bottom-3 right-3 text-xs text-gray-400">
                {formData.prompt_text.length} 자
              </div>
            </div>
          </div>

          {/* 활성화 옵션 - 깔끔한 체크박스 */}
          <div className="flex items-center">
            <input
              type="checkbox"
              id="enabled"
              checked={formData.enabled}
              onChange={(e) => handleChange("enabled", e.target.checked)}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
            <label htmlFor="enabled" className="ml-2 text-sm text-gray-700">
              활성화
            </label>
          </div>

          {/* 버튼 영역 */}
          <div className="flex justify-end space-x-3 pt-6 border-t border-gray-200">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors font-medium"
            >
              취소
            </button>
            <button
              type="submit"
              disabled={!formData.prompt_text.trim()}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors font-medium"
            >
              {initialData ? "수정" : "생성"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PromptCardManager;
