import React, { useState, useEffect, useMemo } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-hot-toast";
import {
  FolderOpenIcon,
  PlusIcon,
  EllipsisHorizontalIcon,
  TrashIcon,
  PencilIcon,
  CalendarIcon,
  DocumentTextIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  Squares2X2Icon,
  ListBulletIcon,
  ChartBarIcon,
  ClockIcon,
  TagIcon,
  SparklesIcon,
} from "@heroicons/react/24/outline";
import {
  projectAPI,
  handleAPIError,
  PROJECT_CATEGORIES,
  getCategoryInfo,
  getCategoryColorClasses,
  filterProjects,
  formatTokenCount,
  formatFileSize,
  calculatePromptStats,
  promptCardAPI,
} from "../services/api";

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 필터링 상태
  const [activeCategory, setActiveCategory] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("created");
  const [viewMode, setViewMode] = useState("grid");

  // 통계 상태
  const [projectStats, setProjectStats] = useState({});
  const [overallStats, setOverallStats] = useState({
    totalProjects: 0,
    totalPrompts: 0,
    totalTokens: 0,
    categoriesUsed: 0,
  });

  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await projectAPI.getProjects();
      const projectsWithStats = data.projects || [];

      // 각 프로젝트별 프롬프트 통계 로드
      const statsPromises = projectsWithStats.map(async (project) => {
        try {
          const promptsData = await promptCardAPI.getPromptCards(
            project.projectId,
            true,
            true
          );
          const stats = calculatePromptStats(promptsData.promptCards || []);
          return { projectId: project.projectId, stats };
        } catch (err) {
          console.error(`프로젝트 ${project.projectId} 통계 로드 실패:`, err);
          return { projectId: project.projectId, stats: null };
        }
      });

      const statsResults = await Promise.all(statsPromises);
      const statsMap = {};
      statsResults.forEach(({ projectId, stats }) => {
        statsMap[projectId] = stats;
      });

      setProjectStats(statsMap);
      setProjects(projectsWithStats);

      // 전체 통계 계산
      const overall = {
        totalProjects: projectsWithStats.length,
        totalPrompts: Object.values(statsMap).reduce(
          (sum, stats) => sum + (stats?.totalCards || 0),
          0
        ),
        totalTokens: Object.values(statsMap).reduce(
          (sum, stats) => sum + (stats?.totalTokens || 0),
          0
        ),
        categoriesUsed: new Set(
          projectsWithStats.map((p) => p.category).filter(Boolean)
        ).size,
      };
      setOverallStats(overall);
    } catch (err) {
      const errorInfo = handleAPIError(err);
      setError(errorInfo.message);
      toast.error(errorInfo.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteProject = async (projectId, projectName) => {
    if (!window.confirm(`"${projectName}" 프로젝트를 삭제하시겠습니까?`)) {
      return;
    }

    try {
      await projectAPI.deleteProject(projectId);
      toast.success("프로젝트가 삭제되었습니다");
      loadProjects();
    } catch (err) {
      const errorInfo = handleAPIError(err);
      toast.error(errorInfo.message);
    }
  };

  // 필터링된 프로젝트 목록
  const filteredProjects = useMemo(() => {
    return filterProjects(projects, {
      category: activeCategory,
      searchQuery,
      sortBy,
    });
  }, [projects, activeCategory, searchQuery, sortBy]);

  // 카테고리별 프로젝트 수
  const categoryStats = useMemo(() => {
    const stats = { all: projects.length };
    PROJECT_CATEGORIES.forEach((category) => {
      stats[category.id] = projects.filter(
        (p) => p.category === category.id
      ).length;
    });
    return stats;
  }, [projects]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">프로젝트 목록을 불러오는 중...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4">⚠️ 오류가 발생했습니다</div>
        <p className="text-gray-600 mb-6">{error}</p>
        <button
          onClick={loadProjects}
          className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
        >
          다시 시도
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* 헤더 & 전체 통계 */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">프로젝트 목록</h2>
          <p className="text-gray-600 mt-1">
            제목 생성 프로젝트를 관리하고 새로운 프로젝트를 생성하세요
          </p>
        </div>

        {/* 전체 통계 */}
        <div className="flex items-center space-x-6 bg-white rounded-lg border border-gray-200 px-4 py-3">
          <div className="text-center">
            <div className="text-lg font-bold text-blue-600">
              {overallStats.totalProjects}
            </div>
            <div className="text-xs text-gray-500">프로젝트</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-green-600">
              {overallStats.totalPrompts}
            </div>
            <div className="text-xs text-gray-500">프롬프트</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-purple-600">
              {formatTokenCount(overallStats.totalTokens)}
            </div>
            <div className="text-xs text-gray-500">토큰</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-orange-600">
              {overallStats.categoriesUsed}
            </div>
            <div className="text-xs text-gray-500">카테고리</div>
          </div>
        </div>
      </div>

      {/* 필터링 바 */}
      <div className="bg-white rounded-lg border border-gray-200 p-4">
        {/* 카테고리 탭 */}
        <div className="flex flex-wrap gap-2 mb-4">
          <button
            onClick={() => setActiveCategory("all")}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${
              activeCategory === "all"
                ? "bg-blue-600 text-white"
                : "bg-gray-100 text-gray-700 hover:bg-gray-200"
            }`}
          >
            전체 ({categoryStats.all})
          </button>
          {PROJECT_CATEGORIES.map((category) => (
            <button
              key={category.id}
              onClick={() => setActiveCategory(category.id)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-colors flex items-center space-x-2 ${
                activeCategory === category.id
                  ? `bg-${category.color}-600 text-white`
                  : "bg-gray-100 text-gray-700 hover:bg-gray-200"
              }`}
            >
              <span>{category.icon}</span>
              <span>{category.name}</span>
              <span className="bg-white bg-opacity-20 px-2 py-0.5 rounded-full text-xs">
                {categoryStats[category.id] || 0}
              </span>
            </button>
          ))}
        </div>

        {/* 검색 및 정렬 */}
        <div className="flex flex-col sm:flex-row gap-4">
          {/* 검색바 */}
          <div className="flex-1 relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="프로젝트 이름, 설명, 태그로 검색..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* 정렬 옵션 */}
          <div className="flex items-center space-x-2">
            <FunnelIcon className="h-5 w-5 text-gray-400" />
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="created">생성일순</option>
              <option value="updated">수정일순</option>
              <option value="name">이름순</option>
            </select>
          </div>

          {/* 뷰 모드 */}
          <div className="flex items-center space-x-1 bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setViewMode("grid")}
              className={`p-2 rounded-md ${
                viewMode === "grid" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <Squares2X2Icon className="h-4 w-4" />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-md ${
                viewMode === "list" ? "bg-white shadow-sm" : "hover:bg-gray-200"
              }`}
            >
              <ListBulletIcon className="h-4 w-4" />
            </button>
          </div>

          {/* 새 프로젝트 버튼 */}
          <Link
            to="/create"
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <PlusIcon className="h-4 w-4 mr-2" />새 프로젝트
          </Link>
        </div>
      </div>

      {/* 프로젝트 목록 */}
      {filteredProjects.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg border border-gray-200">
          <FolderOpenIcon className="mx-auto h-12 w-12 text-gray-400 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            {searchQuery || activeCategory !== "all"
              ? "조건에 맞는 프로젝트가 없습니다"
              : "프로젝트가 없습니다"}
          </h3>
          <p className="text-gray-500 mb-6">
            {searchQuery || activeCategory !== "all"
              ? "다른 조건으로 검색해보세요"
              : "첫 번째 프로젝트를 생성해보세요"}
          </p>
          <Link
            to="/create"
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <PlusIcon className="h-4 w-4 mr-2" />새 프로젝트 생성
          </Link>
        </div>
      ) : (
        <div
          className={
            viewMode === "grid"
              ? "grid gap-6 md:grid-cols-2 lg:grid-cols-3"
              : "space-y-4"
          }
        >
          {filteredProjects.map((project) => (
            <ProjectCard
              key={project.projectId}
              project={project}
              stats={projectStats[project.projectId]}
              onDelete={deleteProject}
              viewMode={viewMode}
            />
          ))}
        </div>
      )}
    </div>
  );
};

const ProjectCard = ({ project, stats, onDelete, viewMode }) => {
  const [showMenu, setShowMenu] = useState(false);
  const categoryInfo = getCategoryInfo(project.category);

  if (viewMode === "list") {
    return (
      <div className="bg-white rounded-lg border border-gray-200 hover:shadow-md transition-shadow">
        <div className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 flex-1">
              <div className="flex-shrink-0">
                <DocumentTextIcon className="h-10 w-10 text-blue-600" />
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-1">
                  <h3 className="text-lg font-semibold text-gray-900 truncate">
                    {project.name}
                  </h3>
                  <span
                    className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getCategoryColorClasses(
                      categoryInfo.color
                    )}`}
                  >
                    <span className="mr-1">{categoryInfo.icon}</span>
                    {categoryInfo.name}
                  </span>
                </div>
                <p className="text-sm text-gray-500 truncate mb-2">
                  {project.description || "설명 없음"}
                </p>
                <div className="flex items-center space-x-4 text-xs text-gray-500">
                  <div className="flex items-center space-x-1">
                    <CalendarIcon className="h-3 w-3" />
                    <span>
                      {new Date(project.createdAt).toLocaleDateString("ko-KR")}
                    </span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <DocumentTextIcon className="h-3 w-3" />
                    <span>{stats?.totalCards || 0}개 프롬프트</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <SparklesIcon className="h-3 w-3" />
                    <span>
                      {formatTokenCount(stats?.totalTokens || 0)} 토큰
                    </span>
                  </div>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <Link
                to={`/projects/${project.projectId}`}
                className="px-3 py-1 text-sm bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                열기
              </Link>

              <div className="relative">
                <button
                  onClick={() => setShowMenu(!showMenu)}
                  className="text-gray-400 hover:text-gray-600 p-1 rounded-full hover:bg-gray-100"
                >
                  <EllipsisHorizontalIcon className="h-5 w-5" />
                </button>

                {showMenu && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                    <div className="py-1">
                      <Link
                        to={`/projects/${project.projectId}`}
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                        onClick={() => setShowMenu(false)}
                      >
                        <PencilIcon className="h-4 w-4 mr-3" />
                        편집
                      </Link>
                      <button
                        onClick={() => {
                          onDelete(project.projectId, project.name);
                          setShowMenu(false);
                        }}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-700 hover:bg-red-50"
                      >
                        <TrashIcon className="h-4 w-4 mr-3" />
                        삭제
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow">
      <div className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">
              <DocumentTextIcon className="h-8 w-8 text-blue-600" />
            </div>
            <div>
              <h3 className="text-lg font-semibold text-gray-900 truncate">
                {project.name}
              </h3>
              <p className="text-sm text-gray-500 mt-1">
                {project.description || "설명 없음"}
              </p>
            </div>
          </div>

          <div className="relative">
            <button
              onClick={() => setShowMenu(!showMenu)}
              className="text-gray-400 hover:text-gray-600 p-1 rounded-full hover:bg-gray-100"
            >
              <EllipsisHorizontalIcon className="h-5 w-5" />
            </button>

            {showMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                <div className="py-1">
                  <Link
                    to={`/projects/${project.projectId}`}
                    className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    onClick={() => setShowMenu(false)}
                  >
                    <PencilIcon className="h-4 w-4 mr-3" />
                    편집
                  </Link>
                  <button
                    onClick={() => {
                      onDelete(project.projectId, project.name);
                      setShowMenu(false);
                    }}
                    className="flex items-center w-full px-4 py-2 text-sm text-red-700 hover:bg-red-50"
                  >
                    <TrashIcon className="h-4 w-4 mr-3" />
                    삭제
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* 카테고리 배지 */}
        <div className="flex items-center justify-between mb-4">
          <span
            className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium border ${getCategoryColorClasses(
              categoryInfo.color
            )}`}
          >
            <span className="mr-1">{categoryInfo.icon}</span>
            {categoryInfo.name}
          </span>

          <span
            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
              project.status === "active"
                ? "bg-green-100 text-green-800"
                : "bg-gray-100 text-gray-800"
            }`}
          >
            {project.status === "active" ? "활성" : "비활성"}
          </span>
        </div>

        {/* 통계 정보 */}
        <div className="grid grid-cols-2 gap-4 mb-4">
          <div className="bg-blue-50 rounded-lg p-3">
            <div className="flex items-center justify-between">
              <span className="text-xs text-blue-600 font-medium">
                프롬프트
              </span>
              <DocumentTextIcon className="h-4 w-4 text-blue-600" />
            </div>
            <div className="text-lg font-bold text-blue-700 mt-1">
              {stats?.totalCards || 0}개
            </div>
          </div>

          <div className="bg-purple-50 rounded-lg p-3">
            <div className="flex items-center justify-between">
              <span className="text-xs text-purple-600 font-medium">토큰</span>
              <SparklesIcon className="h-4 w-4 text-purple-600" />
            </div>
            <div className="text-lg font-bold text-purple-700 mt-1">
              {formatTokenCount(stats?.totalTokens || 0)}
            </div>
          </div>
        </div>

        {/* 세부 정보 */}
        <div className="space-y-2 mb-4">
          <div className="flex items-center text-sm text-gray-500">
            <CalendarIcon className="h-4 w-4 mr-2" />
            생성일: {new Date(project.createdAt).toLocaleDateString("ko-KR")}
          </div>

          {project.tags && project.tags.length > 0 && (
            <div className="flex items-center text-sm text-gray-500">
              <TagIcon className="h-4 w-4 mr-2" />
              <div className="flex flex-wrap gap-1">
                {project.tags.slice(0, 3).map((tag, index) => (
                  <span
                    key={index}
                    className="bg-gray-100 px-2 py-0.5 rounded text-xs"
                  >
                    {tag}
                  </span>
                ))}
                {project.tags.length > 3 && (
                  <span className="text-xs text-gray-400">
                    +{project.tags.length - 3}
                  </span>
                )}
              </div>
            </div>
          )}
        </div>

        {/* 프롬프트 용량 바 */}
        {stats && stats.totalTokens > 0 && (
          <div className="mb-4">
            <div className="flex items-center justify-between text-xs text-gray-500 mb-1">
              <span>프롬프트 용량</span>
              <span>{formatFileSize(stats.totalSize)}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300"
                style={{
                  width: `${Math.min((stats.totalTokens / 10000) * 100, 100)}%`,
                }}
              ></div>
            </div>
          </div>
        )}

        <div className="mt-6">
          <Link
            to={`/projects/${project.projectId}`}
            className="w-full inline-flex justify-center items-center px-4 py-2 border border-blue-600 text-sm font-medium rounded-md text-blue-600 bg-white hover:bg-blue-50"
          >
            프로젝트 열기
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ProjectList;
