import React, { useState, useEffect, useRef } from "react";
import ReactMarkdown from "react-markdown";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  ClipboardDocumentIcon,
  CheckCircleIcon,
  UserIcon,
  CpuChipIcon,
  ArrowLeftIcon,
} from "@heroicons/react/24/outline";
import { toast } from "react-hot-toast";
import { generateAPI } from "../services/api";

const ChatInterface = ({ projectId, projectName }) => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [copiedMessage, setCopiedMessage] = useState(null);
  const [streamingMessage, setStreamingMessage] = useState("");
  const [isStreaming, setIsStreaming] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const streamingRef = useRef(null);

  // ÎåÄÌôî ÏÑ∏ÏÖò Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const welcomeMessage = {
      id: Date.now(),
      type: "ai",
      content: `ÏïàÎÖïÌïòÏÑ∏Ïöî! Ï†ÄÎäî ${projectName}Ïùò AI Ï†úÎ™© ÏûëÍ∞ÄÏûÖÎãàÎã§. üéØ\n\nÍ∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏãúÎ©¥ Îã§ÏñëÌïú Ïä§ÌÉÄÏùºÏùò Ï†úÎ™©ÏùÑ Ï†úÏïàÌï¥ÎìúÎ¶¥Í≤åÏöî. Ï†úÎ™©ÏùÑ ÏàòÏ†ïÌïòÍ±∞ÎÇò Îã§Î•∏ Ïä§ÌÉÄÏùºÎ°ú Î∞îÍæ∏Í≥† Ïã∂ÏúºÏãúÎ©¥ Ïñ∏Ï†úÎì† ÎßêÏîÄÌï¥Ï£ºÏÑ∏Ïöî!`,
      timestamp: new Date(),
    };
    setMessages([welcomeMessage]);
  }, [projectName]);

  // Î©îÏãúÏßÄ Ï∂îÍ∞Ä Ïãú Ïä§ÌÅ¨Î°§ ÌïòÎã®ÏúºÎ°ú
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // Í∞úÏÑ†Îêú Ïä§Ìä∏Î¶¨Î∞ç ÌÉÄÏù¥Ìïë Ìö®Í≥º (ÏÇ¨Í≥†Í≥ºÏ†ï Ìè¨Ìï®)
  const simulateStreamingWithThinking = (text, onComplete) => {
    setIsStreaming(true);
    setStreamingMessage("");

    // 1Îã®Í≥Ñ: ÏÇ¨Í≥†Í≥ºÏ†ï ÌëúÏãú (2Ï¥à)
    const thinkingMessages = [
      "ü§î Í∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§...",
      "üìù ÌïµÏã¨ ÌÇ§ÏõåÎìúÎ•º Ï∂îÏ∂úÌïòÍ≥† ÏûàÏäµÎãàÎã§...", 
      "üí° Îã§ÏñëÌïú Ï†úÎ™© ÌõÑÎ≥¥Î•º ÏÉùÏÑ±ÌïòÍ≥† ÏûàÏäµÎãàÎã§...",
      "‚ú® ÏµúÏ†ÅÏùò Ï†úÎ™©ÏùÑ ÏÑ†Î≥ÑÌïòÍ≥† ÏûàÏäµÎãàÎã§..."
    ];

    let thinkingIndex = 0;
    const thinkingInterval = setInterval(() => {
      if (thinkingIndex < thinkingMessages.length) {
        setStreamingMessage(thinkingMessages[thinkingIndex]);
        thinkingIndex++;
        scrollToBottom();
      } else {
        clearInterval(thinkingInterval);

        // 2Îã®Í≥Ñ: Ïã§Ï†ú ÎãµÎ≥Ä Ïä§Ìä∏Î¶¨Î∞ç
        setStreamingMessage("");
        let textIndex = 0;
        const textInterval = setInterval(() => {
          if (textIndex < text.length) {
            setStreamingMessage((prev) => prev + text[textIndex]);
            textIndex++;
            scrollToBottom();
          } else {
            clearInterval(textInterval);
            setIsStreaming(false);
            setStreamingMessage("");
            onComplete();
          }
        }, 12); // Ï°∞Í∏à Îçî Îπ†Î•∏ ÌÉÄÏù¥Ìïë

        streamingRef.current = textInterval;
      }
    }, 500); // ÏÇ¨Í≥†Í≥ºÏ†ï Í∞Å Îã®Í≥ÑÎßàÎã§ 0.5Ï¥à

    streamingRef.current = thinkingInterval;
  };

  // Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const handleSendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: "user",
      content: inputMessage,
      timestamp: new Date(),
    };

    setMessages((prev) => [...prev, userMessage]);
    const currentInput = inputMessage;
    setInputMessage("");
    setIsLoading(true);

    try {
      // ÎåÄÌôî Ïª®ÌÖçÏä§Ìä∏ ÏÉùÏÑ± (ÏµúÍ∑º 5Í∞ú Î©îÏãúÏßÄ)
      const recentMessages = messages.slice(-5);
      const conversationContext = recentMessages
        .map(
          (msg) =>
            `${msg.type === "user" ? "Human" : "Assistant"}: ${msg.content}`
        )
        .join("\n\n");

      const contextualInput =
        recentMessages.length > 0
          ? `[Ïù¥Ï†Ñ ÎåÄÌôî Ïª®ÌÖçÏä§Ìä∏]\n${conversationContext}\n\n[ÏÉàÎ°úÏö¥ ÏöîÏ≤≠]\nHuman: ${currentInput}`
          : currentInput;

      const result = await generateAPI.generateTitle(
        projectId,
        contextualInput,
        null // onProgress callback
      );

      // ÏÇ¨Í≥†Í≥ºÏ†ïÏù¥ Ìè¨Ìï®Îêú Ïä§Ìä∏Î¶¨Î∞ç Ìö®Í≥ºÎ°ú AI ÏùëÎãµ Ï∂úÎ†•
      simulateStreamingWithThinking(result.result, () => {
        const aiMessage = {
          id: Date.now() + 1,
          type: "ai",
          content: result.result,
          timestamp: new Date(),
          debug: result.debug,
        };
        setMessages((prev) => [...prev, aiMessage]);
      });
    } catch (error) {
      console.error("Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:", error);
      const errorMessage = {
        id: Date.now() + 1,
        type: "ai",
        content:
          "Ï£ÑÏÜ°Ìï©ÎãàÎã§. Î©îÏãúÏßÄ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.",
        timestamp: new Date(),
        isError: true,
      };
      setMessages((prev) => [...prev, errorMessage]);
      toast.error("Î©îÏãúÏßÄ Ï†ÑÏÜ°Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    } finally {
      setIsLoading(false);
    }
  };

  // Enter ÌÇ§Î°ú Ï†ÑÏÜ°
  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Ï†úÎ™©Îßå Ï∂îÏ∂úÌïòÏó¨ Î≥µÏÇ¨
  const copyTitlesOnly = (text, messageId) => {
    const extractedTitles = extractTitles(text);
    const textToCopy =
      extractedTitles.length > 0 ? extractedTitles.join("\n") : text;

    navigator.clipboard
      .writeText(textToCopy)
      .then(() => {
        setCopiedMessage(messageId);
        toast.success("Ï†úÎ™©Ïù¥ ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      });
  };

  // Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏ Î≥µÏÇ¨
  const copyFullText = (text, messageId) => {
    navigator.clipboard
      .writeText(text)
      .then(() => {
        setCopiedMessage(messageId + "_full");
        toast.success("Ï†ÑÏ≤¥ ÌÖçÏä§Ìä∏Í∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      });
  };

  // Ï†úÎ™© Ï∂îÏ∂ú Ìï®Ïàò - Îã§ÏñëÌïú ÌòïÏãù ÏßÄÏõê
  const extractTitles = (text) => {
    const titles = [];

    // 1. Î≤àÌò∏ ÌòïÏãù: "1. [Ï†úÎ™©]" (ÏÉà ÌÖúÌîåÎ¶ø)
    const numberedMatches = text.match(/^\d+\.\s+(.+?)(?=\n\s*-|$)/gm);
    if (numberedMatches) {
      numberedMatches.forEach((match) => {
        const title = match.replace(/^\d+\.\s+/, "").trim();
        if (title && !title.includes("ÌíàÏßà ÌèâÍ∞Ä")) {
          titles.push(title);
        }
      });
    }

    // 2. Bullet ÌòïÏãù: "‚Ä¢ [Ï†úÎ™©]" (ÌòÑÏû¨ Ï∂úÎ†• ÌòïÏãù)
    const bulletMatches = text.match(/‚Ä¢ "([^"]+)"/g);
    if (bulletMatches) {
      bulletMatches.forEach((match) => {
        const title = match.replace(/‚Ä¢ "(.+)"/, "$1").trim();
        if (title) {
          titles.push(title);
        }
      });
    }

    // 3. ÏùºÎ∞ò Bullet ÌòïÏãù: "‚Ä¢ [Ï†úÎ™©]"
    if (titles.length === 0) {
      const simpleBulletMatches = text.match(/‚Ä¢ ([^\n]+)/g);
      if (simpleBulletMatches) {
        simpleBulletMatches.forEach((match) => {
          const title = match.replace("‚Ä¢ ", "").trim();
          // ÏÑ§Î™ÖÎ¨∏Ïù¥ ÏïÑÎãå Ï†úÎ™©Îßå Ï∂îÏ∂ú
          if (
            title &&
            !title.includes("Ïù¥Ïú†:") &&
            !title.includes("Ïù¥Í≤ΩÍ∑úÏùò") &&
            title.length < 100
          ) {
            titles.push(title);
          }
        });
      }
    }

    return titles;
  };

  // Í∞úÎ≥Ñ Ï†úÎ™© Î≥µÏÇ¨
  const copyIndividualTitle = (title, messageId, index) => {
    navigator.clipboard
      .writeText(title)
      .then(() => {
        setCopiedMessage(`${messageId}_title_${index}`);
        toast.success("Ï†úÎ™©Ïù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!");
        setTimeout(() => setCopiedMessage(null), 2000);
      })
      .catch(() => {
        toast.error("Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      });
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-sm border border-gray-200">
      {/* Ï±ÑÌåÖ Ìó§Îçî */}
      <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white rounded-t-lg">
        <div className="flex items-center space-x-3">
          <button
            onClick={() => navigate("/")}
            className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-colors"
            title="ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞"
          >
            <ArrowLeftIcon className="h-5 w-5" />
          </button>
          <CpuChipIcon className="h-5 w-5 text-gray-600" />
          <h3 className="text-md font-medium text-gray-900">AI ÎåÄÌôî</h3>
          <span className="text-sm text-gray-500">{projectName}</span>
        </div>
        <button
          onClick={() => setMessages(messages.slice(0, 1))} // Ï≤´ Î≤àÏß∏ ÌôòÏòÅ Î©îÏãúÏßÄÎßå ÎÇ®Í∏∞Í∏∞
          className="text-sm text-gray-500 hover:text-gray-700 px-2 py-1 rounded hover:bg-gray-100 transition-colors"
        >
          Ï¥àÍ∏∞Ìôî
        </button>
      </div>

      {/* Î©îÏãúÏßÄ ÏòÅÏó≠ */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 min-h-0">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${
