import React, { useState, useEffect, memo } from "react";
import {
  StarIcon,
  CheckCircleIcon,
  ClipboardDocumentIcon,
} from "@heroicons/react/24/outline";
import { toast } from "react-hot-toast";

const ResultDisplay = ({ result, projectName }) => {
  const [copiedTitle, setCopiedTitle] = useState(null);
  const [visibleSections, setVisibleSections] = useState({});
  const [typingEffect, setTypingEffect] = useState({});

  // 결과 파싱 처리 - 자연어 응답과 JSON 응답 모두 지원
  const parseResult = () => {
    try {
      // 먼저 JSON 파싱 시도
      if (typeof result.result === "string") {
        try {
          return JSON.parse(result.result);
        } catch (jsonError) {
          // JSON 파싱 실패 시 자연어 응답으로 처리
          return {
            natural_response: result.result,
            is_natural: true
          };
        }
      }
      return result.result;
    } catch (error) {
      console.error("Result parsing error:", error);
      return {
        analysis: {},
        titles: {},
        final_recommendation: {},
      };
    }
  };

  const parsedResult = parseResult();

  // 애니메이션 효과를 위한 useEffect
  useEffect(() => {
    const sections = ['recommendation', 'categories'];

    sections.forEach((section, index) => {
      setTimeout(() => {
        setVisibleSections(prev => ({ ...prev, [section]: true }));
      }, index * 300);
    });
  }, []);

  // 타이핑 효과 훅
  const useTypingEffect = (text, speed = 50) => {
    const [displayText, setDisplayText] = useState('');
    const [isComplete, setIsComplete] = useState(false);

    useEffect(() => {
      setDisplayText('');
      setIsComplete(false);

      if (!text) return;

      let i = 0;
      const timer = setInterval(() => {
        if (i < text.length) {
          setDisplayText(prev => prev + text.charAt(i));
          i++;
        } else {
          setIsComplete(true);
          clearInterval(timer);
        }
      }, speed);

      return () => clearInterval(timer);
    }, [text, speed]);

    return { displayText, isComplete };
  };

  const copyToClipboard = (text, titleType) => {
    navigator.clipboard
      .writeText(text)
      .then(() => {
        setCopiedTitle(titleType);
        toast.success("제목이 클립보드에 복사되었습니다!");
        setTimeout(() => setCopiedTitle(null), 2000);
      })
      .catch((err) => {
        console.error("복사 실패:", err);
        toast.error("복사에 실패했습니다.");
      });
  };


  // 최종 추천 제목의 타이핑 효과
  const finalTitle = parsedResult.final_recommendation?.title || '';
  const { displayText: typedTitle, isComplete: titleComplete } = useTypingEffect(
    visibleSections.recommendation ? finalTitle : '', 
    30
  );

  return (
    <div className="space-y-6">

      {/* Debug 정보 표시 (개발 모드일 때만) */}
      {result.debug && (
        <div className="bg-gray-50 rounded-xl border border-gray-200 shadow-sm p-4">
          <details className="cursor-pointer">
            <summary className="text-sm font-medium text-gray-700 mb-2">
              🔍 프롬프트 사용 현황 (디버그)
            </summary>
            <div className="text-xs text-gray-600 space-y-2 mt-3">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <span className="font-medium">사용된 프롬프트 수:</span> {result.debug.promptCount || 0}개
                </div>
                <div>
                  <span className="font-medium">기본 가이드라인 사용:</span> {result.debug.usedDefaultGuidelines ? '예' : '아니오'}
                </div>
              </div>

              {result.debug.promptTitles && result.debug.promptTitles.length > 0 && (
                <div>
                  <span className="font-medium">적용된 프롬프트:</span>
                  <ul className="list-disc list-inside ml-2 mt-1">
                    {result.debug.promptTitles.map((title, index) => (
                      <li key={index} className="text-gray-500">{title}</li>
                    ))}
                  </ul>
                </div>
              )}

              {result.debug.combinedPromptsLength && (
                <div>
                  <span className="font-medium">결합된 프롬프트 길이:</span> {result.debug.combinedPromptsLength.toLocaleString()}자
                </div>
              )}

              {result.debug.firstPromptPreview && (
                <div>
                  <span className="font-medium">프롬프트 미리보기:</span>
                  <div className="bg-white p-2 rounded border text-xs font-mono mt-1">
                    {result.debug.firstPromptPreview}
                  </div>
                </div>
              )}
            </div>
          </details>
        </div>
      )}

      {/* 최종 추천 제목 */}
      <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900 flex items-center">
            <StarIcon className="h-5 w-5 mr-2 text-yellow-500" />
            추천 제목
          </h3>
          <span className="text-sm text-gray-500">
            {new Date(result.timestamp).toLocaleString("ko-KR", {
              timeZone: "Asia/Seoul",
              year: "numeric",
              month: "2-digit", 
              day: "2-digit",
              hour: "2-digit",
              minute: "2-digit"
            })}
          </span>
        </div>

        {parsedResult.final_recommendation && (
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center justify-between">
              <h4 className="text-xl font-medium text-gray-900 flex-1 mr-4">
                {visibleSections.recommendation ? typedTitle : ""}
                {visibleSections.recommendation && !titleComplete && (
                  <span className="animate-pulse text-gray-400">|</span>
                )}
              </h4>
              {titleComplete && (
                <button
                  onClick={() =>
                    copyToClipboard(
                      parsedResult.final_recommendation.title,
                      "final"
                    )
                  }
                  className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-200 rounded-lg transition-colors"
                >
                  {copiedTitle === "final" ? (
                    <CheckCircleIcon className="h-5 w-5 text-green-600" />
                  ) : (
                    <ClipboardDocumentIcon className="h-5 w-5" />
                  )}
                </button>
              )}
            </div>
          </div>
        )}
      </div>

      {/* 다른 제목 옵션 */}
      {parsedResult.titles && (
        <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            다른 제목 옵션
          </h3>

          <div className="space-y-3">
            {Object.entries(parsedResult.titles).map(([category, titles]) => 
              titles.map((titleObj, index) => (
                <div
                  key={`${category}-${index}`}
                  className="flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
                >
                  <span className="text-gray-900 flex-1">
                    {titleObj.title || "제목 없음"}
                  </span>
                  <button
                    onClick={() =>
                      copyToClipboard(
                        titleObj.title,
                        `${category}-${index}`
                      )
                    }
                    className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-200 rounded-lg transition-colors ml-3"
                  >
                    {copiedTitle === `${category}-${index}` ? (
                      <CheckCircleIcon className="h-4 w-4 text-green-600" />
                    ) : (
                      <ClipboardDocumentIcon className="h-4 w-4" />
                    )}
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default memo(ResultDisplay);
