import React, { useState } from "react";
import { PlusIcon, PencilIcon, TrashIcon } from "@heroicons/react/24/outline";
import { usePrompts } from "../hooks/usePrompts";
import LoadingSpinner from "./LoadingSpinner";

const PromptManager = ({ projectId, prompts, loading }) => {
  const { createPrompt, updatePrompt, deletePrompt } = usePrompts();
  const [showForm, setShowForm] = useState(false);
  const [editingPrompt, setEditingPrompt] = useState(null);

  const handleCreatePrompt = () => {
    setEditingPrompt(null);
    setShowForm(true);
  };

  const handleEditPrompt = (prompt) => {
    setEditingPrompt(prompt);
    setShowForm(true);
  };

  const handleDeletePrompt = async (promptId) => {
    if (window.confirm("정말로 이 프롬프트를 삭제하시겠습니까?")) {
      await deletePrompt(projectId, promptId);
    }
  };

  const handleSavePrompt = async (promptData) => {
    try {
      if (editingPrompt) {
        await updatePrompt(projectId, editingPrompt.id, promptData);
      } else {
        await createPrompt(projectId, promptData);
      }
      setShowForm(false);
      setEditingPrompt(null);
    } catch (error) {
      // 에러는 hook에서 처리됨
    }
  };

  const handleCancelForm = () => {
    setShowForm(false);
    setEditingPrompt(null);
  };

  return (
    <div className="flex flex-col h-full">
      {/* 헤더 */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h2 className="text-lg font-semibold text-gray-900">프롬프트 관리</h2>
          <button
            onClick={handleCreatePrompt}
            className="inline-flex items-center px-3 py-2 text-sm bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            <PlusIcon className="h-4 w-4 mr-1" />새 프롬프트
          </button>
        </div>
      </div>

      {/* 프롬프트 목록 */}
      <div className="flex-1 overflow-y-auto p-4">
        {loading ? (
          <LoadingSpinner size="medium" message="프롬프트를 불러오는 중..." />
        ) : prompts.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-500 mb-4">프롬프트가 없습니다</p>
            <button
              onClick={handleCreatePrompt}
              className="text-blue-600 hover:text-blue-800"
            >
              첫 번째 프롬프트 만들기
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {prompts.map((prompt) => (
              <PromptCard
                key={prompt.id}
                prompt={prompt}
                onEdit={() => handleEditPrompt(prompt)}
                onDelete={() => handleDeletePrompt(prompt.id)}
              />
            ))}
          </div>
        )}
      </div>

      {/* 프롬프트 편집 폼 */}
      {showForm && (
        <PromptForm
          prompt={editingPrompt}
          onSave={handleSavePrompt}
          onCancel={handleCancelForm}
        />
      )}
    </div>
  );
};

// 프롬프트 카드 컴포넌트
const PromptCard = ({ prompt, onEdit, onDelete }) => {
  return (
    <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
      <div className="flex items-start justify-between mb-2">
        <h3 className="font-medium text-gray-900">
          {prompt.title || `프롬프트 ${prompt.id}`}
        </h3>
        <div className="flex space-x-1">
          <button
            onClick={onEdit}
            className="p-1 text-gray-400 hover:text-blue-600"
          >
            <PencilIcon className="h-4 w-4" />
          </button>
          <button
            onClick={onDelete}
            className="p-1 text-gray-400 hover:text-red-600"
          >
            <TrashIcon className="h-4 w-4" />
          </button>
        </div>
      </div>

      {prompt.content && (
        <p className="text-sm text-gray-600 line-clamp-3 mb-2">
          {prompt.content}
        </p>
      )}

      <div className="flex items-center justify-between text-xs text-gray-500">
        <span>카테고리: {prompt.category || "기본"}</span>
        <span
          className={`px-2 py-1 rounded-full ${
            prompt.enabled
              ? "bg-green-100 text-green-800"
              : "bg-gray-100 text-gray-800"
          }`}
        >
          {prompt.enabled ? "활성" : "비활성"}
        </span>
      </div>
    </div>
  );
};

// 프롬프트 편집 폼 컴포넌트
const PromptForm = ({ prompt, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    title: prompt?.title || "",
    content: prompt?.content || "",
    category: prompt?.category || "instruction",
    enabled: prompt?.enabled !== false,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.content.trim()) {
      return;
    }
    onSave(formData);
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">
            {prompt ? "프롬프트 편집" : "새 프롬프트"}
          </h3>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              제목
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프롬프트 제목"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              카테고리
            </label>
            <select
              value={formData.category}
              onChange={(e) => handleChange("category", e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="instruction">역할 및 목표</option>
              <option value="knowledge">지식 베이스</option>
              <option value="secondary">사고 과정</option>
              <option value="output_format">출력 형식</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              프롬프트 내용 *
            </label>
            <textarea
              value={formData.content}
              onChange={(e) => handleChange("content", e.target.value)}
              rows={8}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="프롬프트 내용을 입력하세요..."
              required
            />
          </div>

          <div className="flex items-center">
            <input
              type="checkbox"
              id="enabled"
              checked={formData.enabled}
              onChange={(e) => handleChange("enabled", e.target.checked)}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
            <label htmlFor="enabled" className="ml-2 text-sm text-gray-700">
              활성화
            </label>
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 text-gray-700 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              취소
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              {prompt ? "수정" : "생성"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PromptManager;
