import React, { useState, useCallback } from "react";
import { ChatBubbleLeftRightIcon, SparklesIcon, PaperAirplaneIcon, StopIcon } from "@heroicons/react/24/outline";
import ConversationDrawer from "./ConversationDrawer";
import ModelSelector from "../ModelSelector";
import { useChat } from "../../hooks/useChat";
import { useMessages } from "../../hooks/useMessages";
import { useConversationContext } from "../../contexts/ConversationContext";
import { useAuth } from "../../contexts/AuthContext";
import SimpleChatMessage from "../SimpleChatMessage";

const ChatWindow = ({ projectId, projectName, promptCards = [] }) => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Auth context for user info
  const { user } = useAuth();

  // Conversation context
  const { currentConversationId, addMessage } = useConversationContext();

  // Messages hook for current conversation
  const {
    messages: historicalMessages,
    loading: messagesLoading,
  } = useMessages(currentConversationId);

  // Chat hook for sending messages
  const {
    messages: chatMessages,
    inputValue,
    handleInputChange,
    copiedMessage,
    isGenerating,
    inputHeight,
    handleSendMessage: originalHandleSendMessage,
    handleStopGeneration,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    wsConnected,
    wsConnecting,
    selectedModel,
    setSelectedModel,
  } = useChat(projectId, projectName, promptCards, currentConversationId);

  // 통합된 메시지 목록 (과거 메시지 + 현재 세션 메시지)
  const allMessages = [...historicalMessages, ...chatMessages];

  // 메시지 전송 핸들러 (conversation 정보 포함)
  const handleSendMessage = useCallback(async () => {
    if (!inputValue.trim() || isGenerating) return;

    // 현재 대화 ID와 사용자 정보 포함하여 메시지 전송
    const messageData = {
      userInput: inputValue,
      projectId,
      conversationId: currentConversationId,
      userSub: user?.id || "anonymous",
      chat_history: allMessages.map((msg) => ({
        role: msg.role,
        content: msg.content,
      })),
      prompt_cards: promptCards,
    };

    // 사용자 메시지를 즉시 UI에 추가
    const userMessage = {
      id: Date.now().toString(),
      role: "user",
      content: inputValue,
      timestamp: new Date().toISOString(),
    };
    addMessage(userMessage);

    // 원래 핸들러 호출 (수정된 데이터로)
    await originalHandleSendMessage(messageData);
  }, [
    inputValue,
    isGenerating,
    currentConversationId,
    allMessages,
    promptCards,
    addMessage,
    originalHandleSendMessage,
    projectId,
    user?.id,
  ]);

  return (
    <div className="flex h-full overflow-hidden">
      {/* 대화 드로어 */}
      <ConversationDrawer onCollapsedChange={setSidebarCollapsed} />

      {/* 메인 채팅 영역 */}
      <div className={`flex-1 flex flex-col bg-gray-50 dark:bg-dark-primary transition-all duration-500 ease-out relative ${
        sidebarCollapsed ? "md:ml-14" : "md:ml-64"
      }`}>
        {/* 메시지 영역 */}
        <div className="flex-1 overflow-y-auto px-4 md:px-6 py-4 min-h-0 bg-gray-50 dark:bg-dark-primary transition-all duration-300 pt-16 pb-40">
          <div className="max-w-5xl mx-auto space-y-6">
            {allMessages.length === 0 && !messagesLoading ? (
              // 빈 상태 - 새 대화 시작
              <div className="flex flex-col items-center justify-center min-h-[calc(100vh-280px)]">
                <div className="bg-white dark:bg-dark-secondary rounded-2xl shadow-md p-8 max-w-3xl w-full mx-auto transition-colors duration-300">
                  <div className="flex justify-center mb-6">
                    <div className="bg-blue-100 dark:bg-dark-tertiary p-3 rounded-full">
                      <ChatBubbleLeftRightIcon className="h-8 w-8 text-blue-600 dark:text-blue-400" />
                    </div>
                  </div>
                  <h2 className="text-xl font-semibold text-center text-gray-800 dark:text-white mb-4">
                    AI 헤드라인 어시스턴트
                  </h2>
                  <p className="text-gray-600 dark:text-gray-300 text-center mb-6">
                    본문을 입력하면 AI가 최적의 헤드라인을 제안해 드립니다.
                  </p>

                  <div className="space-y-4">
                    <div className="flex items-start p-3 bg-gray-50 dark:bg-dark-tertiary rounded-lg">
                      <div className="mr-3 mt-1">
                        <SparklesIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                      </div>
                      <div>
                        <p className="text-sm text-gray-700 dark:text-gray-300">
                          <span className="font-medium">시작하는 방법:</span>{" "}
                          아래 입력창에 본문을 붙여넣고 전송 버튼을 클릭하세요
                        </p>
                      </div>
                    </div>
                    <div className="flex items-start p-3 bg-gray-50 dark:bg-dark-tertiary rounded-lg">
                      <div className="mr-3 mt-1">
                        <SparklesIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                      </div>
                      <div>
                        <p className="text-sm text-gray-700 dark:text-gray-300">
                          <span className="font-medium">대화 기록:</span> 좌측
                          메뉴에서 이전 대화를 확인하고 이어서 대화할 수
                          있습니다
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              // 메시지 목록
              <div className="space-y-6 pt-4">
                {allMessages.map((message) => (
                  <SimpleChatMessage
                    key={message.id}
                    message={message}
                    onCopyMessage={handleCopyMessage}
                    onCopyTitle={handleCopyTitle}
                    copiedMessage={copiedMessage}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* 고정된 하단 입력 영역 - DeepSeek 스타일 */}
        <div className={`fixed bottom-0 left-0 right-0 bg-gray-50 dark:bg-dark-primary border-t border-gray-200 dark:border-gray-700 transition-all duration-500 ${
          sidebarCollapsed ? "md:ml-14" : "md:ml-64"
        }`}>
          <div className="w-full max-w-5xl mx-auto px-4 md:px-6 py-6">
            {/* 둥근 모서리 통짜 입력창 */}
            <div className="w-full rounded-2xl bg-white dark:bg-[#2E333D] border border-gray-200 dark:border-transparent shadow-sm dark:shadow-none transition-all duration-200">
              {/* 메인 입력 영역 */}
              <div className="flex items-center gap-3 px-5 py-4">
                {/* 글 입력 영역 */}
                <textarea
                  value={inputValue}
                  onChange={(e) => handleInputChange(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder={
                    isGenerating
                      ? "생성 중입니다..."
                      : "기사 본문을 입력하세요..."
                  }
                  className="flex-1 resize-none bg-transparent text-gray-900 dark:text-gray-100 placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none appearance-none"
                  rows={1}
                  style={{
                    height: `${Math.max(inputHeight || 24, 24)}px`,
                    minHeight: "24px",
                    maxHeight: "200px",
                    lineHeight: "1.5",
                    overflowY: inputHeight > 120 ? "auto" : "hidden"
                  }}
                  disabled={isGenerating}
                />

                {/* 전송/중단 버튼 */}
                {isGenerating ? (
                  <button
                    onClick={handleStopGeneration}
                    className="shrink-0 rounded-full p-2.5 bg-red-500 hover:bg-red-600 text-white transition-all duration-200"
                  >
                    <StopIcon className="w-4 h-4" />
                  </button>
                ) : (
                  <button
                    onClick={handleSendMessage}
                    disabled={!inputValue.trim()}
                    className={`shrink-0 rounded-full p-2.5 transition-all duration-200 ${
                      inputValue.trim()
                        ? "bg-[#5E89FF] hover:bg-[#4A7BFF] text-white"
                        : "bg-gray-300 dark:bg-gray-600 text-gray-500 dark:text-gray-400 cursor-not-allowed"
                    }`}
                  >
                    <PaperAirplaneIcon className="w-4 h-4" />
                  </button>
                )}
              </div>

              {/* 하단 정보 영역 - 입력창 안에 위치 */}
              <div className="flex items-center justify-between px-5 pb-3 pt-1">
                <div className="flex items-center gap-3">
                  {/* 모델 선택기 */}
                  <div className="scale-90 origin-left">
                    <ModelSelector
                      selectedModel={selectedModel}
                      onModelChange={setSelectedModel}
                    />
                  </div>

                  {/* 글자 수 표시 */}
                  <div className="text-xs text-gray-500 dark:text-gray-400">
                    {inputValue.length}자
                  </div>
                </div>

                {/* 연결 상태 */}
                <div className="flex items-center gap-1.5">
                  {wsConnecting ? (
                    <>
                      <div className="w-1.5 h-1.5 bg-yellow-500 rounded-full animate-pulse"></div>
                      <span className="text-xs text-yellow-600 dark:text-yellow-400">연결 중</span>
                    </>
                  ) : wsConnected ? (
                    <>
                      <div className="w-1.5 h-1.5 bg-green-500 rounded-full animate-pulse"></div>
                      <span className="text-xs text-green-600 dark:text-green-400">실시간 스트리밍</span>
                    </>
                  ) : (
                    <>
                      <div className="w-1.5 h-1.5 bg-gray-400 rounded-full"></div>
                      <span className="text-xs text-gray-500 dark:text-gray-400">일반 모드</span>
                    </>
                  )}
                </div>
              </div>
            </div>

            {/* 하단 경고 문구 */}
            <div className="text-center mt-4">
              <p className="text-xs text-gray-500 dark:text-gray-400">
                AI는 실수를 할 수 있습니다. 중요한 정보는 재차 확인하세요.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
