import React from "react";
import {
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  EllipsisHorizontalIcon,
} from "@heroicons/react/24/outline";
import { AVAILABLE_MODELS } from "../../services/api";

const PromptCard = ({
  card,
  onEdit,
  onToggle,
  onDelete,
  isCurrentStep = false,
  isExecuted = false,
  isExecuting = false,
  stepNumber = 0,
  hideDeleteButton = false, // 삭제 버튼 숨김 옵션 추가
}) => {
  const getModelDisplayName = (modelId) => {
    const model = AVAILABLE_MODELS.find((m) => m.id === modelId);
    return model ? model.name : modelId;
  };

  const handleToggleClick = async () => {
    try {
      await onToggle(card.promptId || card.id, !card.enabled);
    } catch (error) {
      console.error("카드 토글 실패:", error);
    }
  };

  const handleEdit = () => {
    onEdit(card);
  };

  const getCardStyles = () => {
    let baseStyles =
      "bg-gradient-to-br from-white to-gray-50 border rounded-xl p-5 transition-all duration-300 hover:shadow-lg hover:scale-[1.02] backdrop-blur-sm";

    if (isCurrentStep) {
      baseStyles += " ring-2 ring-blue-500 shadow-xl border-blue-200 bg-gradient-to-br from-blue-50 to-white";
    } else if (isExecuted) {
      baseStyles += " border-green-200 bg-gradient-to-br from-green-50 to-white shadow-md";
    } else if (card.enabled) {
      baseStyles += " border-gray-200 hover:border-gray-300 hover:shadow-xl";
    } else {
      baseStyles += " border-gray-150 bg-gradient-to-br from-gray-100 to-gray-200 opacity-75";
    }

    return baseStyles;
  };

  const getCategoryInfo = (categoryId) => {
    const categories = {
      role: { name: "역할", color: "bg-blue-100 text-blue-800" },
      guidelines: {
        name: "가이드라인",
        color: "bg-purple-100 text-purple-800",
      },
      workflow: { name: "워크플로우", color: "bg-green-100 text-green-800" },
      output_results: {
        name: "출력결과",
        color: "bg-orange-100 text-orange-800",
      },
      examples: { name: "예시", color: "bg-yellow-100 text-yellow-800" },
      review: { name: "검수", color: "bg-red-100 text-red-800" },
    };
    return (
      categories[categoryId] || {
        name: categoryId,
        color: "bg-gray-100 text-gray-800",
      }
    );
  };

  const categoryInfo = getCategoryInfo(card.category);

  return (
    <div className={getCardStyles()}>
      {/* 카드 헤더 */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1 min-w-0">
          {/* 실행 단계 표시 */}
          {isExecuting && (
            <div className="flex items-center mb-3 p-2 bg-blue-50 rounded-lg border border-blue-200">
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2 flex-shrink-0"></div>
              <span className="text-sm text-blue-700 font-medium">
                단계 {stepNumber} 실행 중...
              </span>
            </div>
          )}

          {/* 카테고리 태그와 제목 */}
          <div className="flex items-center gap-3 mb-3">
            <span
              className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold ${categoryInfo.color} flex-shrink-0`}
            >
              {categoryInfo.name}
            </span>
            <h4 className="font-semibold text-gray-900 text-sm truncate">
              {card.title || `${categoryInfo.name} 프롬프트`}
            </h4>
          </div>

          {/* 모델 정보와 온도 슬라이더 */}
          <div className="space-y-2">
            <div className="flex items-center text-xs text-gray-500">
              <Cog6ToothIcon className="h-3 w-3 mr-1 flex-shrink-0" />
              <span className="truncate">{getModelDisplayName(card.model_name)}</span>
            </div>

            {/* 온도 슬라이더 */}
            <div className="flex items-center gap-2">
              <span className="text-xs text-gray-500 whitespace-nowrap">온도:</span>
              <div className="flex-1 flex items-center gap-2">
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={card.temperature || 0.7}
                  className="flex-1 h-1 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
                  style={{
                    background: `linear-gradient(to right, #3b82f6 0%, #3b82f6 ${((card.temperature || 0.7) * 100)}%, #e5e7eb ${((card.temperature || 0.7) * 100)}%, #e5e7eb 100%)`
                  }}
                  readOnly
                />
                <span className="text-xs text-gray-600 font-medium min-w-[2rem]">
                  {(card.temperature || 0.7).toFixed(1)}
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* 액션 버튼들 */}
        <div className="flex items-start gap-1 ml-3 flex-shrink-0">
          {/* 활성화/비활성화 토글 */}
          <button
            onClick={handleToggleClick}
            className={`p-2 rounded-lg transition-all duration-200 ${
              card.enabled
                ? "text-green-600 hover:bg-green-100 bg-green-50 shadow-sm"
                : "text-gray-400 hover:bg-gray-100"
            }`}
            title={card.enabled ? "비활성화" : "활성화"}
          >
            {card.enabled ? (
              <EyeIcon className="h-4 w-4" />
            ) : (
              <EyeSlashIcon className="h-4 w-4" />
            )}
          </button>

          {/* 편집 버튼 */}
          <button
            onClick={handleEdit}
            className="p-2 text-gray-600 hover:bg-blue-100 hover:text-blue-600 rounded-lg transition-all duration-200 shadow-sm hover:shadow-md"
            title="편집"
          >
            <PencilIcon className="h-4 w-4" />
          </button>

          {/* 삭제 버튼 - hideDeleteButton이 true면 숨김 */}
          {!hideDeleteButton && (
            <button
              onClick={() => onDelete(card.promptId || card.id)}
              className="p-2 text-red-600 hover:bg-red-100 rounded-lg transition-all duration-200 shadow-sm hover:shadow-md"
              title="삭제"
            >
              <TrashIcon className="h-4 w-4" />
            </button>
          )}
        </div>
      </div>

      {/* 프롬프트 미리보기 */}
      <div className="text-sm text-gray-600 leading-relaxed">
        <div className="bg-gradient-to-br from-gray-50 to-gray-100 rounded-lg p-4 max-h-24 overflow-hidden border border-gray-200 relative">
          {card.prompt_text ? (
            <div className="relative">
              <div className="line-clamp-3 leading-relaxed">
                {card.prompt_text.length > 120
                  ? `${card.prompt_text.substring(0, 120)}...`
                  : card.prompt_text}
              </div>
              {card.prompt_text.length > 120 && (
                <div className="absolute bottom-0 right-0 bg-gradient-to-l from-gray-100 to-transparent pl-8 pr-2">
                  <span className="text-xs text-gray-400">더보기</span>
                </div>
              )}
            </div>
          ) : (
            <div className="flex items-center justify-center h-12">
              <span className="text-gray-400 italic text-sm">
                프롬프트 내용이 없습니다.
              </span>
            </div>
          )}
        </div>
      </div>

      {/* 실행 완료 표시 */}
      {isExecuted && (
        <div className="mt-3 flex items-center text-green-600 text-sm bg-green-50 p-2 rounded-lg border border-green-200">
          <SparklesIcon className="h-4 w-4 mr-2" />
          실행 완료
        </div>
      )}
    </div>
  );
};

export default PromptCard;
