import React from "react";
import { Link, useLocation } from "react-router-dom";
import { DocumentTextIcon, PlusIcon } from "@heroicons/react/24/outline";

const Layout = ({ children }) => {
  const location = useLocation();
  const isProjectDetail = location.pathname.startsWith("/projects/");

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 헤더 - 프로젝트 상세 페이지가 아닐 때만 표시 */}
      {!isProjectDetail && (
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              {/* 로고 */}
              <Link to="/" className="flex items-center space-x-3">
                <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
                  <DocumentTextIcon className="h-5 w-5 text-white" />
                </div>
                <div>
                  <h1 className="text-xl font-bold text-gray-900">
                    TITLE-NOMICS
                  </h1>
                  <p className="text-xs text-gray-500">AI 제목 생성기</p>
                </div>
              </Link>

              {/* 네비게이션 */}
              <nav className="flex items-center space-x-6">
                <Link
                  to="/"
                  className={`text-sm font-medium transition-colors ${
                    location.pathname === "/"
                      ? "text-blue-600"
                      : "text-gray-600 hover:text-gray-900"
                  }`}
                >
                  프로젝트
                </Link>
                <Link
                  to="/create"
                  className="inline-flex items-center px-3 py-2 text-sm font-medium rounded-lg text-white bg-blue-600 hover:bg-blue-700 transition-colors"
                >
                  <PlusIcon className="h-4 w-4 mr-1" />새 프로젝트
                </Link>
              </nav>
            </div>
          </div>
        </header>
      )}

      {/* 메인 콘텐츠 */}
      <main
        className={
          isProjectDetail ? "" : "max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8"
        }
      >
        {children}
      </main>
    </div>
  );
};

export default Layout;
