  <div className="text-center py-12 bg-white rounded-lg border border-gray-200">
    <DocumentTextIcon className="h-16 w-16 text-gray-400 mx-auto mb-4" />
    <h3 className="text-lg font-medium text-gray-900 mb-2">
      프롬프트 카드가 없습니다
    </h3>
    <p className="text-gray-600 mb-6">
      첫 번째 프롬프트 카드를 생성하여 AI 제목 생성을 시작하세요
    </p>
    <button
      onClick={onCreateCard}
      className="flex items-center mx-auto px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors shadow-sm"
    >
      <PlusIcon className="h-5 w-5 mr-2" />첫 번째 카드 생성
    </button>
  </div>
);

// 프롬프트 카드 컴포넌트
const PromptCard = ({
  card,
  isSelected,
  onSelect,
  onEdit,
  onDelete,
  onToggle,
  onDragStart,
  onDragEnd,
  onDragOver,
  onDrop,
  isDragging,
  dragOverIndex,
  index,
}) => {
  const categoryInfo = PROMPT_CARD_CATEGORIES.find(
    (cat) => cat.id === card.category
  );

  return (
    <div
      draggable
      onDragStart={() => onDragStart(index)}
      onDragEnd={onDragEnd}
      onDragOver={(e) => onDragOver(e, index)}
      onDrop={(e) => onDrop(e, index)}
      className={`bg-white rounded-lg border-2 transition-all duration-200 cursor-move shadow-sm hover:shadow-md ${
        isSelected
          ? "border-blue-500 shadow-lg transform scale-[1.02]"
          : "border-gray-200 hover:border-gray-300"
      } ${isDragging ? "opacity-50" : ""} ${
        dragOverIndex === index ? "border-blue-400 bg-blue-50" : ""
      }`}
      onClick={() => onSelect(card)}
    >
      <div className="p-4">
        {/* 카드 헤더 */}
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center space-x-3">
            <div className="flex items-center">
              <Bars3Icon className="h-4 w-4 text-gray-400" />
              <span className="text-sm text-gray-500 ml-2">
                {card.step_order}
              </span>
            </div>
            <div
              className={`px-2 py-1 rounded-full text-xs font-medium border ${
                CATEGORY_COLORS[categoryInfo?.color || "gray"]
              }`}
            >
              {categoryInfo?.name || "기타"}
            </div>
          </div>
        </div>

        {/* 카드 내용 */}
        <div className="mb-4">
          <h3 className="text-lg font-semibold text-gray-900">{card.title}</h3>
          <p className="text-sm text-gray-600">
            {card.description || "설명이 없습니다"}
          </p>
        </div>

        {/* 카드 액션 */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <button
              onClick={(e) => {
                e.stopPropagation();
                onToggle(card.promptId, !card.enabled);
              }}
              className={`p-2 rounded-lg transition-colors ${
                card.enabled
                  ? "text-green-600 hover:bg-green-50"
                  : "text-gray-400 hover:bg-gray-50"
              }`}
              title={card.enabled ? "활성화됨" : "비활성화됨"}
            >
              {card.enabled ? (
                <EyeIcon className="h-4 w-4" />
              ) : (
                <EyeSlashIcon className="h-4 w-4" />
              )}
            </button>

            <button
              onClick={(e) => {
                e.stopPropagation();
                onEdit(card);
              }}
              className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
              title="편집"
            >
              <PencilIcon className="h-4 w-4" />
            </button>

            <button
              onClick={(e) => {
                e.stopPropagation();
                onDelete(card.promptId);
              }}
              className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
              title="삭제"
            >
              <TrashIcon className="h-4 w-4" />
            </button>
          </div>

          <div className="flex items-center space-x-3 text-xs text-gray-500 min-w-0">
            <div className="flex items-center space-x-1 min-w-0 flex-1">
              <SparklesIcon className="h-3 w-3 flex-shrink-0" />
              <span className="truncate text-xs">
                {card.model?.includes('claude-3-5-sonnet') ? 'Claude 3.5 Sonnet' :
                 card.model?.includes('claude-3-sonnet') ? 'Claude 3 Sonnet' :
                 card.model?.includes('claude') ? 'Claude' : 'Model'}
              </span>
            </div>
            <div className="flex items-center space-x-1 flex-shrink-0">
              <Cog6ToothIcon className="h-3 w-3" />
              <span className="text-xs">{card.temperature || 0.7}</span>
            </div>
          </div>
        </div>

        {/* 프롬프트 미리보기 */}
        {(card.prompt_content || card.prompt_text || card.promptContent || card.promptText) && (
          <div className="bg-gray-50 rounded-lg p-3 border border-gray-200 mt-3">
            <div className="text-xs text-gray-700">
              <div className="line-clamp-3 break-words overflow-hidden">
                {(() => {
                  const content = card.prompt_content || card.prompt_text || card.promptContent || card.promptText || "";
                  return content.length > 120
                    ? `${content.substring(0, 120)}...`
                    : content;
                })()}
              </div>
              {(() => {
                const content = card.prompt_content || card.prompt_text || card.promptContent || card.promptText || "";
                return content.length > 120;
              })() && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onEdit(card);
                  }}
                  className="text-blue-600 hover:underline text-xs font-medium mt-1"
                >
                  전체 보기
                </button>
              )}
            </div>
          </div>
        )}

        {!(card.prompt_content || card.prompt_text || card.promptContent || card.promptText) && (
          <div className="text-center py-2">
            <span className="text-gray-400 italic">
              프롬프트 내용이 없습니다
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

const PromptCardManager = ({ projectId, onCardsChanged }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingCard, setEditingCard] = useState(null);
  const [draggedCard, setDraggedCard] = useState(null);
  const [dragOverIndex, setDragOverIndex] = useState(null);

  // 프롬프트 카드 목록 로딩
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      console.log("Loading prompt cards for project:", projectId);
      const response = await promptCardAPI.getPromptCards(
        projectId,
        true,
        true
      );
      console.log("Prompt cards response:", response);
      setPromptCards(response.promptCards || []);
    } catch (error) {
      console.error("Load prompt cards error:", error);
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 새 프롬프트 카드 생성
  const handleCreateCard = async (cardData) => {
    try {
      console.log("Creating prompt card with data:", cardData);
      const result = await promptCardAPI.createPromptCard(projectId, cardData);
      console.log("Create result:", result);
      toast.success("프롬프트 카드가 생성되었습니다!");
      setShowCreateModal(false);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      console.error("Create error:", error);
      const errorInfo = handleAPIError(error);
      toast.error(`카드 생성 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 수정
  const handleUpdateCard = async (promptId, cardData) => {
    try {
      console.log("Updating prompt card with ID:", promptId, "and data:", cardData);
      const result = await promptCardAPI.updatePromptCard(projectId, promptId, cardData);
      console.log("Update result:", result);
      toast.success("프롬프트 카드가 수정되었습니다!");
      setEditingCard(null);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      console.error("Update error:", error);
      const errorInfo = handleAPIError(error);
      toast.error(`카드 수정 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 삭제
  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트 카드를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트 카드가 삭제되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 활성/비활성 토글
  const handleToggleCard = async (promptId, enabled) => {
    try {
      await promptCardAPI.togglePromptCard(projectId, promptId, enabled);
      toast.success(
        enabled ? "카드가 활성화되었습니다!" : "카드가 비활성화되었습니다!"
      );
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`상태 변경 실패: ${errorInfo.message}`);
    }
  };

  // 드래그 앤 드롭 핸들러
  const handleDragStart = (e, card) => {
    setDraggedCard(card);
    if (e.dataTransfer) {
      e.dataTransfer.effectAllowed = "move";
      e.dataTransfer.setData("text/html", e.target.outerHTML);
    }
  };

  const handleDragOver = (e, index) => {
    e.preventDefault();
    if (e.dataTransfer) {
      e.dataTransfer.dropEffect = "move";
    }
    setDragOverIndex(index);
  };

  const handleDragLeave = () => {
    setDragOverIndex(null);
  };

  const handleDrop = async (e, targetIndex) => {
    e.preventDefault();
    setDragOverIndex(null);

    if (!draggedCard) return;

    const currentIndex = promptCards.findIndex(
      (card) => card.promptId === draggedCard.promptId
    );

    if (currentIndex === targetIndex) {
      setDraggedCard(null);
      return;
    }

    try {
      // 새로운 step_order 계산
      const newStepOrder = targetIndex + 1;

      await promptCardAPI.reorderPromptCard(
        projectId,
        draggedCard.promptId,
        newStepOrder
      );

      toast.success("카드 순서가 변경되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`순서 변경 실패: ${errorInfo.message}`);
    }

    setDraggedCard(null);
  };

  // 카테고리 정보 가져오기
  const getCategoryInfo = (categoryId) => {
    return (
      PROMPT_CARD_CATEGORIES.find((cat) => cat.id === categoryId) || {
        name: "알 수 없음",
        color: "gray",
        icon: "❓",
      }
    );
  };

  // 모델 정보 가져오기
  const getModelInfo = (modelId) => {
    return (
      AVAILABLE_MODELS.find((model) => model.id === modelId) || {
        name: "알 수 없는 모델",
      }
    );
  };

  // 카테고리별 색상 클래스 반환
  const getCategoryColorClasses = (color, enabled = true) => {
    const opacity = enabled ? "" : "opacity-60";
    return `${CATEGORY_COLORS[color] || CATEGORY_COLORS.gray} ${opacity}`;
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  const totalCards = promptCards.length;
  const activeCards = promptCards.filter(
    (card) => card.enabled !== false
  ).length;

  return (
    <div className="max-w-6xl mx-auto">
      <PromptCardManagerHeader
        onCreateCard={() => setShowCreateModal(true)}
        totalCards={totalCards}
        activeCards={activeCards}
      />

      {promptCards.length === 0 ? (
        <EmptyState onCreateCard={() => setShowCreateModal(true)} />
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {promptCards.map((card, index) => {
            const categoryInfo = getCategoryInfo(card.category);
            const modelInfo = getModelInfo(card.model);
            const isEnabled = card.enabled !== false;

            return (
              <PromptCard
                key={card.promptId}
                card={card}
                isSelected={false} // No selection for now
                onSelect={() => {}}
                onEdit={() => setEditingCard(card)}
                onDelete={() => handleDeleteCard(card.promptId)}
                onToggle={() => handleToggleCard(card.promptId, !isEnabled)}
                onDragStart={(e) => handleDragStart(e, card)}
                onDragEnd={() => {}}
                onDragOver={handleDragOver}
                onDrop={handleDrop}
                isDragging={draggedCard?.promptId === card.promptId}
                dragOverIndex={dragOverIndex}
                index={index}
              />
            );
          })}
        </div>
      )}

      {/* 프롬프트 카드 생성/수정 모달 */}
      {(showCreateModal || editingCard) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            {/* 모달 헤더 */}
            <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
              <h3 className="text-lg font-semibold text-gray-900">
                {editingCard ? "프롬프트 카드 수정" : "새 프롬프트 카드 생성"}
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                AI 제목 생성에 사용할 프롬프트를 설정하세요
              </p>
            </div>

            {/* 모달 내용 */}
            <div className="p-6">
              <PromptCardForm
                initialData={editingCard}
                onSubmit={editingCard ? (cardData) => handleUpdateCard(editingCard.promptId, cardData) : handleCreateCard}
                onCancel={() => {
                  setShowCreateModal(false);
                  setEditingCard(null);
                }}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// 프롬프트 카드 폼 컴포넌트
const PromptCardForm = ({ initialData, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: initialData?.title || "",
    description: initialData?.description || "",
    category: initialData?.category || "instruction",
    model: initialData?.model || "us.anthropic.claude-3-5-sonnet-20241022-v2:0",
    temperature: initialData?.temperature || 0.7,
    promptContent: initialData?.prompt_content || initialData?.prompt_text || initialData?.promptContent || initialData?.promptText || "",
    enabled: initialData?.enabled !== false,
    stepOrder: initialData?.step_order || initialData?.stepOrder || 1,
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.title.trim()) {
      toast.error("제목을 입력해주세요");
      return;
    }
    if (!formData.promptContent.trim()) {
      toast.error("프롬프트 내용을 입력해주세요");
      return;
    }

    setIsSubmitting(true);
    try {
      // API 스펙에 맞게 필드명 변환
      const apiData = {
        title: formData.title,
        description: formData.description,
        category: formData.category,
        model: formData.model,
        temperature: formData.temperature,
        prompt_text: formData.promptContent, // promptContent -> prompt_text
        enabled: formData.enabled,
        step_order: formData.stepOrder // stepOrder -> step_order
      };

      await onSubmit(apiData);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">

      {/* 기본 정보 */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            제목 *
          </label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => handleChange("title", e.target.value)}
            placeholder="프롬프트 카드의 제목을 입력하세요"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            카테고리
          </label>
          <select
            value={formData.category}
            onChange={(e) => handleChange("category", e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900"
          >
            {PROMPT_CARD_CATEGORIES.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          설명
        </label>
        <textarea
          value={formData.description}
          onChange={(e) => handleChange("description", e.target.value)}
          placeholder="이 프롬프트 카드의 역할과 목적을 설명하세요"
          rows={2}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900"
        />
      </div>

      {/* AI 모델 설정 */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            AI 모델
          </label>
          <select
            value={formData.model}
            onChange={(e) => handleChange("model", e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900"
          >
            {AVAILABLE_MODELS.map((model) => (
              <option key={model.id} value={model.id}>
                {model.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            창의성 (Temperature): {formData.temperature}
          </label>
          <div className="space-y-2">
            <div className="flex items-center space-x-4">
              <span className="text-xs text-gray-500 min-w-[50px]">보수적</span>
              <div className="flex-1 relative">
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={formData.temperature}
                  onChange={(e) =>
                    handleChange("temperature", parseFloat(e.target.value))
                  }
                  className="w-full h-2 bg-gradient-to-r from-blue-200 via-blue-400 to-orange-400 rounded-lg appearance-none cursor-pointer slider-thumb"
                />
                <div className="flex justify-between text-xs text-gray-400 mt-1">
                  <span>0.0</span>
                  <span>0.5</span>
                  <span>1.0</span>
                </div>
              </div>
              <span className="text-xs text-gray-500 min-w-[50px] text-right">
                창의적
              </span>
            </div>
          </div>
          <p className="mt-1 text-xs text-gray-500">
            낮을수록 일관된 결과, 높을수록 창의적인 결과를 생성합니다
          </p>
        </div>

      </div>

      {/* 프롬프트 내용 */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          프롬프트 내용 *
        </label>
        <textarea
          value={formData.promptContent}
          onChange={(e) => handleChange("promptContent", e.target.value)}
          placeholder="AI에게 전달할 프롬프트 내용을 입력하세요..."
          rows={12}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900 font-mono text-sm"
          required
        />
        <p className="mt-1 text-xs text-gray-500">
          현재 {formData.promptContent.length}자 | 권장: 500-2000자
        </p>
      </div>

      {/* 추가 설정 */}
      <div className="flex items-center space-x-6">
        <div className="flex items-center">
          <input
            type="checkbox"
            id="enabled"
            checked={formData.enabled}
            onChange={(e) => handleChange("enabled", e.target.checked)}
            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
          />
          <label htmlFor="enabled" className="ml-2 text-sm text-gray-700">
            활성화
          </label>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            실행 순서
          </label>
          <input
            type="number"
            value={formData.stepOrder}
            onChange={(e) =>
              handleChange("stepOrder", parseInt(e.target.value))
            }
            min="1"
            max="100"
            className="w-20 px-2 py-1 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 bg-white text-gray-900"
          />
        </div>
      </div>

      {/* 버튼 */}
      <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
        <button
          type="button"
          onClick={onCancel}
          className="px-4 py-2 text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
        >
          취소
        </button>
        <button
          type="submit"
          disabled={isSubmitting}
          className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
        >
          {isSubmitting ? "저장 중..." : initialData ? "수정" : "생성"}
        </button>
      </div>
    </form>
  );
};

export default PromptCardManager;
