import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import {
  PlusIcon,
  FolderOpenIcon,
  CalendarIcon,
  DocumentTextIcon,
} from "@heroicons/react/24/outline";
import { useProjects } from "../hooks/useProjects";
import LoadingSpinner from "./LoadingSpinner";

const ProjectList = () => {
  const { projects, loading, loadProjects } = useProjects();

  useEffect(() => {
    loadProjects();
  }, [loadProjects]);

  if (loading) {
    return <LoadingSpinner message="프로젝트를 불러오는 중..." />;
  }

  return (
    <div>
      {projects.length === 0 ? (
        <div className="text-center py-12">
          <FolderOpenIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            프로젝트가 없습니다
          </h3>
          <p className="text-gray-500 mb-6">
            첫 번째 AI 제목 생성 프로젝트를 만들어보세요
          </p>
          <Link
            to="/create"
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <PlusIcon className="h-5 w-5 mr-2" />
            프로젝트 생성
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {projects.map((project) => (
            <ProjectCard key={project.id} project={project} />
          ))}
        </div>
      )}
    </div>
  );
};

// 프로젝트 카드 컴포넌트
const ProjectCard = ({ project }) => {
  return (
    <Link
      to={`/projects/${project.id}`}
      className="block bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow p-6"
    >
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
            <DocumentTextIcon className="h-6 w-6 text-blue-600" />
          </div>
          <div className="ml-3">
            <h3 className="text-lg font-medium text-gray-900 truncate">
              {project.name}
            </h3>
            <p className="text-sm text-gray-500">
              {project.category || "뉴스"}
            </p>
          </div>
        </div>
      </div>

      {project.description && (
        <p className="text-gray-600 text-sm mb-4 line-clamp-2">
          {project.description}
        </p>
      )}

      <div className="flex items-center text-xs text-gray-500">
        <CalendarIcon className="h-4 w-4 mr-1" />
        {new Date(project.createdAt).toLocaleDateString("ko-KR")}
      </div>
    </Link>
  );
};

export default ProjectList;
