import React, { useState, useEffect, useCallback } from "react";
import { toast } from "react-hot-toast";
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  EyeIcon,
  EyeSlashIcon,
  Cog6ToothIcon,
  DocumentTextIcon,
  SparklesIcon,
  CloudArrowUpIcon,
  XMarkIcon,
  Bars3Icon,
} from "@heroicons/react/24/outline";
import {
  promptCardAPI,
  PROMPT_CARD_CATEGORIES,
  AVAILABLE_MODELS,
  handleAPIError,
} from "../services/api";

// 프롬프트 카드 카테고리별 색상 정의
const CATEGORY_COLORS = {
  blue: `bg-blue-100 text-blue-800 border-blue-200`,
  purple: `bg-purple-100 text-purple-800 border-purple-200`,
  green: `bg-green-100 text-green-800 border-green-200`,
  orange: `bg-orange-100 text-orange-800 border-orange-200`,
  red: `bg-red-100 text-red-800 border-red-200`,
  yellow: `bg-yellow-100 text-yellow-800 border-yellow-200`,
  gray: `bg-gray-100 text-gray-800 border-gray-200`,
};

// 입력 방식 선택 모달
const InputMethodModal = ({ isOpen, onClose, onSelectMethod }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full mx-4">
        <div className="text-center mb-8">
          <h3 className="text-2xl font-bold text-gray-900 mb-2">
            프롬프트 추가 방법
          </h3>
          <p className="text-gray-600">원하는 방법을 선택하세요</p>
        </div>

        <div className="space-y-4">
          {/* 파일 업로드 옵션 */}
          <button
            onClick={() => onSelectMethod("upload")}
            className="w-full p-6 border-2 border-gray-200 rounded-xl hover:border-blue-400 hover:bg-blue-50 transition-all duration-200 text-left group"
          >
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mr-4">
                <CloudArrowUpIcon className="w-6 h-6 text-blue-600" />
              </div>
              <div>
                <h4 className="font-semibold text-gray-900 group-hover:text-blue-600">
                  기기에서 업로드
                </h4>
                <p className="text-sm text-gray-600">
                  파일을 선택하여 프롬프트를 생성합니다
                </p>
              </div>
            </div>
          </button>

          {/* 직접 입력 옵션 */}
          <button
            onClick={() => onSelectMethod("direct")}
            className="w-full p-6 border-2 border-gray-200 rounded-xl hover:border-green-400 hover:bg-green-50 transition-all duration-200 text-left group"
          >
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mr-4">
                <PencilIcon className="w-6 h-6 text-green-600" />
              </div>
              <div>
                <h4 className="font-semibold text-gray-900 group-hover:text-green-600">
                  직접 입력
                </h4>
                <p className="text-sm text-gray-600">
                  텍스트를 직접 입력하여 프롬프트를 생성합니다
                </p>
              </div>
            </div>
          </button>
        </div>

        <div className="mt-8 flex justify-end">
          <button
            onClick={onClose}
            className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
          >
            취소
          </button>
        </div>
      </div>
    </div>
  );
};

// 초기 상태 컴포넌트
const InitialState = ({ onAddPrompt }) => (
  <div className="flex flex-col items-center justify-center h-full">
    <div className="text-center">
      <button
        onClick={onAddPrompt}
        className="group relative w-32 h-32 bg-white border-2 border-dashed border-gray-300 rounded-xl hover:border-blue-400 hover:bg-blue-50 transition-all duration-200 flex items-center justify-center mb-6"
      >
        <PlusIcon className="w-12 h-12 text-gray-400 group-hover:text-blue-500 transition-colors" />
      </button>
      <h2 className="text-xl font-semibold text-gray-900 mb-2">
        새 프롬프트 카드 추가
      </h2>
      <p className="text-gray-600">
        파일을 업로드하거나 직접 입력하여 프롬프트를 생성하세요
      </p>
    </div>
  </div>
);

// 프롬프트 설정 폼
const PromptForm = ({
  inputMethod,
  onSubmit,
  onCancel,
  initialData = null,
}) => {
  const [formData, setFormData] = useState({
    title: initialData?.title || "",
    description: initialData?.description || "",
    category: initialData?.category || "역할",
    model: initialData?.model || "anthropic.claude-3-5-sonnet-20241022-v2:0",
    temperature: initialData?.temperature || 0.7,
    promptContent:
      initialData?.prompt_content ||
      initialData?.prompt_text ||
      initialData?.promptContent ||
      initialData?.promptText ||
      "",
    enabled: initialData?.enabled !== false,
    stepOrder: initialData?.step_order || initialData?.stepOrder || 1,
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.title.trim()) {
      toast.error("제목을 입력해주세요");
      return;
    }

    if (inputMethod === "upload" && !selectedFile && !initialData) {
      toast.error("파일을 선택해주세요");
      return;
    }

    if (inputMethod === "direct" && !formData.promptContent.trim()) {
      toast.error("프롬프트 내용을 입력해주세요");
      return;
    }

    setIsSubmitting(true);
    try {
      let finalPromptContent = formData.promptContent;

      // 파일 업로드 처리
      if (inputMethod === "upload" && selectedFile) {
        const fileContent = await readFileContent(selectedFile);
        finalPromptContent = fileContent;
      }

      const apiData = {
        title: formData.title,
        description: formData.description,
        category: formData.category,
        model: formData.model,
        temperature: formData.temperature,
        prompt_text: finalPromptContent,
        enabled: formData.enabled,
        step_order: formData.stepOrder,
      };

      await onSubmit(apiData);
    } finally {
      setIsSubmitting(false);
    }
  };

  const readFileContent = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target.result);
      reader.onerror = reject;
      reader.readAsText(file);
    });
  };

  const handleChange = (field, value) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const categoryOptions = [
    { id: "역할", name: "역할" },
    { id: "가이드라인", name: "가이드라인" },
    { id: "워크플로우", name: "워크플로우" },
    { id: "출력결과", name: "출력결과" },
    { id: "예시", name: "예시" },
    { id: "검수", name: "검수" },
  ];

  const modelOptions = [
    {
      id: "anthropic.claude-3-5-sonnet-20241022-v2:0",
      name: "Claude 3.5 Sonnet",
    },
    { id: "anthropic.claude-3-sonnet-20240229-v1:0", name: "Claude 3 Sonnet" },
  ];

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-2xl shadow-sm border border-gray-200 p-8">
        <div className="flex items-center justify-between mb-8">
          <h2 className="text-2xl font-bold text-gray-900">
            {initialData ? "프롬프트 수정" : "프롬프트 설정"}
          </h2>
          <button
            onClick={onCancel}
            className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100 transition-all"
          >
            <XMarkIcon className="w-5 h-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* 제목 */}
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">
              제목
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => handleChange("title", e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
              placeholder="프롬프트 카드 제목을 입력하세요"
              required
            />
          </div>

          {/* 카테고리 */}
          <div>
            <label className="block text-sm font-medium text-gray-900 mb-2">
              카테고리
            </label>
            <select
              value={formData.category}
              onChange={(e) => handleChange("category", e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
            >
              {categoryOptions.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          {/* 모델 및 온도 설정 */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-900 mb-2">
                모델
              </label>
              <select
                value={formData.model}
                onChange={(e) => handleChange("model", e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all"
              >
                {modelOptions.map((model) => (
                  <option key={model.id} value={model.id}>
                    {model.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-900 mb-2">
                온도
              </label>
              <div className="relative">
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={formData.temperature}
                  onChange={(e) =>
                    handleChange("temperature", parseFloat(e.target.value))
                  }
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                />
                <span className="absolute -top-8 left-1/2 transform -translate-x-1/2 text-sm text-gray-600">
                  {formData.temperature}
                </span>
              </div>
            </div>
          </div>

          {/* 파일 업로드 (업로드 모드일 때만 표시) */}
          {inputMethod === "upload" && !initialData && (
            <div>
              <label className="block text-sm font-medium text-gray-900 mb-2">
                파일 선택
              </label>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-blue-400 transition-colors">
                <input
                  type="file"
                  onChange={(e) => setSelectedFile(e.target.files[0])}
                  className="hidden"
                  accept=".txt,.md,.pdf,.doc,.docx"
                  id="file-upload"
                />
                <CloudArrowUpIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600 mb-2">
                  {selectedFile
                    ? selectedFile.name
                    : "파일을 드래그하거나 클릭하여 선택"}
                </p>
                <label
                  htmlFor="file-upload"
                  className="text-blue-600 hover:text-blue-800 font-medium cursor-pointer"
                >
                  파일 선택
                </label>
              </div>
            </div>
          )}

          {/* 직접 입력 (직접 입력 모드일 때만 표시) */}
          {(inputMethod === "direct" || initialData) && (
            <div>
              <label className="block text-sm font-medium text-gray-900 mb-2">
                프롬프트 내용
              </label>
              <textarea
                rows="8"
                value={formData.promptContent}
                onChange={(e) => handleChange("promptContent", e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all resize-none"
                placeholder="프롬프트 내용을 입력하세요..."
                required={inputMethod === "direct"}
              />
            </div>
          )}

          {/* 저장 버튼 */}
          <div className="flex justify-end space-x-4 pt-4">
            <button
              type="button"
              onClick={onCancel}
              className="px-6 py-3 text-gray-600 hover:text-gray-800 transition-colors"
            >
              취소
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-8 py-3 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-lg transition-colors disabled:opacity-50"
            >
              {isSubmitting ? "저장 중..." : "저장"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// 사이드바 프롬프트 카드
const SidebarPromptCard = ({ card, onEdit, onDelete, onToggle }) => {
  const getCategoryColor = (category) => {
    const colorMap = {
      역할: "bg-blue-100 text-blue-800",
      가이드라인: "bg-purple-100 text-purple-800",
      워크플로우: "bg-green-100 text-green-800",
      출력결과: "bg-orange-100 text-orange-800",
      예시: "bg-yellow-100 text-yellow-800",
      검수: "bg-red-100 text-red-800",
    };
    return colorMap[category] || "bg-gray-100 text-gray-800";
  };

  const getModelDisplayName = (modelId) => {
    if (modelId?.includes("claude-3-5-sonnet")) return "Claude 3.5 Sonnet";
    if (modelId?.includes("claude-3-sonnet")) return "Claude 3 Sonnet";
    if (modelId?.includes("claude")) return "Claude";
    return "Unknown Model";
  };

  return (
    <div className="group p-4 rounded-xl border border-gray-200 bg-white hover:bg-gray-50 transition-all duration-200">
      <div className="flex items-start justify-between">
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-2">
            <span
              className={`inline-flex items-center px-2 py-1 rounded-md text-xs font-medium ${getCategoryColor(
                card.category
              )}`}
            >
              {card.category}
            </span>
            <h6 className="text-sm font-medium text-gray-900 truncate">
              {card.title}
            </h6>
          </div>
          <div className="text-xs text-gray-500 space-y-1">
            <div>Temperature: {card.temperature || 0.7}</div>
            <div>Model: {getModelDisplayName(card.model)}</div>
          </div>
        </div>
        <div className="flex items-center space-x-2 ml-4">
          <button
            onClick={() => onEdit(card)}
            className="opacity-0 group-hover:opacity-100 p-1 text-gray-400 hover:text-blue-600 rounded transition-all"
          >
            <PencilIcon className="h-4 w-4" />
          </button>
          <button
            onClick={() => onToggle(card.promptId || card.id, !card.enabled)}
            className={`w-8 h-4 rounded-full relative transition-colors ${
              card.enabled ? "bg-blue-600" : "bg-gray-300"
            }`}
          >
            <div
              className={`w-3 h-3 bg-white rounded-full shadow transform transition-transform absolute top-0.5 ${
                card.enabled ? "translate-x-4" : "translate-x-0.5"
              }`}
            ></div>
          </button>
        </div>
      </div>
    </div>
  );
};

// 메인 PromptCardManager 컴포넌트
const PromptCardManager = ({ projectId, onCardsChanged }) => {
  const [promptCards, setPromptCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showInputModal, setShowInputModal] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [inputMethod, setInputMethod] = useState(null);
  const [editingCard, setEditingCard] = useState(null);

  // 프롬프트 카드 목록 로딩
  const loadPromptCards = useCallback(async () => {
    try {
      setLoading(true);
      const response = await promptCardAPI.getPromptCards(
        projectId,
        true,
        true
      );
      setPromptCards(response.promptCards || []);
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`프롬프트 카드 로딩 실패: ${errorInfo.message}`);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    loadPromptCards();
  }, [loadPromptCards]);

  // 프롬프트 추가 버튼 클릭
  const handleAddPrompt = () => {
    setShowInputModal(true);
  };

  // 입력 방식 선택
  const handleSelectMethod = (method) => {
    setInputMethod(method);
    setShowInputModal(false);
    setShowForm(true);
  };

  // 프롬프트 카드 생성
  const handleCreateCard = async (cardData) => {
    try {
      await promptCardAPI.createPromptCard(projectId, cardData);
      toast.success("프롬프트 카드가 생성되었습니다!");
      setShowForm(false);
      setInputMethod(null);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 생성 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 수정
  const handleUpdateCard = async (cardData) => {
    try {
      await promptCardAPI.updatePromptCard(
        projectId,
        editingCard.promptId || editingCard.id,
        cardData
      );
      toast.success("프롬프트 카드가 수정되었습니다!");
      setEditingCard(null);
      setShowForm(false);
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 수정 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 삭제
  const handleDeleteCard = async (promptId) => {
    if (!window.confirm("정말로 이 프롬프트 카드를 삭제하시겠습니까?")) {
      return;
    }

    try {
      await promptCardAPI.deletePromptCard(projectId, promptId);
      toast.success("프롬프트 카드가 삭제되었습니다!");
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`카드 삭제 실패: ${errorInfo.message}`);
    }
  };

  // 프롬프트 카드 활성/비활성 토글
  const handleToggleCard = async (promptId, enabled) => {
    try {
      await promptCardAPI.togglePromptCard(projectId, promptId, enabled);
      toast.success(
        enabled ? "카드가 활성화되었습니다!" : "카드가 비활성화되었습니다!"
      );
      loadPromptCards();
      if (onCardsChanged) onCardsChanged();
    } catch (error) {
      const errorInfo = handleAPIError(error);
      toast.error(`상태 변경 실패: ${errorInfo.message}`);
    }
  };

  // 편집 모드 시작
  const handleEditCard = (card) => {
    setEditingCard(card);
    setInputMethod("direct");
    setShowForm(true);
  };

  // 폼 취소
  const handleCancelForm = () => {
    setShowForm(false);
    setInputMethod(null);
    setEditingCard(null);
  };

  // 카테고리별 그룹화
  const groupedCards = promptCards.reduce((groups, card) => {
    const category = card.category || "기타";
    if (!groups[category]) {
      groups[category] = [];
    }
    groups[category].push(card);
    return groups;
  }, {});

  const activeCards = promptCards.filter(
    (card) => card.enabled !== false
  ).length;

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-50">
      {/* 메인 영역 */}
      <main className="flex-1 p-8">
        <div id="main-content">
          {showForm ? (
            <PromptForm
              inputMethod={inputMethod}
              onSubmit={editingCard ? handleUpdateCard : handleCreateCard}
              onCancel={handleCancelForm}
              initialData={editingCard}
            />
          ) : (
            <InitialState onAddPrompt={handleAddPrompt} />
          )}
        </div>
      </main>

      {/* 우측 사이드바 */}
      <aside className="w-96 bg-white border-l border-gray-200 shadow-sm flex flex-col">
        <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white">
          <div className="flex items-center space-x-2">
            <Cog6ToothIcon className="h-5 w-5 text-gray-600" />
            <h3 className="text-sm font-semibold text-gray-900">
              프로젝트 설정
            </h3>
          </div>
          <div className="text-xs text-gray-500">
            {activeCards}/{promptCards.length} 활성
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4">
          <div className="space-y-6">
            {Object.keys(groupedCards).length === 0 ? (
              <div className="text-center py-8">
                <DocumentTextIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">프롬프트 카드가 없습니다</p>
              </div>
            ) : (
              Object.entries(groupedCards).map(([category, cards]) => (
                <div key={category}>
                  <h5 className="text-xs font-medium text-gray-700 uppercase tracking-wider mb-2">
                    {category}
                  </h5>
                  <div className="space-y-2">
                    {cards.map((card) => (
                      <SidebarPromptCard
                        key={card.promptId || card.id}
                        card={card}
                        onEdit={handleEditCard}
                        onDelete={handleDeleteCard}
                        onToggle={handleToggleCard}
                      />
                    ))}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        <div className="p-4 border-t border-gray-200 bg-white">
          <button
            onClick={handleAddPrompt}
            className="w-full flex items-center justify-center px-3 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg text-sm text-white transition-colors"
          >
            <PlusIcon className="h-4 w-4 mr-2" />새 프롬프트 카드 추가
          </button>
        </div>
      </aside>

      {/* 입력 방식 선택 모달 */}
      <InputMethodModal
        isOpen={showInputModal}
        onClose={() => setShowInputModal(false)}
        onSelectMethod={handleSelectMethod}
      />
    </div>
  );
};

export default PromptCardManager;
