import React from "react";
import { Link } from "react-router-dom";
import { DocumentTextIcon } from "@heroicons/react/24/outline";
import DarkModeToggle from "./DarkModeToggle";

const Header = () => {
  return (
    <header className="sticky top-0 z-50 border-b border-white/10 dark:border-white/5 transition-all duration-300">
      {/* 글라스모피즘 배경 */}
      <div className="absolute inset-0 bg-white/80 dark:bg-dark-surface backdrop-blur-xl" />

      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* 로고 */}
          <Link to="/" className="flex items-center space-x-3 group">
            {/* 로고 아이콘 컨테이너 */}
            <div className="relative">
              <div className="w-10 h-10 bg-gradient-to-br from-cyan-400 to-purple-500 rounded-2xl flex items-center justify-center shadow-lg group-hover:shadow-cyan-400/25 transition-all duration-300 group-hover:scale-105">
                <DocumentTextIcon className="h-5 w-5 text-white" />
              </div>
              {/* 네온 글로우 효과 */}
              <div className="absolute inset-0 bg-gradient-to-br from-cyan-400 to-purple-500 rounded-2xl opacity-0 group-hover:opacity-30 blur-lg transition-opacity duration-300" />
            </div>

            {/* 로고 텍스트 */}
            <div className="group-hover:translate-x-1 transition-transform duration-300">
              <h1 className="text-xl font-bold bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-cyan-300 bg-clip-text text-transparent">
                TITLE-NOMICS
              </h1>
              <p className="text-xs text-gray-500 dark:text-gray-400 font-medium">
                AI 제목 생성기
              </p>
            </div>
          </Link>

          {/* 다크모드 토글 */}
          <div className="flex items-center">
            <DarkModeToggle size="md" />
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
