import React, { createContext, useContext, useState, useEffect } from "react";
import { authAPI } from "../services/api";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false); // 🚨 개발 모드: 로딩 비활성화
  const [isAuthenticated, setIsAuthenticated] = useState(true); // 🚨 개발 모드: 기본 인증 true

  // 컴포넌트 마운트 시 인증 상태 확인
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = () => {
    // 🚨 개발 모드: 인증 체크 비활성화
    console.log("🧪 개발 모드: 인증 체크 건너뛰기 - 항상 인증된 상태로 처리");

    try {
      // 개발 모드에서는 항상 인증된 상태로 설정
      setIsAuthenticated(true);
      setUser({
        email: "dev@example.com",
        name: "개발자",
        sub: "dev-user"
      });
    } catch (error) {
      console.error("인증 상태 확인 오류:", error);
      // 개발 모드에서는 오류가 있어도 인증된 상태 유지
      setIsAuthenticated(true);
      setUser({
        email: "dev@example.com",
        name: "개발자",
        sub: "dev-user"
      });
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials) => {
    try {
      const response = await authAPI.signin(credentials);
      const currentUser = authAPI.getCurrentUser();

      setUser(currentUser);
      setIsAuthenticated(true);

      return response;
    } catch (error) {
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const logout = async () => {
    try {
      await authAPI.signout();
    } catch (error) {
      console.error("로그아웃 오류:", error);
    } finally {
      setUser(null);
      setIsAuthenticated(false);
    }
  };

  const signup = async (userData) => {
    return await authAPI.signup(userData);
  };

  const verifyEmail = async (verificationData) => {
    return await authAPI.verifyEmail(verificationData);
  };

  const refreshToken = async () => {
    try {
      await authAPI.refreshToken();
      const currentUser = authAPI.getCurrentUser();
      setUser(currentUser);
      setIsAuthenticated(true);
    } catch (error) {
      setUser(null);
      setIsAuthenticated(false);
      throw error;
    }
  };

  const value = {
    user,
    loading,
    isAuthenticated,
    login,
    logout,
    signup,
    verifyEmail,
    refreshToken,
    checkAuthStatus,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
