import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  PaperAirplaneIcon,
  DocumentPlusIcon,
  XMarkIcon,
  ChatBubbleLeftRightIcon,
  SparklesIcon,
  StopIcon,
} from "@heroicons/react/24/outline";
import { useChat } from "../hooks/useChat";
import AnimatedChatMessage from "./AnimatedChatMessage";
import ModelSelector from "./ModelSelector";

const ChatInterface = ({ projectId, projectName, promptCards = [] }) => {
  const navigate = useNavigate();
  const [uploadedFile, setUploadedFile] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const {
    messages,
    inputValue,
    setInputValue,
    handleInputChange,
    copiedMessage,
    isGenerating,
    canSendMessage,
    messagesEndRef,
    inputRef,
    inputHeight,
    handleSendMessage,
    handleStopGeneration,
    handleKeyPress,
    handleCopyMessage,
    handleCopyTitle,
    wsConnected,
    wsConnecting,
    wsError,
    // 스크롤 관련 추가
    scrollContainerRef,
    handleScroll,
    isUserScrolling,
    // 모델 선택 관련 추가
    selectedModel,
    setSelectedModel,
  } = useChat(projectId, projectName, promptCards);

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target?.result || "";
        handleInputChange(content); // 높이 자동 조절
        setUploadedFile(file);
      };
      reader.readAsText(file);
    }
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      if (file.type === "text/plain") {
        const reader = new FileReader();
        reader.onload = (e) => {
          const content = e.target?.result || "";
          handleInputChange(content); // 높이 자동 조절
          setUploadedFile(file);
        };
        reader.readAsText(file);
      }
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const removeFile = () => {
    setUploadedFile(null);
    handleInputChange(""); // 높이 초기화
  };

  return (
    <div className="flex flex-col h-full bg-gray-50 dark:bg-dark-primary transition-all duration-500">
      {/* 메시지 영역 */}
      <div
        ref={scrollContainerRef}
        onScroll={handleScroll}
        className="flex-1 overflow-y-auto px-6 py-6 min-h-0"
      >
        <div className="max-w-4xl mx-auto space-y-6">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-32 mt-12">
              <div className="card-neo p-10 max-w-2xl w-full text-center">
                <div className="flex justify-center mb-8">
                  <div className="w-16 h-16 bg-blue-600 dark:bg-blue-500 rounded-3xl flex items-center justify-center card-neo">
                    <ChatBubbleLeftRightIcon className="h-8 w-8 text-white" />
                  </div>
                </div>
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
                  AI 헤드라인 어시스턴트
                </h2>
                <p className="text-gray-600 dark:text-gray-300 text-lg mb-8 leading-relaxed">
                  본문을 입력하면 AI가 최적의 헤드라인을 제안해 드립니다.
                </p>

                <div className="space-y-4">
                  <div className="card-neo p-4 rounded-2xl text-left">
                    <div className="flex items-start">
                      <div className="mr-4 mt-1">
                        <SparklesIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                      </div>
                      <div>
                        <p className="text-sm text-gray-700 dark:text-gray-300">
                          <span className="font-semibold text-blue-500 dark:text-blue-400">
                            시작하는 방법:
                          </span>{" "}
                          아래 입력창에 본문을 붙여넣고 전송 버튼을 클릭하세요
                        </p>
                      </div>
                    </div>
                  </div>

                  <div className="card-neo p-4 rounded-2xl text-left">
                    <div className="flex items-start">
                      <div className="mr-4 mt-1">
                        <SparklesIcon className="h-5 w-5 text-emerald-500 dark:text-emerald-400" />
                      </div>
                      <div>
                        <p className="text-sm text-gray-700 dark:text-gray-300">
                          <span className="font-semibold text-emerald-500 dark:text-emerald-400">
                            프롬프트 카드:
                          </span>{" "}
                          관리자 모드에서 프롬프트 카드를 작성하여 제목을
                          생성하세요
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            messages.map((message) => (
              <AnimatedChatMessage
                key={message.id}
                message={message}
                onCopyMessage={handleCopyMessage}
                onCopyTitle={handleCopyTitle}
                copiedMessage={copiedMessage}
              />
            ))
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* 입력 영역 */}
      <div className="flex-shrink-0 px-6 py-6">
        <div className="max-w-4xl mx-auto">
          {/* 업로드된 파일 표시 */}
          {uploadedFile && (
            <div className="mb-4 card-neo p-4 rounded-2xl">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <DocumentPlusIcon className="h-5 w-5 text-blue-500 dark:text-blue-400" />
                  <span className="text-sm text-gray-700 dark:text-gray-300 font-medium">
                    {uploadedFile.name}
                  </span>
                </div>
                <button
                  onClick={removeFile}
                  className="text-gray-500 hover:text-red-500 p-1 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20 transition-all duration-200"
                >
                  <XMarkIcon className="h-4 w-4" />
                </button>
              </div>
            </div>
          )}

          <div
            className="card-neo rounded-3xl p-6 relative transition-all duration-300"
            style={{
              minHeight: Math.max(140, inputHeight + 100),
              transition: "min-height 0.2s ease-in-out",
            }}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
          >
            <div className="flex-1 relative">
              <textarea
                ref={inputRef}
                value={inputValue}
                onChange={(e) => handleInputChange(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={
                  isGenerating
                    ? "생성 중입니다..."
                    : "기사 본문을 입력하세요..."
                }
                className={`input-neo w-full pr-16 pl-2 resize-none transition-all duration-200 ${
                  dragOver ? "bg-blue-50/50 dark:bg-blue-900/20" : ""
                }`}
                rows={1}
                style={{
                  height: `${inputHeight}px`,
                  minHeight: "28px",
                  maxHeight: "400px",
                  lineHeight: "1.5",
                  paddingTop: "4px",
                  paddingBottom: "8px",
                  overflowY: inputHeight > 200 ? "auto" : "hidden",
                  whiteSpace: "pre-wrap",
                  fontSize: "16px",
                  fontWeight: "400",
                }}
                disabled={isGenerating}
              />

              {/* 전송/중단 버튼 - 입력창 내부에 위치 */}
              <div className="absolute right-4 bottom-2 flex items-center gap-2">
                {isGenerating && (
                  <button
                    onClick={handleStopGeneration}
                    className="btn-neo !p-3 bg-red-500 hover:bg-red-600 text-white rounded-2xl transition-all duration-300"
                    title="생성 중단"
                  >
                    <StopIcon className="h-4 w-4" />
                  </button>
                )}
                <button
                  onClick={handleSendMessage}
                  disabled={!inputValue.trim() || isGenerating}
                  className="btn-neo !p-3 rounded-2xl disabled:opacity-50 disabled:cursor-not-allowed group"
                  title={
                    isGenerating
                      ? "생성 중..."
                      : wsConnected
                      ? "실시간 스트리밍 사용 가능"
                      : "일반 모드 (WebSocket 연결 안됨)"
                  }
                >
                  {isGenerating ? (
                    <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
                  ) : (
                    <PaperAirplaneIcon className="h-4 w-4 group-hover:translate-x-0.5 group-hover:-translate-y-0.5 transition-transform duration-200" />
                  )}
                </button>
              </div>
            </div>

            {/* 하단 상태 바 */}
            <div className="flex items-center justify-between mt-6 pt-4 border-t border-gray-200 dark:border-gray-700">
              <div className="flex items-center gap-6">
                {/* 글자 수 표시 */}
                <div className="flex items-center gap-2 text-xs text-gray-500 dark:text-gray-400">
                  <span className="font-medium">{inputValue.length}자</span>
                </div>

                {/* 모델 선택기 */}
                <div className="flex items-center gap-2">
                  <ModelSelector
                    selectedModel={selectedModel}
                    onModelChange={setSelectedModel}
                  />
                </div>
              </div>

              {/* WebSocket 연결 상태 및 스크롤 상태 표시 */}
              <div className="flex items-center gap-4 text-xs">
                {/* 스크롤 상태 표시 */}
                {isUserScrolling && (
                  <div className="flex items-center gap-1 card-neo px-3 py-1 rounded-full">
                    <span>📜</span>
                    <span className="text-blue-500 dark:text-blue-400 font-medium">
                      자유 스크롤
                    </span>
                  </div>
                )}

                {/* WebSocket 연결 상태 */}
                <div className="card-neo px-3 py-1 rounded-full">
                  {wsConnecting ? (
                    <div className="flex items-center gap-2 text-amber-500 dark:text-amber-400">
                      <div className="w-2 h-2 bg-amber-500 dark:bg-amber-400 rounded-full animate-pulse" />
                      <span className="font-medium">연결 중...</span>
                    </div>
                  ) : wsConnected ? (
                    <div className="flex items-center gap-2 text-emerald-500 dark:text-emerald-400">
                      <div className="w-2 h-2 bg-emerald-500 dark:bg-emerald-400 rounded-full animate-pulse" />
                      <span className="font-medium">실시간 스트리밍</span>
                    </div>
                  ) : (
                    <div className="flex items-center gap-2 text-gray-500 dark:text-gray-400">
                      <div className="w-2 h-2 bg-gray-400 rounded-full" />
                      <span className="font-medium">일반 모드</span>
                    </div>
                  )}
                  {wsError && (
                    <span
                      className="text-red-500 dark:text-red-400 ml-2"
                      title={wsError}
                    >
                      ⚠️
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
